css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("scenereleases.info")

{


img[width="134"][height="34"][src$="/link.png"]{display:none!important}

body {background: #111 !important;
color: #999 !important; 
}
a {
color: #94d4e2 !important;
border: none !important; 
}
a:visited {
color: #94d4e2 !important;
}
a:hover {
color: #c0c0c0 !important;
}
strong {color: #c0c0c0 !important}

#head h1 a {display: none !important; top: 0px !important}

#top #head h1 {height: 0px !important; }

.firefox {display: none !important}

.icon_rss {left: 975px !important; height: 22px !important;}

.imgborder {display: none !important}

#head {background-image: none !important; position:relative !important; height: 50px !important; background:none no-repeat !important}

#wrapper {height: 44px !important;}

#top #nav {position: relative !important; top: 5px !important}

.entrys, .widget.widget_search, .widget_block.tablist, .widget.widget_text {-moz-border-radius: 20px !important}

.footer {-moz-border-radius: 20px 20px 0px 0px !important}

#who-is-online {-moz-border-radius: 20px !important}


}
