css:
@-moz-document url("https://www.youtube.com/"), url-prefix("https://www.youtube.com/") {
#container {
    background: rgb(0, 0, 0) !important;
}

#guide-content.ytd-app{
	background: rgb(0, 0, 0);
}
#columns{
	background: rgba(0, 0, 0) !important;
}
ytd-watch-flexy{
	background: rgba(0, 0, 0) !important;
}
html:not(.style-scope)[dark], :not(.style-scope)[dark]{
	--ytd-searchbox-legacy-border-color: white;
}

#masthead-container{
	background: rgba(0, 0, 0) !important;
}

#masthead:after{
	background: rgba(0, 0, 0) !important;
}
ytd-masthead{
	background: rgba(0, 0, 0) !important;
}

yt-live-chat-message-input-renderer{
	background: transparent;
    
}
yt-live-chat-message-input-renderer div#container{
	background: transparent !important;
    
}


form#search-form div#container{
	margin-right: -1px;
    border: 0px solid white;
    border-radius: 15px 0px 0px 15px;
}

#search-icon-legacy.ytd-searchbox{
	border: 0px solid white;
    border-radius: 0px 15px 15px 0px !important;
    width: 60px;
    height: 35px;
    padding-right: 9px;
    background-color: transparent;
}


button[class="ytp-miniplayer-button ytp-button"]{
	display: none !important;
}

#search-icon-legacy.ytd-searchbox svg{
    color: white;
}
#search-icon-legacy paper-tooltip{
    display: none !important;
    background-color: white;
}
html{
    --ytd-searchbox-legacy-button-hover-border-color: white !important;
    --ytd-searchbox-legacy-button-hover-color: transparent !important;
    --ytd-searchbox-border-color: white !important;
}
ytd-active-account-header-renderer{
	background: rgb(31,32,36);
}

div#buttons ytd-topbar-menu-button-renderer{
	margin-right: 0px !important;
    margin-left: 0px !important;
}

div#movie_player{
	border-radius: 10px !important;
}
video[class= "video-stream html5-main-video"]{
    border-radius: 10px !important;
}

ytd-multi-page-menu-renderer{
	background: rgb(10,12,15);
}

ytd-simple-menu-header-renderer{
	background: rgb(31,32,36);
}

div[class="container style-scope ytd-account-settings"]{
	background:  rgb(10,12,15) !important;
    
}
div[class="container style-scope ytd-account-settings"] paper-item p{
    color: white;
    background: none;
}
div#container[class="menu-container style-scope ytd-multi-page-menu-renderer"]{
	margin-right: 0px;
    overflow-x: hidden !important;
}

ytd-conversation-switcher-item-renderer{
	width: 98%;
    overflow-x: hidden !important;
}

div#buttons ytd-notification-topbar-button-renderer{
	margin-right: 0px !important;
    margin-left: 0px !important;
}

#search-icon-legacy.ytd-searchbox:hover{
	background: transparent !important;
    border: 1px solid white !important;
}
#search-icon-legacy.ytd-searchbox:focus{
	background: transparent !important;
}

paper-tooltip.ytd-searchbox div#tooltip{
	display: none !important;
    height: 0px !important;
    width: 0px !important;
}

a.gsst_a{
    display: none;
}
div#sb_ifc50{
	padding-left: 10px;
}


ytd-search[class="style-scope ytd-page-manager"]{
	padding: 0 !important;
    margin-left: 20px;
}

ytd-page-manager#page-manager{
	background: rgba(0, 0, 0);
}




/* 
Live chat stuff:
--yt-live-chat-background-color: #191919; 
--yt-live-chat-action-panel-background-color: #282828;

*/

ytd-post-renderer div#body{
	background: rgb(38,38,43) !important;
}
ytd-post-renderer div#body a{
	background: rgb(38,38,43) !important;
}

ytd-post-renderer div#post-text yt-formatted-string{
	background: rgb(38,38,43) !important;
}

ytd-post-renderer div#header a{
	background: rgb(38,38,43) !important;
}

ytd-post-renderer div#header{
	background: rgb(38,38,43) !important;
    margin-bottom: 0px !important;
}

span#author-divider{background: rgb(38,38,43) !important;}

ytd-post-renderer div#author a#author-text span{
	background: rgba(38,38,43) !important;
}

ytd-post-renderer[surface_="backstage-surface-type-home"]{
	background: rgba(38,38,43) !important;
}

ytd-post-renderer{
	background-color: rgba(38,38,43) !important;
}


ytd-post-renderer[surface_="backstage-surface-type-home"][enable-shorter-height_] #toolbar.ytd-post-renderer{
	margin: 0 !important;
    background: rgba(38,38,43) !important;
}
#body div#dismissable {
 	background: rgba(38,38,43) !important;
    
}
ytd-post-renderer div#body{
	padding-bottom: 16px;
}

div#right-arrow{
	background: transparent !important;
     
}
/*  31,32,36 */
    
div#chat-messages yt-live-chat-header-renderer{
	background: rgb(31,32,36);
    border-radius: 10px 10px 0px 0px;
}

div#show-hide-button ytd-toggle-button-renderer{
	background: rgb(31,32,36) !important;
    border-radius: 0px 0px 10px 10px;
}

div#chat-messages{
	background: rgba(10,12,15, 0.8) !important;
}    
    

div#right-arrow #button{
	border-radius: 50%;
    
}
right-arrow a{
	background: white !important;
}

a.yt-simple-endpoint.ytd-button-renderer{
	color: white;
}

.arrow.ytd-merch-shelf-renderer{
	color: white;
    box-shadow: none;;
    border: 1px solid white
}



ytd-comment-renderer#comment ytd-toggle-button-renderer a{
	
}

#reply-button-end yt-formatted-string{
	color: var(--yt-button-text-color);
}
}
