css:
@-moz-document domain("tutorialspoint.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

.dev-box h5,
.whtboard,
	
div.featured-box h4,
h1.thick-heading,
nav ul.nav-main li a
{
	text-transform: capitalize!important;
}

.android img,
.chat-msg a.sendmsg,
.col-md-3,
.content img,
.footer-nav .iphone img,
.gsc-input-box,
.gsc-search-button-v2,.gsc-search-button-v2:focus,.gsc-search-button-v2:hover,
.l-btn-plain:hover,
.m-btn-plain-active,
.menu-active,
.microsoft img,
.more-btn,
.pages a,.pages a.number,.pages span,
.panel-body,
.panel-header,
.right-blog,
.search-widget,
.top-jobs,
.tp-content-widget .tp-content-viewmore a:hover,
.tut_profile .tut_rght h4 span,
.views,
.whtboard,
	
:focus,
div.featured-box,
ul.cat-menu ul li,ul.chapters li a img,
ul.menu li a,ul.menu li a:hover,
ul.sprite li
{
	border: none!important;
	box-shadow: none!important;
	outline: none!important;
    text-shadow: none!important;
}

/* Fonts 
_____________________________________________________________________________________________________*/

.fa-angle-down,
.fa-search,

[style="font-size:20px;"],
pre.prettyprint
{
	font-size: 2rem!important;
}

.spanQ,
.tutorial-content h2,

section.toggle>label
{
	font-size: 1.85rem!important;
}

.Q,
.qa_widget .qa_list h2,
.qa_widget .qa_post h1,
.qa_widget .qa_post_no_link h1,
.table>tbody>tr>td,
.tut_profile .tut_rght h2,
.whtboard,

div.featured-box h4,
li.heading,
li.sreading
{
	font-size: 21px!important;
}

.ace-cobalt,
.ace_editor,
.mui-col-md-6 label,
.qa_answer_widget .qa_answer_detail a,
.tut_profile .tut_rght .tut-title,
.tutorial-content p,

div.content p,
li,
p,
ul.toc>li a
{
	font-size: 1.5rem!important;
}

.mui-appbar-home a svg {
    font-size: 18px!important;
}

.qa_answer_widget {
	font-size: 17px!important;
}

.qa_category span {
	font-size: 15px!important;
	line-height: 27px!important;
}

.ace-cobalt {
	line-height: 25px!important;
}

.table>tbody>tr>td {
	text-align: center;
	vertical-align: middle;
}

.tutorial-content ul.list li {
    line-height: 42px!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

#google-top-ads,

._ap_apex_ad,
.adsbygoogle,
.bottomgooglead,
.course-box:after,
.enlarge img,
.google-right-ads,
.header-navbar .navbar-header .navbar-brand .brand-logo,
.ms-2,
.rightgooglead,
.top-ad-heading,
.topgooglead,

div.simple-ad,
ul.social-icons
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.sidenav {
    margin-right: 11%!important;
}

.mui-col-md-6,
.tutorial-toc {
	margin-left: 12px;
	margin-right: 12px;
    margin-top: 22px;
}

.float-right {
    margin-right: 15%;
}

#codebox .layout-split-west,

.layout-panel-east, .layout-panel-center
{
    top: 65px!important;
}

#codebox .layout-panel-center,
#codebox .tabs-header
{
    top: 65px!important;
}

.panel-body {
    top: 4rem!important;
}

.layout-split-west
 {
    top: 11px!important;
}

.row {
    margin-top: 2rem;
}

.mui-container .mui-col-md-3 {
    margin-top: -91px!important;
}

.navbar-light .navbar-brand {
    margin-top: 0!important;
}

/* Margins 
_____________________________________________________________________________________________________*/

#sidebar-left,

.col-md-3,
.col-md-7,
.compete-exams,
.content-wrapper,
.mui-container .mui-col-md-3,
.question_section,
.row.featured-boxes,
.sidebar-right,
.slick-initialized .slick-slide,
.tp-slider,
.tp-vd-courses

{
	margin: 1.2rem!important;
}

x.mui-container-fluid {
	margin: 9rem 12px 12px!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

#sidebar-left,
	
.card,
.col-md-3,
.col-md-7,
.compete-exams,
.content-wrapper,
.left-menu .mui-dropdown__menu,
.mui-container .mui-col-md-3,
.row.featured-boxes,
.sidebar-right,
.slick-initialized .slick-slide,
.tp-slider,
	
blockquote,
pre.prettyprint,
pre.result
{
	padding: 1.2rem!important;
}

.tutorial-content ul.list {
    padding: 1rem!important;
}

selector
{
	padding: 13px 26px!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

.left-menu .mui-dropdown__menu
{
	max-height: none!important;
}

.course-card-thumbnail img
{
	height: auto!important;
}

/* Widths 
_____________________________________________________________________________________________________*/

#privacy-banner a,

.container,
.content,
.content-wrapper,
.large,
.left-menu .mui-dropdown__menu,
.mui-container
{
	max-width: none!important;
}

.container,
.content
{
	width: 98%!important;
}

.col-md-7 {
    width: 74.333333%;
}

.col-xl-6 ,
.mui-col-md-6
{
    width: 73%!important;
}

.content-wrapper {
    width: 69.076%!important;
}

.mui-col-md-5 {
    width: 31.66667%!important;
}

.mui-container .mui-col-md-3 {
    width: 23%!important;
}

.col-md-3 {
    width: 15%!important;
}

.col-md-12.col-sm-12.col-xs-12 .col-md-3 {
    width: 23%!important;
}

.slick-initialized .slick-slide {
    width: 7%!important;
}

.left-menu .mui-dropdown__menu
{
	width: 47rem!important;
}


}

@-moz-document domain("tutorialspoint.com") {
/* Font Settings */

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
