h5,
tr:hover .ts,
ul.menu li a:hover
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

#privacy-banner p,
	
.MathJax_Menu,
.ace_text.ace_xml,
.add-to-cart-form,
