.mui-col-md-6 p,
.qa_answer_widget,
.qa_category span,
.tutorial-content ul.list li,
.text-secondary,
.ts,
.tut_profile .tut_rght h4 span,
.vid-options,
	
[style*="color:#545454;"],
blockquote,
div.content p,
pre.result,
table,
ul.menu li a,
p,
p:first-child
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.card-title,
.h5,
.image-title,
.imgs-title,
.main-title,
.mui-col-md-6 h1,
.mui-col-md-6 h2,
.mui-col-md-6 li.big-font,
.mui-col-md-6 th,
.mui-tabs__bar.mui-tabs__bar--justified>li>a,
.panel-title,
.top-trainer-desc p strong,
.tp-content-widget .tp-content-icons a div,
.tut_profile .tut_rght h2,
.whtboard,	

div.featured-box h4,
footer h4,
h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

.chat-msg a.sendmsg,
.crs-left-tagline,
.h6,
.mui-col-md-6 h3,
.mui-col-md-6 h4,
.mui-col-md-6 p b,
.tut_profile .tut_rght .tut-title,
.views,

b,
h3,
h4,
h6
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

.mui-col-md-6 h5,
.mui-col-md-6 h6,
.mui-col-md-6 label
 {
	color: #F0A060!important;
}

/* Blue 
_____________________________________________________________________________________________________*/

.qa_answer_dtm,
.qa_published_dtm,
	
time
 {
	color: #07beff!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.card .mui-col-sm-6,
.mui-col-md-5,
.price
 {
	color: #07ff24!important;
}

/* Red 
_____________________________________________________________________________________________________*/

selector
 {
	color: #ff0707!important;
}

/*
_____________________________________________________________________________________________________*/

.d-xl-inline-block,
.categories:hover,
.categories:focus,
.categories,

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

/*___ Code _________________________________________________________________________*/

#code .ace_line .ace_constant.ace_numeric {    color: #fbff00;}
#code .ace_line .ace_constant{    color: #ff8383;}
#code .ace_line .ace_keyword,.kwd{	color: #a4f7ff;}
#code .ace_line .ace_paren.ace_lparen,.clo,.opn,.pun{	color: #ffff91;}
#code .ace_line .ace_string,.pln,.str {	color: #f8f7f3;}
#code .ace_line,.Q,.ace_gutter-cell,.spanQ,.ace_punctuation.ace_operator{	color: #fff;}

.Q .correct {    color: #3eaa1c!important;}
.Q .wrong {    color: red!important;}
.Q span,i{	color: #ffdf6f!important;}

.ace-cobalt .ace_comment,.com{	color: #a9a5a5;}
.ace_comment{	color: #9e9e9d!important;}
.ace_identifier{	color: #efff74;}
.ace_layer .ace-crimson-editor .ace_support.ace_class,.ace_layer .ace-crimson-editor .ace_support.ace_type{    color: #8e9aff;}
.ace_storage.ace_type{	color: #ffcd74;}

.atn{	color: #ff83ff;}
.atv{	color: #5f5;}

.dec,.var{    color: #f0f;}
.lit {	color: #03ffff;}
.tag {	color: #8f8fff;}
.typ {    color: #ff30ff;}

pre.prettyprint,pre.result{	font-size: 1.95em;}






}

@-moz-document domain("tutorialspoint.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

.nimbus-is-editor {
