yt-live-chat-message-input-renderer
{
    --yt-live-chat-primary-text-color: rgb(255,255,255);
}

#input-panel.yt-live-chat-renderer, 
#input-panel.yt-live-chat-renderer *,
#watch-sidebar-live-chat #hide-live-comments
{
    background: rgb(45, 45, 45);
    --paper-input-container-input_-_color: rgb(255,255,255);
    --paper-input-container-input-color: rgb(255,255,255);
    --primary-text-color: rgb(255,255,255);
    --yt-emoji-picker-category-color: rgb(255,255,255);
}

#input-panel.yt-live-chat-renderer, 
#input-panel.yt-live-chat-renderer *
{
    background: rgb(45, 45, 45);
    --paper-input-container-input_-_color: rgb(255,255,255);
    --paper-input-container-input-color: rgb(255,255,255);
    --primary-text-color: rgb(255,255,255);
    --yt-emoji-picker-category-color: rgb(255,255,255);
}

#watch-sidebar-live-chat #hide-live-comments,
#watch-sidebar-live-chat .yt-uix-expander-collapsed
{
    background: transparent;
}

#count.yt-live-chat-message-input-renderer
{
    --yt-live-chat-secondary-text-color: rgb(255,255,255);
}

yt-emoji-picker-renderer yt-icon,
yt-live-chat-message-input-renderer yt-icon
{
    fill: white;
}

.yt-live-chat-renderer,
#watch-sidebar-live-chat
{
    background: rgb(33, 33, 33);
}

yt-live-chat-text-message-renderer[author-is-owner]
{
    background-color: transparent !important;
}

yt-live-chat-author-chip[is-highlighted] #author-name.owner.yt-live-chat-author-chip,
#author-name.owner.yt-live-chat-author-chip
{
    background-color: #ffb300;
    color: #000 !important;
}

ytd-menu-popup-renderer,
paper-listbox,
yt-formatted-string.ytd-menu-navigation-item-renderer,
paper-item.ytd-menu-navigation-item-renderer:hover,
yt-report-form-modal-renderer[dialog][dialog][dialog],
paper-dialog,
.paper-input,
yt-confirm-dialog-renderer[dialog][dialog][dialog],
#title.yt-confirm-dialog-renderer
{
    --yt-spec-brand-background-solid: rgb(30, 30, 30);
    --paper-dialog-background-color: rgb(30, 30, 30);
    --paper-listbox-background-color: rgb(30, 30, 30);
    --yt-menu-hover-backgound-color: rgb(40, 40, 40);
    --yt-primary-text-color: rgb(255, 255, 255);
    --yt-secondary-text-color: rgb(240,240,240);
    --paper-listbox-color: rgb(255, 255, 255);
    --yt-spec-text-primary: rgb(255, 255, 255);
    --yt-spec-brand-background-solid: rgb(30, 30, 30);
    --yt-dialog-background: rgb(30, 30, 30);
    --yt-emoji-picker-search-placeholder-color: rgb(210,200,200);
    --paper-input-container-color: rgb(210,200,200);
}
}
