/* ==UserStyle==
@name         Benchmark.pl - 2019 - Dark mode
@version      1.3.0
@description  Benchmark.pl - 2019 - Dark mode
@namespace    StylishThemes
@author       Sylwester Zarębski
@license      CC-BY-SA-4.0
@var color bg-main "Main Background Color" #222
@var color bg-lighter "Main Background (Lighter)" #333
@var color text-main "Main Text" #eee
@var color text-darker "Main Text (Darker)" #ddd
@var color text-darker2 "Main Text (Darker 2)" #aaa
@var color border-main "Main Border" #444
@var color poll-main "Main Poll Color" #666
@preprocessor default
==/UserStyle== */

/* Version history:
  v1.0 - nowa odsłona 2019-07-03
  v1.1 - poprawki podstrony logowania
  v1.2 - poprawki innych kolorów w tabelach
  v1.3 - listy
*/
@-moz-document domain("benchmark.pl") {
/* Główne */
body {
    background-color: var(--bg-main);
    color: var(--text-main);
}

a {
    color: var(--text-darker);
}

/* Artykuł */
.bbh-p-head-inner {
    border: none;
}

section > .container {
    background-color: var(--bg-main);
}

.bcrumbs ul > li > a {
    color: var(--text-darker2);
}

.categoryCompetition p,
.globalFullContainer p,
.inner-sec > article:first-of-type p {
    color: inherit;
}

article:first-of-type td[style*="background-color: rgb(242, 242, 242)"] {
    background-color: var(--bg-lighter) !important;
}

.cs-list li:first-child {
    color: inherit;
}

.categoryCompetition ol:not([class]),
.categoryCompetition ul:not([class]),
.globalFullContainer ol:not([class]),
.globalFullContainer ul:not([class]),
.inner-sec > article:first-of-type ol:not([class]),
.inner-sec > article:first-of-type ul:not([class]) {
    color: var(--text-darker);
}

/* Pasek boczny */
.prsn-nm {
    background-color: var(--bg-main);
    color: inherit;
}

.prsn-desc {
    background-color: var(--bg-main);
    color: inherit;
}

.box-big-heading.bbh-blue .bbh-al-detailed > ul > li:nth-of-type(n) {
    background-color: var(--border-main);
}

/* Komentarze */
.add {
    background-color: inherit;
}

.add textarea {
    height: 8em;
    border-color: var(--border-main);
    background-color: var(--bg-lighter);
    color: var(--text-main);
}

.comm-body > ul li.commentSingle:nth-child(2n) {
    background-color: var(--bg-lighter);
}

.comm-edit textarea, .comm-re textarea {
    border-color: var(--border-main);
    background-color: var(--bg-lighter);
    color: var(--text-main);
}

/* Głosowania */
.inlinePool .poolBody .answers_graph {
    background-color: var(--poll-main);
}

/* Stopka */
footer {
    background-color: inherit;
}

.ft-panel-grey {
    background-color: var(--bg-lighter);
}

.ft-bottom-links > li > a {
    color: var(--text-darker)
}

/* Podstrona logowania */
.form {
    background-color: var(--bg-lighter);
}

.form_row > .box > .box_inpt {
    background-color: var(--bg-main);
    color: var(--text-main);
}

.form_row > .box::before {
    color: var(--text-darker)
}

}
