css:
@-moz-document domain("darksky.net") {
* {
    color: #00fbff !important;
}

*:not(.colorpickertile):not(.colorpicker-button-colorbox):not(input) {
    background-color: #000 !important;
}

a *,
button *,
input *,
optgroup *,
select *,
textarea * {
    background-color: rgba(0, 0, 0, 0) !important;
    text-shadow: #000 0pt 0pt 1pt,
    #000000 0pt 0pt 2pt,
    #00ffff75 0pt 0pt 3pt,
    #00ffff75 0pt 0pt 4pt,
    #00ffff75 0pt 0pt 5pt,
    #000000 0pt 0pt 5pt,
    #000000 0pt 0pt 5pt !important;
}
a,
button,
input,
optgroup,
select,
textarea {
    text-shadow: #000000 0pt 0pt 1pt,
    #000000 0pt 0pt 2pt,
    #000000 0pt 0pt 3pt,
    #000000 0pt 0pt 4pt,
    #000000 0pt 0pt 5pt,
    #000000 0pt 0pt 5pt,
    #000000 0pt 0pt 5pt !important;
}
}

@-moz-document domain("darksky.net") {
canvas {
    -webkit-filter: invert(1);
    filter: invert(1);
}
input {
    background-color: #171717c4;
}
}
