css:
@-moz-document domain(mafiaonline.ru) {body {
    font-size: 14px;
    line-height: 1.3;
    color: #fff !important;
    background: #333 !important;
}

a { color: #e2e900; text-decoration: none; cursor: pointer; }
a:hover { text-decoration: none; }
span { font-size: 14px; line-height: 15px; color: #fff; }
input, textarea, select { font-size: 12px; font-family: arial, sans-serif; }
textarea { overflow: auto; }
p { line-height: 20px; font-size: 14px; }
/*.round-bordered {width: 30px;
    height: 30px;}


/*Street partiy*/
table.oldschool { border-collapse: separate; border-spacing: 7px 5px}
td.players {text-align: left; vertical-align: middle; border-radius: 8px; padding: 3px;}
.chat_bg {background: #111;}
table.oldschool th , .ui-button, .but ,.ui-dialog, .ui-dialog-titlebar ,.ui-dialog-buttonpane, #comment{background:#111 !important; border-radius: 8px;}
#dialog-form {background:#000;}

input[type="radio"] {
    
    background-color: white;
    border: 2px solid #000;
    width: 20px;
    height: 20px;
    position: relative;
    display: inline-block;
    color:#5A009D;
    border-radius: 50%;
    cursor: pointer;
    -webkit-appearance: none;
  	-moz-appearance: none;
  	appearance: none;
}
 input[type='radio']:hover {
    width: 20px;
    height: 20px;
    position: relative;
    background-color: #333;
    content: '';
    display: inline-block;
    visibility: visible;
    border: 2px solid white;
    -webkit-appearance: none;
  	-moz-appearance: none;
  	appearance: none;
    }
/*Game */
td.game_ramkal {line-height: 50px; }
.game_ramkal img {height: 50px; }
