div#smile_div {background-color: #222 !important;}

/*Info tables style */
.table_info {background-color:#333; border-collapse: separate; border-spacing: 7px 5px; border : 0px;}
.table_info td{background-color:#111 !important; border-radius: 8px; }
.table_info tr:nth-child(2n+1), .table_info tr:nth-child(2n){background-color:#333 !important;}

/* Profile style */
#profilenav {background-color:#333; border-collapse: separate; border-spacing: 7px 5px; border : 0px;}
#profilenav td{background-color:#111 !important; border-radius: 8px; }
#dopinfo {background:#2d2f2f !important;}


/*Forum*/
.postmsg, .form-group, .dropdown-menu.open span, #pagetext, .byuser, .modlist,.usertitle, .cell-midi, .cell-maxi, .hidden-xs, .break-word, h3, .text-center, .pull-left, .table  {color: #000;}
.col-md-2.text-center span.level {color: #000;}
.collapsed{color:red}
.panel-body p, .col-sm-2 p, .col-sm-2 span{color: #000;}
.control-label, .checkbox label, .bblinks.list-inline li {color: #000;}

/*Clans*/
#families span, .block.pun, .hidden-xs span{color: #000}

/*Log*/
.panel-body span.text{color: #000}
