css:
@-moz-document url-prefix("https://gab.com") {
/* Hides promo-panel, shows it on hover */
.promo-panel {
    opacity: 0 !important
}
.promo-panel:hover {
    opacity: 1 !important
}

/* Hides getting-started footer, shows it on hover */
.getting-started__footer {
    opacity: 0 !important
}
.getting-started__footer:hover {
    opacity: 1 !important
}

/* Hides wtf-panel (trending-panel) , shows it on hover */
.wtf-panel {
    opacity: 0 !important
}
.wtf-panel:hover {
    opacity: 1 !important
}
}
