css:
@-moz-document domain(mafiaonline.ru) {
body {
    font-size: 14px;
    line-height: 1.3;
    color: #fff;
    /*background: #ffb6c1  !important;*/
    background: url(https://previews.123rf.com/images/saidi21ns/saidi21ns1701/saidi21ns170100087/69917818-valentine-s-day-seamless-background-with-cute-pink-unicorn-and-pink-hearts-on-pink-background-suitab.jpg) center/50% repeat ;
}

 a { color: #0000CD; text-decoration: none; cursor: pointer; }
.chat_system{color: #8A2BE2;}
a:hover { text-decoration: none; }
span { font-size: 14px; line-height: 15px; color: #fff; }
.text, .chat_text { font-size: 14px; line-height: 15px; color: #000; }
.menuitem, .text a, #userlist a,  #location, #numusers {color: #000 !important;}
.level {background: #ffb6c1; color: #0000CD; }
.to { color: #4b0082;}
input, textarea, select { font-size: 12px; font-family: arial, sans-serif; }
textarea { overflow: auto; }
p { line-height: 20px; font-size: 14px; }

/*Street partiy*/
table.oldschool { border-collapse: separate; border-spacing: 7px 5px}
td.players {background: #333; text-align: left; vertical-align: middle; border-radius: 8px; padding: 3px;}
.chat_bg {background: #131313;}
table.oldschool th , .ui-button, .but ,.ui-dialog, .ui-dialog-titlebar ,.ui-dialog-buttonpane, #comment{background:#2d2f2f !important; border-radius: 8px;}
#dialog-form {background:#1a1a1b;}
input[type="radio"] {
    
    background-color: #5A009D;
    border: 2px solid white;
    width: 12px;
    height: 12px;
    position: relative;
    display: inline-block;
    color:#5A009D;
    border-radius: 50%;
    cursor: pointer;
    -webkit-appearance: none;
  	-moz-appearance: none;
  	appearance: none;
}
 input[type='radio']:hover {
    width: 10px;
    height: 10px;
    position: relative;
    background-color: #333;
    content: '';
    display: inline-block;
    visibility: visible;
    border: 2px solid white;
    -webkit-appearance: none;
  	-moz-appearance: none;
  	appearance: none;
    }



/*Bottom buttons images https://i.ibb.co/McpS4bJ/photo-2019-07-04-20-06-00-2.jpg*/

.send_button {background: url(https://i.ibb.co/DrJskTs/icons8-64.png) center/100% no-repeat;}
.send_button_ph {background: url(https://i.ibb.co/DrJskTs/icons8-64.png) center/100% no-repeat;}
.send_smile {background: url(https://i.ibb.co/DrJskTs/icons8-64.png) center/100% no-repeat;}
.send_clean{background: url(https://i.ibb.co/DrJskTs/icons8-64.png) center/100% no-repeat;}
.send_zamok{background: url(https://i.ibb.co/DrJskTs/icons8-64.png) center/100% no-repeat; }
.send_info{background: url(https://i.ibb.co/DrJskTs/icons8-64.png) center/100% no-repeat;}
.send_vverh {background: url(https://i.ibb.co/DrJskTs/icons8-64.png) center/100% no-repeat ;}
.send_smile2 {background: url(https://i.ibb.co/XjDDdBy/icons8-theatre-mask-26.png) no-repeat center;}
.send_zamok2{background: url(https://i.ibb.co/GWG85wJ/icons8-padlock-26.png) no-repeat center;}
.chat_global {background: url(https://i.ibb.co/m601XW9/global-Chat.png) no-repeat center;}
.chat_global2 {background: url(https://i.ibb.co/m601XW9/global-Chat.png) no-repeat center;}
.chat_lichn {background: url(https://i.ibb.co/Hnsd6rc/private-Chat.png) no-repeat center;}
.chat_lichn2{background: url(https://i.ibb.co/Hnsd6rc/private-Chat.png) no-repeat center; }
.chat_note{background: url(https://i.ibb.co/SXY6Fpj/blocknot-Chat.png) no-repeat center;}
.chat_note2{background: url(https://i.ibb.co/SXY6Fpj/blocknot-Chat.png) no-repeat center;}

#chat_note, div#smile_div {background-color: #131313 !important;}

/*Info tables style */
.table_info {background-color:#131313; border-collapse: separate; border-spacing: 7px 5px; border : 0px;}
.table_info td{background-color:#1a1a1b !important; border-radius: 8px; }

.table_info tr:nth-child(2n+1), .table_info tr:nth-child(2n){background-color:#1a1a1b !important;}

/* Profile style */
#profilenav {background-color:#131313; border-collapse: separate; border-spacing: 7px 5px; border : 0px;}
#profilenav td{background-color:#1a1a1b !important; border-radius: 8px; }
#dopinfo {background:#1a1a1b !important;}

/*Forum*/
.postmsg, .byuser, .modlist,.usertitle, .cell-midi, .cell-maxi, .hidden-xs, .break-word, h3, .text-center, .pull-left, .table  {color: #000;}
}
