css:
@-moz-document domain(flightradar24.com)
{
  div[id="modalBlackout"]
  {
    display: none!important;
  }

  div[id="disable-page-text"]
  {
    display: none!important;
  }
#mapView {
    display: none!important;
}
#fr24_SearchContainer {
    display: none!important;
}
#fr24_ViewsDropdownContainer > button {
    display: none!important;
}
#navContainer {
    display: none!important;
}
#map > a {
    display: none!important;
}
#map_canvas > div > div > div:nth-child(3) {
    display: none!important;
}
#map_canvas > div > div > div:nth-child(5) {
    display: none!important;
}
#map_canvas > div > div > div:nth-child(9) > div:nth-child(2) {
    display: none!important;
}
#map_canvas > div > div > div.gmnoprint.gm-style-cc > div:nth-child(2) {
    display: none!important;
}
#fr24_ViewsToolbar {
    display: none!important;
}
#mapStaticOverlays > div.airport-info-wrapper > section.scroll-wrapper > section.pnl-component.airport-general.active.no-travelers > div.myfr24-area.pnl-component.appear {
    display: none!important;
}
#mapStaticOverlays > div.airport-info-wrapper > section.scroll-wrapper > section.pnl-component.airport-general.active.no-travelers > div.pnl-component.metar-component.collapsed {
    display: none!important;
}
#mapStaticOverlays > div.airport-info-wrapper > section.scroll-wrapper > section.pnl-component.airport-general.active.no-travelers > div.airport-worker {
    display: none!important;
}
#mapStaticOverlays > div.airport-info-wrapper > section.scroll-wrapper > section.pnl-component.airport-general.active.no-travelers > div.pnl-component.collapsed.myfr24-component {
    display: none!important;
}
#mapStaticOverlays > div.airport-info-wrapper {
    top: 0px;
}
#mapStaticOverlays > div.airport-info-wrapper > section.scroll-wrapper > section.pnl-component.airport-general.active.no-travelers > div.pnl-component.scheduled-routes-stats.flight-data > div.flex-wrapper > div.row.link > a > span {
    display:none!important;
}
#arrival {
    display:none!important;
}
#departure {
    display:none!important;
}
#credits-3d > div.text-row > a:nth-child(5) {
    display:none!important;
}
#credits-3d {
    display: none!important;
}
#minimap-container {
    width: 250px;
    top: 24px;
}
#fr24_3DToolbar {
    margin-top: 0;
    bottom: 24px!important;
}
#cockpitView {
    top: 0;
    height:100%!important;
}
.flight-info-wrapper {
    top:24px!important;
}
.flight-info-wrapper:target {
    background-color: gold;
}
/* Add a pseudo-element inside the target element */
 .flight-info-wrapper:target::before {
    font: 70% sans-serif;
    content:"â–º";
    color: limegreen;
    margin-right: .25em;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.ads.slot0 {
    display: none!important;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.onboard.appear {
    display: none!important;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(11) > div.flex-wrapper > div:nth-child(1) > div:nth-child(2) {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(11) > div.flex-wrapper > div:nth-child(1) > div:nth-child(3) {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(11) > div.flex-wrapper > div:nth-child(1) > div:nth-child(4) {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(11) > div.flex-wrapper > div:nth-child(1) > div:nth-child(5) {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(11) > div.flex-wrapper > div:nth-child(2) {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(11) > div.flex-wrapper > div.row.with-white-divider {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(11) > div.icon-panel > svg.aircraft-weather {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.flight-data.fir.appear > div.flex-wrapper {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.flight-data.fir.appear > div.icon-panel {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.altitude-chart.collapsed > a {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.recent-flights.collapsed.appear > a {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(9) > div.flex-wrapper > div:nth-child(2) > div:nth-child(1) {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(9) > div.flex-wrapper > div:nth-child(1) > div:nth-child(2) {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.aircraft-info.appear > div.flex-wrapper > div:nth-child(3) > div:nth-child(1) {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.aircraft-info.appear > div.flex-wrapper > div:nth-child(3) > div:nth-child(2) {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.more-flightinfo.collapsed > a {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.data-source.appear.collapsed > a {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section.pnl-component.flight-data.squawk.appear > div.flex-wrapper > div:nth-child(1) > div:nth-child(2) {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.pnl-component.action-bar > ul > li:nth-child(5) {
    display:none;
}
#aircraft-popup {
    display:none;
}
#airport-popup {
    display:none;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(11) > div.flex-wrapper > div:nth-child(1) > div:nth-child(1) > div > span {
    visibility: hidden;
    position: relative;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(11) > div.flex-wrapper > div:nth-child(1) > div:nth-child(1) > div > span::after {
    visibility: visible;
    display:block;
    position: absolute;
    width: 200px;
    top: 0;
    left: 0;
    content: attr(data-tooltip-value);
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(9) > div.flex-wrapper > div:nth-child(1) > div:nth-child(1) > div > span {
    visibility: hidden;
    position: relative;
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(9) > div.flex-wrapper > div:nth-child(1) > div:nth-child(1) > div > span::after {
    visibility: visible;
    position: absolute;
    width: 200px;
    top: 0;
    left: 0;
    content: attr(data-tooltip-value);
}
#mapStaticOverlays > div.flight-info-wrapper > section.scroll-wrapper > section:nth-child(11) > div.flex-wrapper > div:nth-child(1) > div:nth-child(1) > div > span::before:first-child:nth-last-child() {
    display:none;
}
#cockpitView {
    width: 100%!important;
}
#minimap > div > div > div:nth-child(5) {
    display:none;
}
#minimap > div > div > div.gmnoprint.gm-style-cc > div:nth-child(1) > div:nth-child(2) {
    display:none;
}
#minimap > div > div > div:nth-child(3) > a > div > img {
    display:none;
}
}
