css:
@-moz-document domain(time.mk) {
  body {
    color: white;
    background-color: #272822;
  }
  #wrapper {
    background-color: #272822;
    padding: 1em;
  }
  .dbar a span {
    color: #66D9EF;
  }
  .related_article p a {
    color: #F92672;
  }
  #ads_pane {
    display:none;
  }
  #adpos1 {
    display:none;
  }
  #step1, #step2 {
    background-color: #272822 !important;
    border: 0 !important;
  }
  #step2 a {
    /*background-color: #FD971F !important;*/
    color: white;
    border: 0;
  }
  #split_pane #news_pane {
    width: 100%;
  }
  #news_pane .article_body {
    width: 80%;
  }
  a.super_btn {
    background-color:#272822 !important;
    color:white !important;
    border-right:0 !important;
  }
  a.extra_btn {
    background-color:#272822 !important;
    color:white !important;
    border:0 !important;
  }
  .cluster h1 a {
    color: #66D9EF;
  }
  #page_title p {
    color: #66D9EF;
  }
  .when{color:#FD971F}
  .article_body h2 .source {
    color:#FD971F !important
  }
  .related_article .r_source {
    color: #FD971F;
  }
  .other_sources span a {
    color: #A6E22E !important;
  }
  .other_articles {
    color: #A6E22E !important;
  }
  h10 {
    display:none
  }
  .kategorija_naslov {
    border:0;
    background-color: #AE81FF;
    color: #272822;
  }
  .kategorija_naslov center p{color: #272822 !important}
  /*video emisii*/
   .video > a > h3 {
    color: white
  }
  /*Ajro*/
   div.station:nth-child(1) > a:nth-child(2) > svg:nth-child(1) > text:nth-child(3) > tspan:nth-child(2) {
    fill:white
  }
  div.station:nth-child(2) > a:nth-child(2) > svg:nth-child(1) > text:nth-child(3) > tspan:nth-child(2) {
    fill:white
  }
  div.station:nth-child(3) > a:nth-child(2) > svg:nth-child(1) > text:nth-child(3) > tspan:nth-child(2) {
    fill:white
  }
  div.station:nth-child(4) > a:nth-child(2) > svg:nth-child(1) > text:nth-child(3) > tspan:nth-child(2) {
    fill:white
  }
  div.station:nth-child(5) > a:nth-child(2) > svg:nth-child(1) > text:nth-child(3) > tspan:nth-child(2) {
    fill:white
  }
  div.station:nth-child(6) > a:nth-child(2) > svg:nth-child(1) > text:nth-child(3) > tspan:nth-child(2) {
    fill:white
  }
  /*twitter*/

  /*Mess with the borders if u dont like te current look*/
  #twitter_widget .kategorija_naslov {
    border: none;
    border-radius: 0px
  }
   #twitter_widget > div > span {
    border: none
  }
  .content_bottom {
    border: none
  }
  #twitter_widget .tweet {
   /* border: none*/
  }
  #twitter_widget .timeline {
    /*border: none;*/
    border-radius: 0px
  }
  .content {
    color: white
  }
  .content_top .name{
    color:#66D9EF !important;
  }
  .content_top .nick{
    color:#FD971F !important;
  }
  .content_bottom .count{
    color:#A6E22E !important;
  }
  .content_bottom .tw_details a{
    color:#FD971F !important;
  }
  .content_bottom div a{
    color:#A6E22E !important;
  }
  .date{
    color:#FD971F !important;
  }
  .tweet u a {
    color: #F92672 !important;
  }
  #twitter_widget > div > span > span > a {color: #FD971F}
  .rng{color:#F92672}
  .pagination span {color:#A6E22E}
  .pagination span:hover {background-color:#196675 !important}
  .pagination a span.select{background-color: #F92672 !important;}

  /*javni nabavki*/
   #wrapper > div:nth-child(30) > div.source > div:nth-child(9) > table > tbody > tr:nth-child(1) {
    background-color: #393e2f;
  }
  #wrapper > div:nth-child(30) > div.source > div:nth-child(9) > table > tbody > tr:nth-child(1) > th:nth-child(5) > center > span > div:nth-child(2) {
    color:#66D9EF !important
  }
  .tabela a {
    color:#F92672
  }
  .tabela center{color: #272822}
  /*vremeplov*/
   #vremeplov h6 a {
    color: #66D9EF !important;
    text-decoration: none;
  }
  #footer {
    background-color:#272822 !important;
  }
  #footer .dbar a span, #copyright .dbar a span {
    color: #66D9EF !important;
  }
  #copyright .dbar span {
    color: #FD971F;
  }
 }
