}

.highlight {
    color: #91f4df!important;
}

 i {
    color: #f45c42!important;
}

.curs b {
    color: #f6b51c!important;
}

li .u {
    color: #f491d2!important;
}

ul i {
    color: #e2ff00!important;
}



}

@-moz-document domain("grammar-monster.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

#canvas1 {
    background: #363636;
}

/* Background 
_____________________________________________________________________________________________________*/

selector
{
	background: #060505!important;
	background-color: #060505!important;
}

.drop-down-button
{
    background-color: #1a1a1a!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

#turnerScore,

.ansBox,
.container,
.drop-down-list:hover,
.highlight,
.scoreBox,
.see-also a:hover,
.sidebox,
.siteNav a:hover,
.verbTable ul li:nth-of-type(even),

[style*="background: #c0c0c0"],
[style*="background: #f0f0f0"],
[style="background: #c0c0c0;"],
[style="background: #cc9999"],
div.cursr

{
	background: #0000!important;
	background-color: #0000!important;
}

/* Buttons 
_____________________________________________________________________________________________________*/

/* Blue 
_____________________________________________________________________________________________________*/

.cursq4
{	
	background: radial-gradient(circle, #031017a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.cursq4::before
{
	background: radial-gradient(circle, #000 0%, #031017 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.cursq4:hover::before
 {
  opacity: 1!important;
}

/* Red 
_____________________________________________________________________________________________________*/

input[type=button]
{	
	background: radial-gradient(circle, #990005a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

input[type=button]::before
{
	background: radial-gradient(circle, #000 0%, #990005 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

input[type=button]:hover::before {
  opacity: 1!important;
}

/* Orange 
_____________________________________________________________________________________________________*/

.guessLetter,
.wdLetter

{	
	background: radial-gradient(circle, #ca3e0ea8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.guessLetter::before,
.wdLetter::before
{
	background: radial-gradient(circle, #000 0%, #ca3e0e 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.guessLetter:hover::before,
.wdLetter:hover::before {
  opacity: 1!important;
}

/* Green 
_____________________________________________________________________________________________________*/

.bnet-form .formbtn,
.gameButtonOn,
.searchButton,
.shareMe,
.testButtonOn
{	
	background: radial-gradient(circle, #008000a8 0%, #000 100%)!important;
	border-radius: 22px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff50!important;
	color: #fff!important;
	opacity: 1!important;
	padding: .5rem 1.85rem!important;
	position: relative!important;
	z-index: 1!important;
}

.bnet-form .formbtn::before,
.gameButtonOn::before,
.searchButton::before,
.shareMe::before,
.testButtonOn::before
{
	background: radial-gradient(circle, #000 0%, #008000 100%)!important;
	border-radius: 22px!important;
	bottom: 0;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px #0000004d, inset -2px -3px 5px #ffffff80!important;
	color: #ff0!important;
	content: "";
	left: 0;
	opacity: 0!important;
	position: absolute!important;
	right: 0;
	top: 0;
	transition: opacity .5s linear!important;
	z-index: -1!important;
}

.bnet-form .formbtn:hover::before,
.gameButtonOn:hover::before,
.searchButton:hover::before,
.shareMe:hover::before,
.testButtonOn:hover::before
{
  opacity: 1!important;
}

/* Plain Black 
_____________________________________________________________________________________________________*/

#messageWindow,

.helpUs,
.see-also div,
.sidebox,
.sidelink,
.testbox
{
	background-color: #000!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.curriculum,
.main-content,
.sidebar-content,
.verbTable
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: .5rem!important;
}

#messageWindow:hover,

.curriculum:hover,
.helpUs:hover,
.main-content:hover,
.see-also div:hover,
.sidebox:hover,
.sidebar-content:hover,
.sidelink:hover,
.verbTable:hover,
.testbox:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Red Gradient 
_____________________________________________________________________________________________________*/

.drop-down-content,

[style="background: rgb(153, 17, 17);"]
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

.curriculum-button-off,
.curriculum-button:hover,
.gradient-background,

footer
{
	background: linear-gradient(to bottom, #300202 0%, #590505 100%)!important;
	border-bottom: 1px solid #000!important;
}

/* Blue Gradient 
_____________________________________________________________________________________________________*/

.callover,
.messageBox
{
	background: linear-gradient(to bottom, #03193c, #000 100%)!important;
}

/* Green Gradient 
_____________________________________________________________________________________________________*/

.navPoint,
.nextLesson,

[style="background: rgb(12, 91, 29);"],
div.cursr1,
h2 p,
header
{
	background: linear-gradient(to bottom, #008000 0%, #000 100%)!important;
}

/* Yellow Gradient 
_____________________________________________________________________________________________________*/

[style="background: yellow;"]
{
	background: linear-gradient(to bottom, #9d9d5d 0%, #000 100%)!important;
}

/* Orange Gradient 
_____________________________________________________________________________________________________*/

selector
{
	background: linear-gradient(to bottom, #da3a04 0%, #000 100%)!important;
}

/* Gray Gradient 
_____________________________________________________________________________________________________*/

.verbDescription
{
	background: linear-gradient(to bottom, #424040 0%, #000 100%)!important;
}

/* Gradient Border 
_____________________________________________________________________________________________________*/

.messageBox
{
	border-bottom: 1px solid #000!important;
	border-radius: 22px!important;
}

/* Code 
_____________________________________________________________________________________________________*/

.ansBoxILB
{
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background-size: 100%!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

.ansBox:hover,
.verbTable li:hover
{
	background: linear-gradient(to right, #008000, #0000)!important;
	border-left-color: #434343!important;
}

/* Radial Background 
_____________________________________________________________________________________________________*/

.ansBoxWrong
{
	background: radial-gradient(ellipse at center, #ff0008 0%,#0000 70%)!important;
	padding: 13px 26px!important;
}

.siteNav [style="background: #ddffdd;"]
{
	background: radial-gradient(ellipse at center, #ddffdd 0%,#0e0e0e 70%)!important;
	padding: 13px 26px!important;
}

.ansBoxRight
{
	background: radial-gradient(ellipse at center, #008000 0%,#0e0e0e00 70%)!important;
	padding: 13px 26px!important;
}


}

