css:
@-moz-document url-prefix("https://eksisozluk.com") {
::selection {
    background: #888;
    color: #fff;
}

::-webkit-scrollbar-track {
    background-color: transparent;
}

::-webkit-scrollbar {
    width: 7px!important;
}

::-webkit-scrollbar-thumb {
    background-color: #444 !important;
}

body,
.topic-list li > a {
	color: #aaa;
}

body,
body > header {
    background-color: #222;
}

body > header {
    border: 0;
    border-bottom: 1px solid #333;
}

input[type=text], input[type=email], input[type=search], input[type=number], input[type=url], input[type=datetime], input[type=date], input[type=time], input[type=password], select, textarea {
	border: 1px solid #666;   
}

h1, h3, h4, h5, h6 {
    color: #aaa;
}

h2 {
	font-weight: bold;   
}

a, h2 {
    color: #53a245;
}

button, input[type=submit], input[type=button] {
    border-radius: 4px;
    border: 0;
    background-color: #666;
    color: #ccc;
}

button:hover, input[type=submit]:hover, input[type=button]:hover, button:focus, input[type=submit]:focus, input[type=button]:focus {
    background-color: #888;
}

html.no-touch .topic-list li > a,
.more-data {
	border-radius: 4px;   
}

html.no-touch .topic-list li > a:hover {
    background-color: #333;
}

html.no-touch .pager > a:hover,
html.no-touch #partial-index::-webkit-scrollbar-thumb {
	background-color: #444;
}

html.no-touch a.more-data:hover {
	background-color: #222;   
}

.topic-list.partial > li > a > small {
    color: #53a245;
}

.topic-list.partial > li > a {
    padding: 8px;
    padding-right: 12%;
}

.clearfix.dropdown {
	padding-left: 8px;
}

.ui-autocomplete {
    background-color: #333;
}

.ui-autocomplete > li > div {
    color: #aaa;
    cursor: pointer;
}

.no-touch .ui-autocomplete > li > div.ui-state-active, 
.no-touch .ui-autocomplete > li > div:hover
{
	background-color: #444;
    color: #aaa;
}

.topic-list.partial >li > a > small {
    right: 8px;
    top: 8px;
}

.profile-stats-sections .load-more-entries {
	border-color: #666;
    border-radius: 4px;
}

.pager select,
.pager a,
.more-data {
    background-color: #333;
    border-color: #666;
    min-width: 47px;
    text-align: center;
}

.more-data {
	background-color: #222;   
}

.tabs > li.active > a {
    background-color: #333;
    color: #ccc;
}

.tabs {
    border-bottom: 1px solid #666;
}

.tabs > li.active {
    border: 1px solid #666;
}

.tabs > li > a:hover {
    background-color: #333;
}

.tabs2 {
	border-color: #666;   
}

.tabs2 > li {
	margin-bottom: 0;   
}

.tabs2 > li+li {
    margin-left: 15px;
}

.tabs2 > li:hover > a {
	color: #aaa;
    font-weight: normal;
}

.tabs2 > li.active > a {
    background-color: #333;
    padding: 3px 6px;
    border-radius: 4px;
    border-bottom: 0;
    color: #aaa;
    font-weight: bold;
}

.topic-list .seperator {
	margin-bottom: 5px;   
}

.topic-list .seperator hr {
	border-color: #666;   
}

.topic-list .seperator span {
    background-color: #333;
    padding: 4px 6px;
    border: 1px solid #666;
    border-radius: 4px;
}

.related-titles {
	background-color: #282828;
    border-radius: 4px;
}

.related-titles>ul>li {
    padding: 3px;
    font-size: 1.1em;
}

.eksico {
    fill: #aaa;
}

.dropdown .dropdown-menu {
	background-color: #333;
}

.dropdown .dropdown-menu a {
	color: #aaa;   
}

html.no-touch .dropdown .dropdown-menu li:hover {
	background-color: #444;   
}

#search-form #search-textbox {
    border: 0;
    height: 32px;
}

#search-form button[type=submit] {
    height: 32px;
    line-height: 32px;
    background-color: #777;
}

#main {
	margin-left: 270px;   
}

#partial-index .today-in-past-selector {
	padding: 0;   
}

#aside {
	display: none;
}

.sub-title-menu > a,
.sub-title-menu > div > a,
.sub-title-menu > div > .dropdown > a,
#top-navigation > ul > li > a,
#sub-navigation > ul > li > a {
	color: #666;
}

#content-body {
    width: 96%;
}

#top-bar .eksico {
    fill: #666;
}

#index-section {
 	width: 270px;   
}

#channel-follow-list li {
	border-radius: 4px;   
}

html.no-touch #channel-follow-list li:hover {
    background-color: #333;
}

#logo a {
    background-image: url(https://ekstat.com/img/new-design/eksisozluk_logo_darktheme.svg);
}

input, select, textarea,
#search-form #search-textbox {
    color: #ddd;
	background-color: #333;   
}

#partial-index .pager select,
#partial-index .pager a {
    width: 47px;
}

#site-footer {
	width: 96%;
    border-top: 1px solid #333;
}

#top-navigation {
	font-weight: bold;   
}

#search-form button[type=submit] .eksico {
	fill: #222;   
}

#entry-item-list .matter-index-item-title,
#matter-index-item-list .matter-index-item-title,
#matter-content .matter-index-item-title,
#draft-content .matter-index-item-title,
#disambiguations .matter-index-item-title,
#pinned-entry .matter-index-item-title {
        color: #aaa;
}

#matter-index-filter-options,
#matter-filter-options {
        background-color: #333;
}

#matter-index-filter-options a,
#matter-filter-options a {
        color: #aaa;
}

html.no-touch #matter-index-filter-options > li:hover {
    background-color: #444;
}

@media only screen and (min-width: 1060px) {
    #search-textbox {
        width: 565px;
    }
}

#index-filter-prefs,#index-filter-prefs-mobile {
    background: #1a1a1a;
}

.index-cookie-filter>li>#filter-index-channel.passive {
    color: #777;
}

#fixed-intro,
#topic>h1 {
    background-color: #222 !important;
}
}
