/* ==UserStyle==
@name           CSS - userstyles.org - Simple - v1@20190708
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Remove unnecessary elements.
@author         Me
==/UserStyle== */

@-moz-document domain("userstyles.org") {
    .adContainer,
    .fallbackDiv,
    .navigation,
    .flags,
    #wrapper-carousel,
    #ovals-wrapper,
    #installBox,
    #top_android_button,
    #footer,
    .android_button_banner,
    .android_button_button,
    .walking,
    .overlay_background
    {
        display: none !important;
    }
    
    #header
    {
       	background-color: lightslategray;
       	position: static;
    }
