css:
@-moz-document domain("duckduckgo.com") {
html {
    background-color: #000000;
    color: #fff;
}

.header-wrap {
    background-color: #000000;
    box-shadow: 0 1px 0 #232323;
}

.search--home, .search--header {
    background-color: #000000;
    border-color: #232323;
}

.site-wrapper {
    background-color: #000000;
}

.search-filters-wrap::before, .search-filters-wrap::after {
    background-color: inherit;
    background-image: inherit;
}

.result__check {
    color: #2f2f2f !important;
}

.result__check:visited {
    color: #fff !important;
}

.footer {
    background-color: #000000 !important;
    border-color: #232323 !important;
}

.footer__card {
    background-color: #000000 !important; 
}

.set-bookmarklet__input, .set-bookmarklet__data, .btn, .btn:visited, .btn:hover, .btn.is-active, .btn:active, .btn.btn--primary, .btn--primary:hover, .frm__input {
    background-color: #000000 !important;
    border-color: #232323 !important;
}

pre,
tt,
code {
    background-color: #000000 !important;
}

.module:not(.module--carousel):not(.module--placeholder):not(.module--images), .modal--popout .modal__box, .modal__box, .sidebar-modules .module, .results--main .result:not(.result--ad).highlight, .module .module--carousel__item, .user-loc-cta, .address-detail, .modal--dropdown.modal--popout .modal__box, .tile--info, .vertical--map__sidebar__results__inner, .place-detail, .badge-link, .atb-banner__bottom, .is-mobile .results_links_deep, .is-mobile .results_links, .is-mobile .result--news, .most-visited__info-tt {
    background: #020202 !important;
    box-shadow: 0 1px 3px #282828 !important;
}

.module__toggle, .tile__expand {
    background-color: #000000 !important;
}

.is-not-mobile-device .module__toggle:hover {
    color: #050505 !important;
}

.zci, .zci::before, .tileview .metabar--fixed, .tileview--grid .metabar--fixed, .tileview--grid .metabar--fixed.is-stuck {
    background-color: #000000 !important;
}

.tile--img__details {
    background: #050505 !important;
}

.btn.feedback-btn, .btn.feedback-btn a, .btn.feedback-btn a:hover {
    background-color: #101010 !important;
}
}

@-moz-document url-prefix("https://duckduckgo.com") {
#pg-index {
    background-color: #0f0f0f !important;
}

.search--home, .search--header {
    background-color: #0f0f0f;
    border-color: #232323;
}

.search__input--adv {
    color: #fff !important;
}
}

@-moz-document url-prefix("https://duckduckgo.com/settings") {
.modal__list__link:hover, .modal__list__link.is-active, .modal__list__link.is-highlighted, .modal--dropdown--region .modal__list__link.is-highlighted, .highlight .result__image, .switch, .cloudsave {
    background-color: #111 !important;
}

.set-bookmarklet, .set-reset, .set-head, .set-thumbnail__img, .set-main-footer, .region__header__section--current, .badge-link, .frm__color__swatch {
    border-color: #151515 !important;
}

.is-checked .btn.frm__switch__label {
    color: #e8e8e8 !important;
}
}
