css:
@-moz-document domain("dmzj.com") {
body{
  background:url("/*[[bg1]]*/"); 
}

.leftmiddle, .blue_bg, .top10_top, .read_mend_back, .week_foot, .wrap, .week_mend_nav, .topnav,.bbg, .footer, .intro_top{/*本体背景*/
    background: #272833;
}

.read_mend, li.rank_tab, li.rank_tab2, .s-nav3, .h2_title,.tcaricature_block2 ul, .topnav li a,.topnav2 li img, .blank-more-important:hover,.mainNav, .book_open, .week_mend, .work_mend_mr_a, .odd_anim_title_l, .odd_anim_title_m, .odd_anim_title_r, .h2_title2, .cartoon_online_button li.b1, .cartoon_online_border, .reply_content, .commentBox, .commentBox .textareaDiv,.commentBox .textareaDiv .textare{/*较浅背景*/
    background-color: #383a4d;
    background: #383a4d !important;
}

.h2_title, .read, .read_top, .blank-more-important, .top10_top, .h2_title2, .read_top_log{/*标题*/
    background-color: #383a4d;
    background: #383a4c;
    color: lightgrey;
    padding: 8px 0 0 8px;
    text-align: left;
    font-family: "思源黑体","微软雅黑";
    font-size: x-large;
    height: 20px;
    font-weight: 500;
    width: auto;
    border: 0px;
    margin-top: 20px;
    line-height: inherit;
    border-radius: 0 14px 14px 0;
}

.topnav2 li span,li.rank_tab2 a, li.rank_tab, 
.top_list a,
a,
.book_con_bottom,
.btm_chapter_btn,.tcaricature_block
{
    color:#8f97c3;
    font-family: "思源黑体","微软雅黑";
}

.tcaricature_see1_a2_b, .today_recommended_bigpic2_a2, .gray12, .black_font12, .mainNav_in_l a, .mainNav_in_r ul li a, .book_open .book_open_tabs>li.font, 
.book_open .book_con_li li a.book_title,
.path_lv3, .subscribe_num, .linehidden2, .odd_anim_title_m, .line_height_content, .graybold, .adivhidden, .content_r .text,input.search_sr,.commentBox .textareaDiv .textare
{color: #c4cfe2;}

.tcaricature_block ul li img, .topnav2 li img, .footer, .book_con_bottom, .book_open .book_open_tabs, .slider-wrap,
.today_recommended_bigpic2_a1, .today_recommended_bigpic2_a, .today_recommended_bigpic2_b2, .cartoon_desc, .cartoon_online_border, .cartoon_online_button, .commentBox, .btm_chapter_btn{border:0px}/*清除边框*/
}

@-moz-document regexp("https?://(www\\.)?(manhua\\.)?dmzj.com.*") {
.read_mend, ul.topnav.switch_tab{margin-top:20px;}
.read_top, .top10_top, .blank-more-important, .read{width:250px}
.read_mend_mr_a{background: #272834;} .red_font12{font-family:'Josefin Sans',"思源黑体","微软雅黑";line-height:22px;}

.right{margin-top: 56px;width: 239px;float: left;background-color: #383a4b70;padding: 0px 1px 0 1px; }
.right_mr{margin:0 !important;}

.mainNav, .book_wrap{position:fixed; z-index: 10;}
.mainNav_in_l{margin-left: 295px;}
.search_b_box{position: fixed;top: -1px;margin-left:5px;z-index:11;width: 270px;}
.mainNav_in_l a:hover {
    background-color: #dbdbdb;
    color: #1b203c;
    height: auto;
    line-height: unset;
    margin:0 0;
    padding: 2px 4px 10px 4px;
    border-bottom: #383a4c solid 5px;
}
.mainNav_in_l a{margin:0 4px;}
.week_foot{height:0px; width:auto;}

.h2_title {margin-bottom: 21px;}
.h2_title h2{font-size:x-large}
.h2_title a{font-size:5px;}
.today_recommended>.h2_title a {font-size: 17px;position: absolute;}

.tcaricature_block ul li img:hover, .topnav2 li img:hover{
    position: relative;
    width: 200px;
    height: auto;
    z-index: 200;
    top: 0;
    left: -52px;
}



.tcaricature_block2 ul{margin: 1px;width: 100px;padding:0;height: 189px;}
.tcaricature_block{margin: 18px 0 0 42px;}

.topnav2, #category_recom_0{margin:0 0 0 42px;}
.topnav li a{border:unset;}
#category_recom_0>ul>li, .topnav2 li{padding:0;}
.topnav2>li{height: 189px;}
ul.topnav.switch_tab {margin-left: 25px;}

.twoer_index-li {background-color: #353645;}

.blank-more-important:hover{text-align:left;}

.week_mend_nav2>ul>.rank_tab2{width:52px;}
.week_mend_nav li{width:70px;}

input.search_sr:focus {outline: none;}
input.search_sr {
    background-color: #383a4c;
    border-radius: 12px 0 0 12px;
    border-style: inset none inset inset;
    height: 19px;
    width: 184px;
    padding-left: 12px;
}
.search_input{
    background-color: #383a4c;
    border-radius: 0 12px 12px 0;
    border-width: 2px;
    border-style: inset inset inset none;
    height: 23px;
    background: coral;
    top: 0px;
    left: 0px;
}
.mainNav{
    height:37px;
    border:0px;
}


.book_open .book_con_li li{color:#8f97c3}
.book_open .book_con_li li a.book_title.wid {width: 140px;color: inherit;}
.book_open .book_con_li li.li_bg, .book_open .book_con_li li:hover {
    background: #ebeff2;
    color: #29101c;}
.book_open .top{left:0;}


.week_mend_back{background:#383a4b}

.anim-main_list {opacity:0;}
.anim-main_list:hover {opacity:1}

.msg>a, .rss{font-size: small;}

.middleright{margin-top:14px;}
.anim_title_text h1 {
    font-size: 100%;
}

.margin_top_10px, div#type_comics{margin-top:5px;}

.cartoon_online_button li.b1 {
    border-style: none none solid none;
    border-bottom-color: #383a4c;
}

.part_collection_bnt{background:#797c9a;}
.part_collection_bnt:hover{background:#e6200e}

div#type_comics, div#type_comics>ul{width: auto;}
div#type_comics ul li img:hover{width:160px;left:0;}

#hd{border:0 !important;}

.display_graybg {
    position: absolute;
    top: -200px;
}
.newpic_bgno {
    background-color: #eeefff;
        height: 154px;
}
}

@-moz-document domain("dmzj.com") {
/**广告,常年无变化的东西**/
.caricature_nav,
div.search_b_box>form>span>label,
div.search_b_box>form>.search_botton,
.float_code>img,
iframe.share_self,
.header-box,
.display_graybg funcdiv,
.hotrmbox,
.footer,
.recom_one,
.icorss_acg,
.h2_title2, 
.h2_icon,
.flink-box
{display:none}

.toppic_content
{top: -500px;position: absolute;}

.weiboShow .weiboShow_wrap {background: #272834;}
/**********************************/
}

@-moz-document regexp("https?://manhua.dmzj.com/[0-9a-zA-Z]+/\\d+.*") {
.btm_chapter_btn:hover{opacity: 1;}
.btm_chapter_btn{
    position: fixed;
    bottom: 0;
    width: 165px;
    height: 27px;
    padding-top: 7px;
    line-height: unset;
    text-align: center;
    background-color: #383a4c;
    opacity:0;
}
.btm_chapter_btn.fl  {left:0;} 
.btm_chapter_btn.fr {right: 0;}
.btm_chapter_btn:visited {
  color: #992957;
}


.display_middle:hover {
    background: white;
    height: 29px;
    opacity: 1;
}

.display_middle {
    bottom: 0px;
    position: fixed;
    color: #ec136f;
    font-size: 14px;
    font-weight: bold;
    z-index: 101;
    left: 0;
    right: 0;
    margin: auto;
    line-height: 19px;
    height: 29px;
    opacity: 0;
    transition: all 1s;
    transition-timing-function: cubic-bezier(0.28, 1.01, 0.64, 1.03);
}

.display_graybg+*>div:nth-of-type(1){
    position: fixed;
    z-index: 20;
    left: 7px;
    top: 8px;
color: #272a39;
}

div#center_box img {
    padding:0 !important;
    border:0 !important;
    border-radius: 6px;
    box-shadow: 0 0 14px #5b5e7b;
}
.mainNav {
    opacity: 0;
    transition: opacity 0.5s;
}
.mainNav:hover {
  opacity: 1;
}
.inner_img{
    margin-top: 0px !important;
}
.curr_page {
    height: 20px;
    line-height: 20px;
}
#app_manhua{display:none !important;}
}
