/* i really want this to be global */
.checklist-title, .checklist-item-details, .editable .description-content .description-edit .description, .sc-gZMcBi jJCRUe {
	direction: rtl;
}
.checklist-item-checkbox {
	left: auto;
	right: 0;
}
.checklist-item-details .checklist-item-menu-wrapper {
	right: auto;
	left: 0;
}
