css:
@-moz-document domain("cryptii.com") {
/*Custom Colours!*/
:root {
    --highlight: #007f96;
}

body,
.modal__content,
.pipe__scrollable {
    background: #333;
}

#carbonads,
option,
.header__badge,
.brick,
.featured,
.field__message,
.modal-library__brick,
.field-number__display,
.modal__dialog,
.field-byte__input[type=text],
.field-number__display,
.field-number__input,
.field-text__input[type=text] {
    background: #444;
}

.header,
.footer {
    background: #2f2f2f;
}

.pipe__part-pipe {
    background: var(--highlight);
    border-color: var(--highlight);
}

.pipe__btn-add {
    mix-blend-mode: difference;
}

body,
select,
.field-text__input[type=text],
input,
textarea,
.field-number__display,
.field-number__input,
.field-enum--radio .field-enum__option-radio:checked + .field-enum__option-label {
    color: #ccc;
}

.field-boolean__input:checked + .field-boolean__toggle .field-boolean__choice:first-child,
.field-boolean__choice:last-child {
    color: #666;
}

.brick__header,
.field,
.field__label {
    border-color: #3f3f3f;
    border-width: 2px;
}

.footer {
    border: none;
}
}
