css:
@-moz-document url-prefix("http://forum.football365.com") {
div.copyright {
  color: #989da4;
}

label {
  color: #989da4;
}

#tabs .tab > a {
  background: #78818e;
  color: #536482;
}

#tabs .tab > a:hover {
  background: #6d7279;
  color: #989da4;
}

#tabs .activetab > a,
#tabs .activetab > a:hover {
  background: #536482;
  background: -webkit-gradient(linear, left top, left bottom, from(#bec9d6), to(#70747d));
  background: linear-gradient(to bottom, #bec9d6 0%, #70747d 100%);
  border-color: #536482;
  -webkit-box-shadow: 0 1px 1px #70747d inset;
          box-shadow: 0 1px 1px #70747d inset;
  color: #333333;
}

.cp-mini {
  background-color: #696969;
}

dl.details dt {
  color: #989da4;
}

dl.details dd {
  color: #cccccc;
}

.pagination li a {
  background-color: #a3a4a5;
  border-color: #B4BAC0;
  color: #5C758C;
}

.pagination li.ellipsis span {
  background-color: transparent;
  color: #000000;
}

.pagination li.active span {
  background-color: #326a87;
  border-color: #4692BF;
  color: #FFFFFF;
}

.pagination li a:hover, .pagination .dropdown-visible a.dropdown-trigger, .nojs .pagination .dropdown-container:hover a.dropdown-trigger {
  background-color: #368AD2;
  border-color: #368AD2;
  color: #FFFFFF;
}

.button {
  border-color: #616161;
  background-color: #696969;
  background-image: -webkit-gradient(linear, left top, left bottom, from(#9b9b9b), to(#696969));
  background-image: linear-gradient(to bottom, #9b9b9b, #696969);
  box-shadow: 0 0 0 1px #616161 inset;
  -webkit-box-shadow: 0 0 0 1px #616161 inset;
  color: #dddddd;
}

.button:hover, .dropdown-visible .dropdown-select, .nojs .dropdown-container:hover .dropdown-select {
  border-color: #616161;
  background-image: -webkit-gradient(linear, left top, left bottom, from(#E9E9E9), to(#616161));
  background-image: linear-gradient(to bottom, #E9E9E9, #616161);
  /* text-shadow: 1px 1px 0 #616161, -1px -1px 0 #616161, -1px -1px 0 rgba(188, 42, 77, 0.2); */
}

#navigation a {
  color: #333;
  background: #696969;
  background: -webkit-gradient(left top, right top, color-stop(50%, #696969), color-stop(100%, #768291));
  background: -webkit-gradient(linear, left top, right top, color-stop(50%, #696969), to(#768291));
  background: linear-gradient(to right, #696969 50%, #768291 100%);
}

#navigation a:hover {
  background: #798894;
  color: #9b9b9b;
}

#navigation #active-subsection a {
  background: #7e7e7e;
  color: #9b9b9b;
}

#navigation #active-subsection a:hover {
  color: #9b9b9b;
}

div.bg3 {
  background-color: #70747d !important;
}

ul.topiclist dd {
  border-left-color: #818181 !important;
}

ul.navlinks {
  border-top-color: #989da4;
}

.fixedfluidfixed-content {
  width: calc(100%);
  float: left;
}

.fixedfluidfixed-right {
  display: none !important;
}

#page-footer > div:first-child {
  display: none !important;
}

/* END ADVERT SHITHOUSERY */
#wrap {
  max-width: 100%;
}

.forumbg {
  border-radius: 0;
  margin-bottom: 8px;
}

.navbar {
  background-color: #eee;
  border-radius: 0;
}

.forumbg.announcement {
  margin-top: 7px;
}

.headerbar {
  background-color: #4b4b4b;
}

#logos {
  display: none !important;
}

#page-footer #leaderboard-2 {
  display: none;
}

/**** END GLOBAL AND TOP / BOTTOM AREA CHANGES ****/
/**** NARROW SCREEN AMENDMENTS ****/
@media screen and (min-width: 700px) and (max-width: 1176px) {
  .postbody {
    width: 80% !important;
  }
}

/**** END NARROW SCREEN AMENDMENTS ****/
/**** THREAD AREA CHANGES ****/
.post {
  border-radius: 0;
  margin-bottom: 2px;
}

.post .responsive-hide {
  display: none;
}

.content blockquote {
  background-size: 11px !important;
  padding: 7px;
}

blockquote.uncited {
  padding-top: 25px !important;
}

.postprofile {
  color: #a8a8a8;
  border: none;
  float: left;
}

.postprofile strong {
  color: #cccccc;
}

.postprofile {
  width: 12%;
}

.postbody {
  width: 87%;
  color: #888888;
}

.postbody {
  border-left: 1px solid #bbbbbb;
  padding-left: 10px;
}

.post .content {
  font-size: 13px !important;
  line-height: 1.5 !important;
}

.online {
  background-image: none !important;
}

.post {
  border: 1px solid #c4c4c4;
}

.postprofile .profile-posts a {
  font-weight: bold;
}

/**** END THREAD AREA CHANGES ****/
/**** CHANGES POSTING AREA ****/
#postingbox #smiley-box {
  float: left;
}

#postingbox #message-box {
  float: right;
}

/**** END POSTING AREA CHANGES ****/
/**** CHANGE THE MAIN FORUM LIST ****/
/*div.inner {*/
/*    background-color: #00AA00;*/
/*}*/
div.forumbg li.row div.list-inner {
  padding-top: 10px;
}

div.forumbg li.row div.list-inner div.responsive-hide {
  margin-top: -18px;
  text-align: right;
}

div.forumbg li.row div.list-inner div.responsive-hide a {
  display: block;
  float: right;
  margin-top: 3px;
}

div.forumbg li.row div.list-inner br,
li.row div.list-inner div.responsive-hide span.imageset {
  display: none !important;
}

div.forumbg li.row div.list-inner div.pagination {
  display: inline-block;
  float: none;
  position: relative;
  top: -2px;
}

ul.topiclist .row {
  background-color: #aaa;
  color: #989da4 !important;
}

li.row:hover {
  background-color: #eee;
}

.forum_unread,
.forum_read {
  background-image: none !important;
}

.topiclist.forums .list-inner {
  margin-top: 11px;
  padding-left: 10px;
  background-color: #505050;
}

.sticky {
  background-color: #dfdfdf !important;
}

ul.topiclist.topics .responsive-hide a {
  margin-left: 4px !important;
}

/**** END HOME FORUM CHANGES ****/
/**** HOME THREAD TITLES & PAGINATION ****/
ul.topiclist.topics .pagination::before {
  content: "(";
  color: #777777;
}

ul.topiclist.topics .pagination::after {
  content: ")";
  color: #777777;
}

ul.topiclist.topics .pagination li a::after {
  content: ",";
}

ul.topiclist.topics .pagination li a {
  border: 0 none;
  font-size: 0.9em !important;
  font-weight: bold;
  background-color: transparent;
}

ul.topiclist.topics .pagination li a:hover {
  background-color: transparent;
  font-weight: bold;
  color: #d31141;
  text-decoration: underline;
}

.pagination li:last-of-type a:after {
  content: "" !important;
}

ul.topiclist.topics a.topictitle {
  font-size: 1.1em;
}

/**** END HOME THREAD TITLE & PAGINATION ****/
/**** REMOVAL OF ICONS EVERYWHERE ****/
ul.topiclist .row .topic_read,
ul.topiclist .row .topic_unread,
ul.topiclist .row .topic_unread_hot,
ul.topiclist .row .topic_read_hot,
ul.topiclist .row .topic_unread_hot_mine,
ul.topiclist .row .topic_read_hot_mine,
ul.topiclist .row .topic_unread_mine,
ul.topiclist .row .topic_read_mine,
ul.topiclist.topics .sticky_read_mine,
ul.topiclist.topics .sticky_unread,
ul.topiclist.topics .sticky_unread_mine,
ul.topiclist.topics .sticky_unread_locked,
ul.topiclist.topics .sticky_read_locked,
ul.topiclist.topics .topic_unread_locked,
ul.topiclist.topics .topic_read_locked,
ul.topiclist.topics .topic_read_locked_mine,
ul.topiclist.topics .topic_unread_locked_mine,
ul.topiclist.cplist .sticky_read_mine,
ul.topiclist.cplist .sticky_unread_mine,
ul.topiclist.topics .sticky_read,
ul.topiclist.cplist .global_read_locked,
ul.topiclist.cplist .global_unread_locked {
  background-image: none !important;
}

ul.topiclist.topics dt,
ul.topiclist.cplist dt {
  background-image: none !important;
}

ul.topiclist.topics .row .list-inner,
ul.topiclist.cplist .row .list-inner {
  padding-left: 7px;
}

.announcement ul.topiclist.topics .list-inner {
  padding-left: 45px !important;
}

/**** END ICON REMOVAL ****/
/**** GET THE BELLS BACK ****/
.imageset.icon_topic_newest {
  background-image: url("https://s22.postimg.cc/pp2x11sap/unread-circle.png");
  margin-top: 0;
  padding-left: 16px;
  padding-top: 14px;
}

/**** END OF BELLS BACK ****/
/**** PERSONAL STYLES IN HERE (COPY FROM HERE IF UPDATING) ****/
html,
body {
  background-color: #4b4b4b;
  color: #989da4;
}

#wrap {
  max-width: 1800px;
  background-color: #505050;
  border-style: none;
}

a {
  color: #989da4;
}

a:hover {
  color: #989da4;
}

.forumbg.announcement {
  margin-bottom: 20px;
}

.action-bar.top {
  margin-bottom: 10px;
  margin-top: 10px;
}

a.topictitle,
a.forumtitle {
  font-family: Arial !important;
  font-size: 13px !important;
  line-height: 18px;
  text-decoration: none;
}

.forumbg,
.forabg {
  border: 1px solid #818181;
  padding: 0;
  background-color: #4b4b4b;
}

.forumbg .topiclist .header,
.forabg .topiclist .header {
  background-color: #4b4b4b;
  font-size: 13px;
  padding-bottom: 5px;
  padding-left: 23px;
  padding-top: 5px;
}

.navbar {
  font-size: 11px;
  background-color: #505050;
}

h2.forum-title {
  display: none;
}

.sticky .topictitle:before {
  content: "Sticky: ";
  color: #558e7c;
  font-weight: bold !important;
  font-size: 11px;
}

.sticky .imageset.icon_topic_newest {
  background-image: none;
  margin-top: 0;
  padding-top: 14px;
  padding-left: 0px;
}

ul.topiclist .row .topic_unread a.topictitle {
  font-weight: bold;
}

ul.topiclist .row .topic_read a.topictitle,
ul.topiclist .row .topic_read_hot a.topictitle,
ul.topiclist .row .topic_read_hot_mine a.topictitle,
ul.topiclist .row .topic_read_mine a.topictitle {
  font-weight: normal !important;
}

ul.topiclist .row.bg1 {
  background-color: #505050 !important;
}

ul.topiclist .row.bg2 {
  background-color: #575757 !important;
}

ul.topiclist .row .sticky_read,
ul.topiclist .row .sticky_unread,
ul.topiclist .row .sticky_read_mine,
ul.topiclist .row .sticky_unread_mine,
ul.topiclist .row .sticky_unread_locked,
ul.topiclist .row .sticky_read_locked {
  background-color: #6e6e6e;
}

ul.topiclist .row .sticky_read a.topictitle,
ul.topiclist .row .sticky_read_mine a.topictitle {
  font-weight: normal !important;
}

ul.topiclist li.row {
  border-bottom: none;
  border-top: none;
}

ul.topiclist .row .topic_read,
ul.topiclist .row .topic_read_hot,
ul.topiclist .row .topic_read_hot_mine,
ul.topiclist .row .topic_read_mine,
ul.topiclist.topics .topic_read_locked,
ul.topiclist.topics .topic_read_locked_mine,
ul.topiclist.cplist .global_read_locked {
  padding-left: 20px;
}

.sticky_read .list-inner,
.sticky_read_mine .list-inner {
  padding-left: 10px !important;
}

li.row:hover dd {
  border-left-color: #fff;
}

.action-bar.bottom {
  background-color: #5f5f5f;
  padding: 10px;
  color: #989da4;
}

.action-bar.bottom a {
  color: #cccccc;
}

.action-bar.bottom .pagination a {
  color: #555555;
}

.action-bar.bottom .pagination a:hover {
  color: #ffffff;
}

.action-bar.bottom .buttons a {
  color: #555555;
}

.action-bar.bottom ul,
.action-bar.bottom ul a {
  color: #555555;
}

p.jumpbox-return {
  font-family: Arial !important;
  font-size: 12px !important;
}

/* FRONT FORUM PAGE */
ul.topiclist.forums .row {
  background-color: #505050;
}

.forabg {
  border-radius: 0;
}

.forabg .topiclist .header {
  padding-left: 10px;
}

.stat-block,
form.headerspace {
  font-family: Arial !important;
  font-size: 11px !important;
}

/* THREAD AREA CHANGES */
.postbody .content {
  font-family: Arial !important;
  color: #989da4 !important;
}

.post {
  border: 1px solid #646464;
  margin-bottom: 10px;
}

dl.postprofile a.username,
dl.postprofile a.username-coloured {
  font-size: 11px !important;
}

p.author {
  font-family: Arial;
  font-size: 11px;
  font-weight: bold;
}

h3 {
  color: #989da4;
}

.postbody h3 {
  font-size: 13px;
}

.postbody h3.first {
  font-size: 13px;
}

.postbody {
  border-left: 1px solid #969696 !important;
}

.post.bg1,
.post.bg2 {
  background-color: #505050;
}

.content blockquote {
  background-color: #5f5f5f !important;
  border-color: #5f5f5f;
}

div.quotecontent div {
  background-color: #5f5f5f !important;
}

/* POSTING AREA CHANGES */
#postingbox.panel {
  background-color: #f7f7f7;
}

.panel {
  border-radius: 0 !important;
  color: #989da4;
}

.panel-container .panel {
  background-color: #5f5f5f;
}

ul.topiclist .row .topic_unread_hot_mine .imageset.icon_topic_newest,
ul.topiclist .row .topic_unread_mine .imageset.icon_topic_newest,
ul.topiclist.topics .sticky_unread_mine .imageset.icon_topic_newest,
ul.topiclist.topics .topic_unread_locked_mine .imageset.icon_topic_newest,
ul.topiclist.cplist .sticky_unread_mine .imageset.icon_topic_newest {
  background-image: url("https://s22.postimg.cc/ckxcod7yp/unread-mine.png") !important;
}
}
