css:
/* Scrollbar track > #f1f1f1 */
::-webkit-scrollbar {
    background-color: rgba(128,128,128,.11);
	width: 1%;
	max-width: 14px;
	height: 1%;
	max-height: 14px;
	min-width: 10px;
	min-height: 10px;
}

/* Scrollbar > #c1c1c1 */
::-webkit-scrollbar-thumb {
    background-color: rgba(125, 125, 125, .4);
    max-height:50px;
	border: 1px solid rgba(0, 0, 0, 0);
    background-clip: padding-box;
}
/* Scrollbar when hover> #a8a8a8 */
::-webkit-scrollbar-thumb:hover {
    background-color: rgba(127,127,127,0.63);
}
/* Scrollbar when clicked> #787878 */
::-webkit-scrollbar-thumb:active {
    background-color: rgba(68, 68, 68, .7);
}
/* Scrollbar corner > #dcdcdc */
::-webkit-scrollbar-corner {
    background-color: rgba(68, 68, 68, .245);
}



@-moz-document domain("www.messenger.com") {
::-webkit-scrollbar {
    width:0px;
	height:0px;
}
}
