css:
@-moz-document url("https://jira.arvato-support.com/"), url-prefix("https://jira.arvato-support.com/") {
.ghx-issue{
    padding: 1px;
    margin: 1px;
}

.ghx-issue .ghx-card-footer > .ghx-type{
    display:none;
}
.ghx-issue .ghx-card-footer > .ghx-flags{
    display:none;
}
.ghx-issue .ghx-card-footer > .ghx-days{
    display:none;
}
.ghx-issue .ghx-end{
    display:none;
}

.ghx-issue .ghx-issue-content {
    min-height: 0;
    padding: 2px;
    background-color: #e1efff;
}
.ghx-issue.ghx-issue-subtask .ghx-issue-content {
    background-color: #e1FFe1;
}

.ghx-issue-subtask{
    margin-left: 20px;
}

.ghx-column-headers .ghx-column{
    padding: 0;
}

.ghx-issue-fields .ghx-key{
    font-size: 10px;
}
.ghx-parent-stub .ghx-group{
    background-color: #e1efff;
}
.ghx-parent-group.ghx-home, .ghx-parent-group.ghx-away{
    border-left: 2px solid gray;
    margin-top: 10px;
    margin-bottom: 10px;
}
.ghx-days{
    display:none;
}
.ghx-flags{
    display:none;
}
.ghx-type{
    display:none;
}
.ghx-issue-fields .ghx-key{
    font-size: 12px !important;
}
}
