css:
@-moz-document domain("coub.com") {
a,
html.win .page-menu__item:not(.list__item).-active,
.page-menu__item:not(.list__item),
.description__controls,
[blocks-stories-block] .more-stories-button,
.page-menu .dropdown--select .dropdown__handler .sb.-toggle,
.header__nav .nav__item .nav__item__inner,
html.win strong,
html.win .hbold,
html.win .channels-block.channels-block--list h3,
.main-menu__community-item,
.main-menu__nav-item a,
.locale-selector .dropdown__handler span,
.footer__country-selector .handler span,
.list.list--selectable .list__item:not(.disabled).active > a,
.list.list--selectable .list__item:not(.disabled):hover > a,
html.win .header__login-buttons .header__login-buttons__log-in {
    color: #E5E5E5;
}
.-color--emperor {
    color: #FFD500;
}
.body-container {
    background-color: #0F0F0F;
    background-blend-mode: multiply;
}
.search-popup__recent__list {
    background-color: #131313;
}
.dropdown__content,
.header__contents,
.header__bg,
.page-menu::before,
.channels-block.channels-block--list {
    background-position: 0px 0px;
    background-color: #0f0f0f;
    background-blend-mode: multiply;
}
footer.footer,
.main-menu__nav,
.coub__tags-tag,
.story__tags-tag,
.main-menu__fold {
    border-width: 1px;
    border-top-color: #151515;
    border-bottom-color: #151515;
    background-color: #151515;
    background-blend-mode: multiply;
}
.page-menu::before {
    background-position: -1px 0px;
}
.main-menu__nav {
    background-position: -2px 0px;
}
.list.list--selectable .list__item:not(.disabled).active,
.list.list--selectable .list__item:not(.disabled):hover,
.main-menu__nav-item.active,
.main-menu__nav-item:hover,
.main-menu__community-item:hover,
.main-menu__community-item.active {
    background-color: rgb(27, 27, 27)
}
.coub__like-button i,
.coub__recoub-button i,
.coub__remix-button i {
    filter: invert(1);
}
.switcher,
.coub__tags-tag.tags-community,
.story__tags-tag.tags-community {
    background-color: #282828;
}
.switcher:hover,
.coub__tags-tag.tags-community:hover,
.story__tags-tag.tags-community:hover {
    background-color: #282828;
}
.main-menu__fold {
    border-top-color: gray;
    border-bottom-color: gray;
    border-right-color: gray;
}
.sb.-toggle:after {
    border-top-color: #333;
}
.header__search .input-field {
    background-color: #151515;
}
.main-menu__hr {
    height: 1px;
    background-color: #151515;
    margin: 10px 0px;
}
.page-menu {
    display: flex;
    border-bottom: 1px solid #0F0F0F;
}
.channels-block.channels-block--list .list__item {
    border-bottom: 1px solid #0F0F0F;
}
.follow-button__container button {
    background-color: #262626;
    color: #b5b5b5;
}
.channels-block.channels-block--list .channels-block__view-more {
    color: #B5B5B5;
}
body {
    color: #b5b5b5;
    background-color: #0F0F0F;
}
.main-menu__fold {
    border-top-color: #151515;
    border-bottom-color: #151515;
    border-right-color: #151515;
}
.viewer.viewer--v2 .viewer__suggests {
    visibility: hidden;
}
.viewer.viewer--v2 .viewer__controls .viewer__sad.viewer__sad__animated {
    display: none;
}
body.megafontv-promo-embed .viewer.viewer--v2 .viewer__site-logo {
    background: url(https://coub.com/assets/svg/html5_player/logo-fe98aa308238b41f73772deebf5542128fbfa9deb4808ec343ac9f45f9108ade.svg) no-repeat;
}
body.megafontv-promo .header__logo .img.megafontv {
    display: none;
}
body.megafontv-promo .header__logo .img {
    display: block;
}
body.megafontv-promo-embed .viewer.viewer--v2 .viewer__controls-top:hover .viewer__site-logo {
    background: url(https://coub.com/assets/svg/html5_player/logo_hover-86ccabced0a378eabb8e53db55f4972686eb907b60336b25b4792b8820569ce8.svg) no-repeat;
}
.viewer__das.viewer__das__animated {
    display: none;
}

.antiscroll-scrollbar {
    background: #262626;
    -moz-box-shadow: 0 0 1px #0F0F0F;
    box-shadow: 0 0 0px #0F0F0F;
}

.coub__description .description__stamp__source {
    color: #B5B5B5;
}
a {
    color: #D2D2D2;
    text-decoration: none;
}
.main-menu__nav-item.active i svg path {
    fill: #FFD500;
}
.badge-counter {
    background: #FFD500;
}
.coub__tags-tag.tags-cotd-featured, .story__tags-tag.tags-cotd-featured {
    background-color: #282828;
    border-radius: 50%;
}
::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 10000px #0F0F0F; 

}

::-webkit-scrollbar-thumb {
    -webkit-border-radius: 0px;
    border-radius: 0px;
    background: #0F0F0F; 

}
::-webkit-scrollbar-thumb:window-inactive {
    background: #0F0F0F; 
}
.PRZWQ {
    order: 1;
    min-width: 310px;
    width: 310px;
    background: #0F0F0F;
}
[adfox-featured-banner] .bg {
    position: absolute;
    top: 0;
    left: -5000px;
    background-color: #0F0F0F;
    height: 100%;
    width: 10000px;
}
.box--banner {
    position: relative;
    border-radius: 6px;
    background-color: #0F0F0F;
    margin-bottom: 20px;
}
.main-menu__inner {
    background-color: #151515;
}

.switcher.-off {
    background-color: #313131;
}
}
