css:
@-moz-document domain(programme-tv.net) {
	


  div#videoPush {
      display: none
  }
  div#newsPush {
      display: none
  }
  div#sidebar {
      display: none
  }
  #header {
      display: none
  }
  #col_left {
      width: 100%
  }
  .channel .programme .readmore {
      display: none
  }
  .block.sondages {
      display: none
  }
  p.baseline,
  .footer-layout,
  .sf-extensionfactory-link {
      display: none
  }


  .sp-message-open {
      margin-top: 0 !important;
      overflow: auto !important;
      position: static !important;
  }

  .container-pave-haut-sidebar {
      display: none !important;
  }

  div[class^='sp_veil'],
  div[class*=' sp_veil'],
  div[class^='sp_message_id'],
  div[class*=' sp_message'],
  div[class^='sp_message_panel'],
  div[class*=' sp_message_panel'] {
      display: none !important;
  }

}
