css:
@namespace html url(http://www.w3.org/1999/xhtml);
@namespace xul url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

.flights {
    width: 100%;
    font-size: 13px;
    line-height: 0.5;
    background-color: #fff;
}


@media (min-width: 1281px){
.flights_page .flights td:first-child, .flights_page .flights th:first-child {
     padding-left: 50px;
}
}
@media (min-width: 1281px){
.flights_page .flights td:last-child, .flights_page .flights th:last-child {
     padding-right: 50px;
}
}

.flights-item--special.blue {
    background-color: #f7f8fd;
}

.flights .airline_logo img {
    max-width: 120px;
    max-height: 15px;
}

.header{
    display:none;
}

.canopy{
    display: none;
}

.footer{
    display:none;
}

.table th {
    text-transform: uppercase;
    font-weight: 700;
    font-size: 0.775rem;
}

.table td, .table th {
    padding: .5rem 0 .5rem 2.5rem;
    box-sizing: border-box;
    height: 15px;
    text-align: left;
    vertical-align: middle;
}

.table td {
    font-size: 1rem;
}
