css:
@-moz-document domain("air-quality.com") {
.forecast-box {
     transform:translateY(100%);/**下移元素**/
	-ms-transform:translateY(100%);
	-webkit-transform:translateY(110%);
}
.history-box {
    transform:translateY(-100%);/**上移元素**/
	-ms-transform:translateY(-100%);
	-webkit-transform:translateY(-100%);
}

body{
background-color: #acacac;
}
.navbar-default {
    background: rgba(252, 209, 209, .96);
}
.navbar-fixed-top {
    top: 0;
    border-width: 0 0 0px;
}
.detail-main {
    float: left;
    position: relative;
    width: 780px;
    margin-right: 15px;
    padding: 0px;
    border-radius: 8px;
    border: 0px #fff7f7 solid;
    background: #e8e8e8;
}

.detail-title h2 {
    font-size: 24px;
    color: #101010;
    font-weight: 500;
    text-align: center;
    margin: 0px 0 0;
}
.reading-box .pollutant-item .name {
    position: absolute;
    top: 0px;
    left: 0px;
    font-size: 12px;
    color: #000;
    font-weight: 400;
}
.detail-side-nearby {
    border-radius: 8px;
    border: 0px #ececec00 solid;
    background:  #e8e8e8;
    margin-bottom: 15px;
    position: relative;
}
a {
    color: #000;
    text-decoration: none;
}
}
