@-moz-document domain("inmogames.com") {
/* Medallas */

.barra-medallas {
    width: auto;
    height: 24px;
    border: 1px solid #dcdcdc;
    display: flex;
    justify-content: center;
    align-items: center;
    font-weight: 700;
    font-family: Arial;
    font-size: 12px;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    -os-border-radius: 5px;
    -ms-border-radius: 5px;
    background: #045FB4;
    margin: 10px;
    color: white;
}

/* Fondo */

.content-wrapper, .right-side {
    background: white;
    padding: 10px 0 20px;
}

/* Header */

.main-header .navbar {
    -webkit-transition: margin-left .3s ease-in-out;
    -o-transition: margin-left .3s ease-in-out;
    transition: margin-left .3s ease-in-out;
    margin-bottom: 0;
    margin-left: 0px;
    border: none;
    min-height: 50px;
    border-radius: 0;
    background-repeat: no-repeat;
    background-image: url(https://i.imgur.com/X9K8mc5.png);
    background-position: center;
    background-size: cover;
}

.main-header .navbar-custom-menu, .main-header .navbar-right {
    float: right;
    height: 64px!important;
    background: url(https://i.imgur.com/MBXJocf.png) no-repeat;
}

.navbar-custom-menu > a {
    color: #ffffff;
    padding: 7px 7px;
    font-size: 12px;
    text-transform: initial;
    letter-spacing: 1px;
}

.navbar-profile-avatar {
    position: relative;
    top: -2px;
    width: 50px;
    height: 50px;
    /* margin-right: auto; */
    border: none;
    /* border-top-right-radius: 3px; */
    /* border-top-left-radius: 3px; */
    /* border-bottom-right-radius: 3px; */
    /* border-bottom-left-radius: 3px; */
    border-radius: 50%;
    min-width: 40px;
    min-height: 40px;
    max-width: 40px;
    max-height: 40px;
    margin-top: 6px;
}

/* Men√∫ */

.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    font-size: 14px;
    text-align: left;
    list-style: none;
    background-color: #ffffff;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
    border: 1px solid #ccc;
    border: 1px solid rgba(0,0,0,.15);
    border-radius: 4px;
    -webkit-box-shadow: 0 6px 12px rgba(0,0,0,.175);
    box-shadow: 0 6px 12px rgba(0,0,0,.175);
}

.user-panel > .info > h3 {
    font-weight: 600;
    margin-bottom: 15px;
    color: #fff;
    font-size: 17px;
}

.pull-left.info {
    margin-top: -10px!important;
}

.sidebar-menu>li>a, .sidebar-menu>li>a {
    color: #fff;
    font-size: 12px;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.main-header .logo {
    color: #fff;
    /* background-color: #222d32; */
    border-bottom: 0 solid transparent;
    background: none;
    margin-top: 20px;
    font-size: 0;
    margin-left: 0px;
}

.main-sidebar, .left-side {
    background: linear-gradient(200deg,#1aaaed,#0075ab);
    font-family: Helvetica;
    font-weight: bold;
}

.button_vip {
    background: linear-gradient(100deg,#eab00b,#fbca40);
}

.sidebar-menu>li:hover>a, .sidebar-menu>li.active>a {
    color: #fff !important;
    background: #2fb2ef;
    border-left-color: #ffffff;
}

/* test */

.pull-left.info .username {
    margin-top: -2px;
    color: #f1f1f1;
}

.user-panel {
    height: 90px!important;
    background: url(https://i.imgur.com/TPtsniL.png) no-repeat center;
}

.sidebar {
    padding-bottom: 10px;
    margin-top: 36px;
    border-top: 0.1rem solid white;
}

.pull-left.image img {
    min-width: 45px;
    min-height: 45px;
    max-width: 45px;
    max-height: 45px;
    margin-top: 22px;
}

/* Main Block */

.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th {
    color: #bfc3d0;
    text-transform: uppercase;
    letter-spacing: 2px;
    font-size: 1em;
    font-weight: 600;
    background: url(https://i.imgur.com/VgHSQVs.png) no-repeat -10px -155px;
}

h4.catbg, h4.catbg2, h3.catbg, h3.catbg2, .table_list tbody.header td.catbg {
    background: url(https://i.imgur.com/VgHSQVs.png) no-repeat 100% -160px;
    padding-right: 9px;
}

h3.catbg, h3.catbg2, h3.titlebg, h4.titlebg, h4.catbg {
    overflow: hidden;
    height: 31px;
    line-height: 32px;
    font-size: 14px;
    font-weight: normal;
}

div.cat_bar {
    padding-left: 15px;
    height: 40px;
    overflow: hidden;
    margin-bottom: 1px;
    background: url(https://i.imgur.com/VgHSQVs.png) no-repeat 0 -160px;
}

.table_list tbody.content td.info a.subject {
    font-weight: bold;
    font-size: 110%;
    color: Black;
}

table.table_list p {
    padding: 0;
    margin: 0;
}

body, td, th, tr {
    color: black;
}

/* Tablas */

table.table_list td, table.table_list th {
    padding: 5px;
}

.windowbg2 {
    background-color: rgb(240, 244, 247);
}

tr.windowbg td, tr.windowbg2 td, tr.approvebg td, tr.highlight2 td {
    padding: 0.3em 0.7em;
    -webkit-border-radius: 7px;
    -moz-border-radius: 7px;
    -border-radius: 7px;
}

.table_list td.icon, .table_list td.info, .table_list td.stats {
    border-right: 0px solid #ededed;
}

table.table_list td, table.table_list th {
}

.table_list td {
    border-bottom: 0px solid #ededed;
}

table {
display: table;
    border-collapse: separate;
    box-sizing: border-box;
    border-spacing: 2px;
    border-color: grey;
}

.windowbg {
    background-color: rgb(231, 234, 239);
}

table.table_list tbody.content td.lastpost {
    line-height: 1.3em;
    font-size: 90%;
    width: 24%;
    background: #f8ecc4;
    -webkit-border-radius: 7px;
    -moz-border-radius: 7px;
    -border-radius: 7px;
}

table.table_list tbody.content td.stats {
    font-size: 90%;
    width: 15%;
    text-align: center;
    width: 15% !important;
    -webkit-border-radius: 7px;
    -moz-border-radius: 7px;
    -border-radius: 0px;
}

.table_list td.icon, .table_list td.info, .table_list td.stats {
    border-right: 0px solid #ededed;
    -webkit-border-radius: 7px;
    -moz-border-radius: 7px;
    -border-radius: 7px;
}

/* Footer */

.main-footer {
    text-align: center;
    background: url(https://i.imgur.com/QLMWs0E.png) 0 0 repeat-x;
    height: 74px;
    border-top: 0px solid #d2d6de;
}

#footerarea a:link, #footerarea a:visited {
    color: rgb(204, 204, 204);
}

#footerarea {
    color: rgb(255, 255, 255);
    padding: 25px 0px 0px;
    font-size: 12px;
}

.main-footer ul li.copyright {
    display: block;
    padding: 13px 0px 0px 20px;
}

a.new_win:link, a.new_win:visited {
    color: rgb(204, 204, 204);
}

span.upperframe {
    padding: 0px 0px 0px 20px;
    display: block;
    background: url(https://i.imgur.com/VgHSQVs.png) 0px -90px no-repeat;
}

span.lowerframe {
    padding: 0px 0px 0px 20px;
    display: block;
    background: url(https://i.imgur.com/VgHSQVs.png) 0px -102px no-repeat;
}

.roundframe {
    padding: 0px 10px;
    background: rgb(246, 244, 237);
    border-left: 1px solid rgb(197, 197, 197);
    border-right: 1px solid rgb(197, 197, 197);
}

span.upperframe span {
    padding: 0px;
    height: 12px;
    display: block;
    background: url(https://i.imgur.com/VgHSQVs.png) 100% -90px no-repeat;
}

span.lowerframe span {
    padding: 0px;
    height: 12px;
    display: block;
    background: url(https://i.imgur.com/VgHSQVs.png) 100% -102px no-repeat;
}

#upshrinkHeaderIC h4.titlebg {
    background: url(https://i.imgur.com/VgHSQVs.png) 100% -157px no-repeat;
}

div.title_barIC h4.titlebg {
    color: black;
    font-size: 1em;
    font-weight: bold; 
}

h3.catbg, h3.catbg2, h3.titlebg, h4.titlebg, h4.catbg, .table_list tbody.header td {
    overflow: hidden;
    height: 31px;
    line-height: 32px;
    font-size: 14px;
    font-weight: normal;
}

.titlebg, .titlebg2, thead tr.titlebg th, tr.titlebg td, tr.titlebg2 td {
    color: rgb(255, 255, 255);
    font-size: 11px;
    font-weight: normal;
    background: url(https://i.imgur.com/VgHSQVs.png) -10px -120px no-repeat;
}

h4.titlebg span.left, h3.titlebg span.left {
    float: left;
    display: block;
    width: 9px;
    height: 31px;
    background: url(https://i.imgur.com/VgHSQVs.png) 0px -200px no-repeat;
}

#upshrinkHeaderIC h4.titlebg span.left {
    background: url(https://i.imgur.com/VgHSQVs.png) 0px -120px no-repeat;
}

/* Otros */

.content-header {
    display: none;
}

.content {
    padding: .5em 1.2em;
    margin: 0;
    border: none;
    background: white;
}

/* Citas */

blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
    background: url(https://www.inmogames.com/images/theme/quote.png) .1em .1em no-repeat;
    font-size: 11px;
    color: #5c5c5c;
    line-height: 1.4em;
    border: 1px solid #dddcd5;
    padding: 1.1em 1.4em;
    margin: 0.1em 0 0.3em 0;
    overflow: auto;
}

blockquote.bbc_standard_quote {
    background-color: #fff;
}
}

