css:
@-moz-document domain("booky.io") {
body, #loading {
    background-color: #222;
}

.portlet-content li a{
    color: #aaa;
}

#messages, .dashboards li:not(:hover):not(.add-dashboard), #dashboard-title {
    background-color: #444 !important;
    color: #fff !important
}

#dashboards:hover #dashboard-title {color: #444 !important}

input[type="url"], input[type="text"], input[type="password"], input[type="email"], #categories, #bookmarks {
	background-color: #272727;
    color: #aaa;
    border-color: #666 !important;
}

input[type="url"]:hover, input[type="text"]:hover, input[type="url"]:focus, input[type="text"]:focus, 
input[type="password"]:focus, input[type="password"]:hover, input[type="email"]:hover, input[type="email"]:focus {
    background-color: #222 !important;
    border-color: #888 !important;
}

input[type="file"] {color: #aaa !important}

input[type="file"]:hover {
    background-color: #111 !important;
    color: #fff !important}

.button-style-small.animated:hover, #login-button:hover, #customize:hover, #logout:hover {
    background-color: #222 !important}

.button-style-small.animated:hover, #login-button:hover {border-color: #3e8ca6 !important}
#customize:hover, #logout:hover {border-color: #888 !important}

#header .bottom {background-color: rgba(0,0,0,0.2) !important}

.dashboards {
 	background-color: #333;   
}

.portlet h2 {
    color: #aaf
}

.nav-bg {
    background-color: #111;
}

#new-category, #dashboard-title {
    color: white;
}

#dashboard-title:hover {
    color: #222;
}

#dialog, #sidebar, .colors {
    background-color: #333 !important;
    border-color: transparent !important;
}

#help-box .icon, .bookmark-up.button-style, .bookmark-down.button-style, .edit-bookmark.button-style, .delete-bookmark.button-style,
.category-up.button-style, .category-right.button-style, .category-down.button-style, .edit-category.button-style, 
.delete-category.button-style, .category-left.button-style, .note.button-style, #close.button-style,
.edit-dashboard.button-style, .move-dashboard.button-style, .move-dashboard-up.button-style, .move-dashboard-down.button-style,
.delete-dashboard.button-style {filter: invert(80%) hue-rotate(180deg) !important}

#close.button-style {border-color: transparent !important}

.book, .bookmark-circle {filter: invert(86.5%) hue-rotate(190deg)}

#dialog #cancel {border-color: #666 !important; color: #ccc !important}

.group li.link.over {border-color: #fff !important}

h4, h2:not(.portlet-header), h1, .features a:not(:hover), #about-box a:not(:hover), #about-box span:not(:hover),
#help-box a:not(:hover), p > a:not(:hover), b {color: #fff !important}

p, h3, #about-box div, label, #bookmark-import {color: #ddd !important}

#sidebar label, .features li, #top-nav a:hover, #dialog span {color: #ccc !important}
}
