css:
@-moz-document domain("booky.io") {
body, #loading, #main, .section--light .testimonials__testimonial {
    background-color: #222;
}

.section--light, .about__member, .footer__stats, .toolbar, .modal__inner  {
    background-color: #444;
}

.skeleton {
    background-color: #202020 !important;
    background-image: linear-gradient(to left, #333, #222)}

.modal__inner {
    border: 2px solid #333 !important}

.tab-bar--end::after, .dashboards-tabs--darkMode.tab-bar--end::after {
    background: linear-gradient(270deg,#444,transparent);
}

.error {
    background-color: #333 !important;
    border-color: #666 !important}


/* Header elements */
.header .button {
    background-color: #333 !important;
    border-color: transparent !important;
    color: #fff;}

.header .button svg, .menu__item svg, .icon--light svg, .toolbar button.icon .icon__svg:not(:hover) {
    fill: #fff !important;
    transition: all 200ms}

.header .button:hover {
    background-color: #444 !important;
    color: #eb7260}

.header .button:hover svg, .menu__item:hover svg, .icon--light:hover svg {
    fill: #eb7260 !important}

.menu__item:hover, p .link {
    color: #eb7260}

.button-style-small.animated:hover, #login-button:hover {border-color: #3e8ca6 !important}
#customize:hover, #logout:hover {border-color: #888 !important}

/* Darker header colors */
.header--color0 {
    background-color: #93392a
}

.header--color1 {
    background-color: #13659a
}

.header--color2 {
    background-color: #405324
}

.header--color3 {
    background-color: #905812
}

.header--color4 {
    background-color: #9245a0
}

.header--color5 {
    background-color: #45194d
}

.header--color6 {
    background-color: #296163
}

.header--color7 {
    background-color: #2b3238
}

.header--color8 {
    background-color: #272727
}

/* Inputs */
input[type="url"], input[type="text"], input[type="password"], input[type="email"] {
	background-color: #272727;
    color: #aaa;
    border-color: #666 !important;
}

input[type="url"]:hover, input[type="text"]:hover, input[type="url"]:focus, input[type="text"]:focus, 
input[type="password"]:focus, input[type="password"]:hover, input[type="email"]:hover, input[type="email"]:focus {
    background-color: #222 !important;
    border-color: #888 !important;
}

input[type="file"] {color: #aaa !important}

input[type="file"]:hover {
    background-color: #111 !important;
    color: #fff !important}

/* Text */
h1, h2, h3, .headline, .tab-bar__tab:not(.tab-bar__tab--active):hover {color: #fff !important}

label, p, summary, .tab-bar__tab:not(.tab-bar__tab--active):not(:hover) {color: #bbb !important}

.bookmark__link:not(:hover) {
    color: #ccc !important}

.tab-bar__tab--active:hover, .tab-bar__tab--active:focus {
    color: #eb7260;
    border-bottom-color: #eb7260;
}

/* Buttons */ 
.button--large-blue {
    background-color: #1b4f9f;
    border-bottom: 1px solid #2b7cbf;
}

.button--large-blue:hover {
    background-color: #2b5ca8;
}

.button--large-light {
    background-color: #333;
    color: #ccc;
    border-bottom: 1px solid #666;
}

.button--large-light:hover {
    background-color: #555;
    color: #fff;
    border-bottom: 1px solid #666;
}

.donate__content--light {
    background-color: #444;
    color: #fff}

.donate__submit:hover ~ .donate__content--light {
    background-color: #111 !important;
    color: #fff !important}

.button--small-light {
    background-color: #333;
    border-color: #555;
}

.button--small-light:hover {
    background-color: #222;
}
}
