css:
@-moz-document domain("web.telegram.org"), domain("web.tlgrm.eu") {
  html body, div .modal-content {
    color: #bbb;
    background: url("https://raw.githubusercontent.com/CaelanBorowiec/Web-Telegram-Responsive-Dark-Purple/master/img/what-the-hex-dark.png")
  }

  a.tg_checkbox {
    color: #bbb !important;
  }

  div.im_page_wrap {
    /*background color of contentwrapper under header*/
    background-color: #303030;
    border-left: 1px solid #4d4d4d;
    border-right: 1px solid #4d4d4d;
    border-bottom: 1px solid #4d4d4d;
    box-shadow: none;
    max-width: 90%;
    position: relative;
  }

  div.im_page_wrap::after {
    content: "";
    background: url(https://shop.mikael-b.com/wp-content/uploads/2017/02/mikaelb-worldmap-art-iceworld-purple.png);
    opacity: 0.5;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    position: absolute;
    display: block;
  }

  div.im_page_wrap>div {
    z-index: 1;
    position: relative;
  }

  .im_attach, .im_media_attach, .im_record, .im_record_bg, .im_record:hover, .im_message_document_thumbed, .composer_emoji_panel a.composer_emoji_btn {
    background: inherit !important;
  }

  .im_dialog_peer {
    /*peer nickname unselected*/
    color: #bbb !important;
  }

  .contacts_modal_members_list a.contacts_modal_contact {
    /*nicknames in contactlist via hamburger menu*/
    color: #bbb;
  }

  div.im_dialogs_scrollable_wrap .active a.im_dialog {
    background: linear-gradient(45deg, #90318c, transparent);
    border: 1px solid purple;
  }

  div.tg_head_split, div.md_modal_head {
    /*page header block*/
    background-color: purple;
  }

  .dropdown.open .tg_head_btn, .tg_head_btn:hover {
    background: #4d4d4d !important;
  }

  .form-control {
    /*input field*/
    background-color: #0e0e0e !important;
    color: #bbb !important;
  }

  .composer_rich_textarea {
    background: #303030 !important;
    border-color: #bbb !important;
  }

  .im_dialogs_col_wrap {
    /*contactlist right border*/
    border-right: 2px solid #4d4d4d;
    display: block;
    position: relative;
  }

  .im_dialogs_col_wrap::after {
    content: "";
    background: purple;
    opacity: 0.2;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    position: absolute;
    z-index: -1;
  }

  .im_dialogs_col .nano>.nano-pane>.nano-slider {
    /*scrollbarbutton when hover*/
    background-color: #4d4d4d !important;
  }

  code, pre {
    background-color: #bbb !important;
    color: #0e0e0e;
    border: none !important;
  }

  div.tg_head_split {
    max-width: 90%;
  }

  li.im_dialog_wrap {
    background: linear-gradient(45deg, black, transparent);
  }

  .im_submit {
    color: #4d4d4d;
  }

  div.im_send_panel_wrap {
    background: linear-gradient(0deg, #5f004f, #0000001c);
    border-radius: 15px 15px 0px 0px;
    padding: 15px 49px 30px;
    max-width: 650px;
    border: 1px solid purple;
    border-bottom: none;
  }

  .im_message_mymention {
    background: #1d1d1d;
  }

  .im_message_focus .im_message_outer_wrap {
    background-color: #4d4d4d;
  }

  .im_message_selected .im_message_outer_wrap {
    background: #4d4d4d;
  }

  div.im_grouped_short {
    margin-top: -15px;
  }

  .im_grouped_short .im_message_body {
    margin: 0;
  }

  .im_history_message_wrap {
    margin: 0 auto;
    max-width: 50%;
  }

  div.im_content_message_wrap {
    padding: 15px;
    border: 1px solid purple;
    background: rgba(0, 0, 0, .5);
    border-radius: 15px;
  }

  div.im_message_wrap {
    max-width: 75%;
    padding: 0 15px 0 15px;
    display: inline-block;
  }

  a:hover {
    background-color: #282828 !important;
  }

  .im_message_unread_split {
    background: linear-gradient(90deg, transparent, black, transparent);
    border-top: 1px solid purple;
    border-bottom: 1px solid purple;
  }

  @keyframes im_message_focus_fade {
    0% {
      /*soft transaction*/
      background-color: #2d2d2d;
    }
  }

  .btn-primary {
    background-color: #4d4d4d;
  }

  .im_history_select_active .im_message_outer_wrap:hover {
    background: #4d4d4d;
  }

  .im_dialog_chat_from_wrap, .im_dialog_message_service {
    color: #6ac;
  }

  .composer_rich_textarea, .composer_textarea {
    box-shadow: 0px 3px 1px #ddd;
  }

  .composer_rich_textarea:focus {
    box-shadow: 0px 3px 1px #ddd !important;
  }

  .tg_head_logo_dropdown .dropdown-menu>li>a {
    color: #ddd;
  }

  .btn-primary.disabled, .btn-primary[disabled] {
    background: #4d4d4d none repeat scroll 0% 0%;
  }

  /*emoji*/
  .composer_emoji_tooltip, .icon-tooltip-tail, .dropdown-menu {
    background-color: #4d4d4d !important;
    border-color: #5d5d5d;
  }

  div .im_dialog_badge {
    background-color: purple;
  }

  div .im_dialog_badge_muted {
    background: #555;
  }

  .im_message_selected .im_message_outer_wrap, .im_history_select_active .im_message_outer_wrap:hover {
    background: linear-gradient(90deg, transparent, #800080e6, transparent);
  }

  .reply_markup_button {
    color: #FFFFFF !important;
    background-color: #666666 !important;
  }

  .reply_markup_button:focus, .reply_markup_button:hover {
    color: #FFFFFF !important;
    background-color: #4d4d4d !important;
  }

  .tg_head_logo_dropdown .dropdown-menu>li>a, .dropdown-menu>li>a, .dropdown-menu>li>a, .dropdown-menu>li>a:focus, .dropdown-menu>li>a:hover {
    color: #FFFFFF !important;
  }

  @media (max-width: 1000px) {
    .im_message_wrap {
      position: relative;
      max-width: 100% !important;
    }

    .im_history_message_wrap {
      max-width: 100%;
    }
  }
}
