css:
@-moz-document url-prefix("https://www.last.fm/user/kaorumorite") {

.chartlist--with-image .chartlist-row,.chartlist--with-index .chartlist-row,.chartlist--with-index.chartlist--with-image .chartlist-row,.chartlist--with-more .chartlist-row,.chartlist--with-more.chartlist--with-mobile-play .chartlist-row,.chartlist--with-type.chartlist--with-image .chartlist-row,.chartlist-row{
	padding:1px 8px 1px 0;
    height: 30px !important;
}
.chartlist-download-button, .chartlist-image, .chartlist-index, .chartlist-more-button, .chartlist-type, .chartlist .chartlist-play .chartlist-play-button {
    margin-top: -30px;
}
.chartlist-play,#top-tracks .chartlist-play{
	display:none!important
}
.chartlist-name{
	font-weight:normal;
}
.avatar-shape, .avatar::after, .avatar img {
    border-radius: 0;
}
/*ZEBRA*/
.chartlist tr td.chartlist-loved,#top-tracks .chartlist tr td.chartlist-loved{
	order:-1
}
.chartlist-row:nth-of-type(odd),#top-tracks .chartlist-row:nth-of-type(odd){
	background-color:#f9f9f9
}
.chartlist-row:nth-of-type(odd):hover,#top-tracks .chartlist-row:nth-of-type(odd):hover{
	background-color:#f9f9f9
}
.chartlist-row--highlight,.chartlist-row--highlight:hover,.chartlist-row--now-scrobbling,.chartlist-row--now-scrobbling:hover{
	background-color:#fff
}
/*BARS*/
.chartlist-count-bar-slug {
    position: absolute;
    left: 0;
    top: 4px;
    height: 25px;
    min-width: 1px;
    background-color: #d4d9d9;
    border-radius: 2px;
}
.chartlist-count-bar-value {
    color: #fff;
    font-weight: bold;
}
/*TOP ALBUMS*/
#top-albums .chartlist-artist{
	left:70px
}

#top-albums .chartlist-artist {
    display:none;
}
#top-albums .chartlist-name {
    left: 79px;
    float: right;
    margin: auto;
    
}
/*TOP TRACKS*/
#top-tracks .chartlist-artist{
	left:79px
}

#top-tracks .chartlist-index{
	display:none
}
#top-tracks .stat-name{
	display:inline!important
}
#top-tracks .chartlist-count-bar{
	text-align:right
}
#top-tracks .chartlist-count-bar-slug{
	display:none!important
}
#top-tracks .chartlist-count-bar-value {
    color: #d4d9d9;
    font-weight: bold;
}
#top-tracks .chartlist-artist {
    text-align: right;
    width: 400px;
    margin: auto;
}

#top-tracks .chartlist-name {
    left: 79px;
    float: right;
    margin: auto;
}


.chartlist .lazy-buylinks-toggle::before {
    margin-top: -9px;
}
.chartlist-more-button::before {
    margin-top: 15px;
}
.chartlist-timestamp {
    width: 130px;
}

.chartlist-count-bar {
    width: 120px;
    line-height: 32px;
}
.chartlist-count-bar-value {
    overflow: visible;
    text-overflow: ellipsis;
    white-space: nowrap;
    display: block;
    position: relative;
}

.chartlist-count-bar-value {
    color: 
    #ffffff;
    font-weight: bold;
}
.chartlist-count-bar-slug {
    min-width: 36px !important;
}
}
