css:
@-moz-document domain("justdancenow.com") {
/*just-dance-now*/
.preview--bg::before
  {
    position: absolute;
    top: -10.41rem;
    right: 10.7rem;
    display: none
}
.song-cover--low-res {
width : 1000%;
height : 500%;
opacity: 0.4;
top : -390%;
left : -65%;
animation: colom 0.5s infinite, colom2 0.5s infinite;

}

.song-cove {
 -webkit-transform: rotate(-4deg);
    -ms-transform: rotate(-4deg);
    transform: rotate(-4deg);
}
   
@keyframes colom {
    100% {
        -webkit-filter: blur(10px);
        filter: blur(10px);

    }
    0% {
        -webkit-filter: brightness(130%);
        filter: brightness(130%);
    }
}
@keyframes colom2 {
    100% {
        -webkit-filter: blur(19px)  brightness(150%);
        filter: blur(19px)  brightness(150%);
    }
    0% {
-webkit-filter: blur(20px)  brightness(100%);
        filter: blur(20px)  brightness(100%);    
    }
}
.danceroom{
    text-shadow: none;
    text-align: center;
    display: visible;
}
/*---------------------------------- BEAT -------------------------------*/
#beat
{
  width: 42.2%;
  height: 92% !important;
  background-image: url(https://i.imgur.com/sxMSqd4.png) !important;
  background-repeat: no-repeat !important;
  background-size: 100% 100% !important;
  background-position: bottom !important;
}

.multi-coach #beat
{
  width: 30.4%;
}

#beat .particle-system
{
  display: none;
}

html:not(.ipad) .hud.beat>#pictos>#beat
{
  -webkit-animation-name: pictoOut !important;
  animation-name: pictoOut !important;
}
 
@keyframes pictoOut
{
  0%
  {
    background-size: 100% 100%;
    filter: brightness(250%);
    opacity: 1;
  }

  25%
  {
    filter: brightness(100%);
  }
  
  75%
   {
       background-size: 100% 50%;
    opacity: 1;
    filter: brightness(100%);
  }

  100%
  {
    background-size: 100% 0%;
    opacity: 0;
    filter: brightness(100%);
  }
}
#pictos {
    width: 29.6%;
    z-index: 9999
}
#pictos.multi-coach {
    width: 41.2%!important;
    right: 0%!important;
}

.picto {
    filter: drop-shadow(4px -2px 2px gray);
    left: -1.5%
}
.multi-coach .picto {
    left: -9%
}
/*lyric*/
 #lyrics::after {
    content: '';
    position: absolute;
    height: 21%;
    width: 80.3%;
    left: -16%;
    opacity: 0.6;
    bottom: 86%;
    background: url(https://dl.dropbox.com/s/q1c7iqinulvdaqu/ShadowLyric.png?dl=0) center/ cover no-repeat;
}
#lyrics {
    content: '';
    position: absolute;
    opacity: 0.7;
    height: 100%;
    width: 0.3%;
    left: 8%;
    bottom: -1%;
    background-color: white;
    transform: skew(11deg);
}
#lyrics .line.current,
#lyrics .line.previous {
    left: 0% !important;
}
#lyrics .line.previous {
    left: 1% !important;
}
#lyrics .line {
    left: 2%;
}
.state-interstitial #lyrics::before,
.state-loadingdance #lyrics::before,
.state-tutorial #lyrics::before {
    display: none
}
/*afterdance*/
#afterdance[data-stars="5"][data-score^="13"] .stars .star, .score__stars.star {
    background: url(https://dl.dropbox.com/s/4npwo3s11yi21k9/megaStar.png?dl=0) !important;
    background-size: 100% 100%!important;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center
}
#afterdance[data-stars="5"][data-score^="12"] .stars .star, .score__stars.star {
    background: url(https://dl.dropbox.com/s/4npwo3s11yi21k9/megaStar.png?dl=0) !important;
    background-size: 100% 100%!important;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center
}
#afterdance[data-stars="5"][data-score^="11"] .stars .star, .score__stars.star {
    background: url(https://dl.dropboxusercontent.com/s/a1g8qfvg96my1u5/SuperSTAR%205%20STARS.png?dl=0) !important;
    background-size: 100% 100%!important;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center
}
}
