/* This style was made by LeSirH, Discord: LeSirH#3301 */
@-moz-document domain("nitrotype.com") {
::selection {
    color: white;
    background-color: #fff;
}
::-webkit-scrollbar {
    display: none;
}
::focus {
    outline: none;
}
.hp-heroBG, .hp-heroBG::after {
    -webkit-filter: blur(2px);
    border-radius: 10px;
}
.hp-heroBG::after {
    background: #070506;
}
.hp-heroBG {
    background: url("https://cdna.artstation.com/p/assets/images/images/011/593/738/large/ruben-baars-test4-111.jpg?1530373809");
    background-size: 130%;
}
div.flash.flash--beta {
    display: none;
}
.structure.structure--flashed.structure--noAds {
    padding-top: 50px;
}
body {
    background: url("https://i.pinimg.com/originals/a1/9a/b7/a19ab7e71095bb14bd13c6c0fc4c5e55.jpg");
    background-repeat: no-repeat;
    background-attachment: fixed;
}
.news-article.card.card--e.card--o.card--grit.card--sq.card--shadow-l.card--f {
    background: url("https://i.pinimg.com/originals/a1/9a/b7/a19ab7e71095bb14bd13c6c0fc4c5e55.jpg");
    border: 2px solid #353535;
    border-radius: 10px;
}
h1, h2, h3, p {
    text-shadow: #0f0f0e 1px 0 10px;
}
.footer.cell.cell--s, .footer-copyright {
    background: none;
}
.card-cap.bg--gradient {
    color: white;
    background: #131313;
}
.btn {
    background-color: #1b1e28;
    border: 0px;
}
.btn.btn--a.btn--float.btn--fw, .btn.btn--a.btn--float.btn--icon.animate--iconSlam.header-cta, .btn.btn--a.btn--s, .btn.btn--a.btn--float {
    background: #1b3893;
}
.garage {
    background: url("https://www.transparenttextures.com/patterns/asfalt-light.png");
    background-color: #131313;
    opacity: 1;
    box-shadow: inset 0 20px 300px -20px rgba(0,0,0,1);
}
.card-cap.bg--gradient {
    background: url("https://www.transparenttextures.com/patterns/asfalt-light.png");
    background-color: #000;
}
.garage-vehicle:hover {
    background-color: #1b1e28;
}
.profile-car {
    background-color: rgb(22, 24, 28, 0.6);
    color: white;
}
.theme--pDefault .profile-bgHead {
    background: url("https://i.pinimg.com/originals/a1/9a/b7/a19ab7e71095bb14bd13c6c0fc4c5e55.jpg");
    color: white;
}
.theme--pDefault .profile-bg {
    background: url("https://i.pinimg.com/originals/a1/9a/b7/a19ab7e71095bb14bd13c6c0fc4c5e55.jpg");
    color: white;
}
.profile-bodyInterior {
    color: white;
}
.hp-teacherHero img {
    background: url("https://i.pinimg.com/originals/a1/9a/b7/a19ab7e71095bb14bd13c6c0fc4c5e55.jpg");
}
.card.card--b.card--shadow.card--grit.card--f.card--o.card--roundBtm.well.well--b.well--l {
     background: none !important;
    
}
.raceResults.card.card--b.card--grit.card--o.card--f {
    background: url("https://i.pinimg.com/originals/a1/9a/b7/a19ab7e71095bb14bd13c6c0fc4c5e55.jpg");
}
.card.card--d.card--grit.card--o.card--roundRt.mbf.dhf.pbs.raceResults-earnings.raceResults-earnings--2nitro {
    background: url("https://i.pinimg.com/originals/a1/9a/b7/a19ab7e71095bb14bd13c6c0fc4c5e55.jpg");
}
.card.card--d.card--grit.card--o.card--roundRt.mbf.dhf.pbs {
    background: url("https://i.pinimg.com/originals/a1/9a/b7/a19ab7e71095bb14bd13c6c0fc4c5e55.jpg");
}
.btn.btn--a.btn--striped.btn--fw.btn--icon.animate--iconSlam {
    background: #1b3893;
}
.db.cell {
    border-radius: 30px;
    box-shadow: 0px 20px 20px rgb(0, 0, 0, 0.8);
}
.race-toggle.race-toggle--line {
    display: block;
}
.table-row.link.is-user {
    border: 1px solid yellow !important;
}
.list.list--f.dropdown-items, .dropdown-link.animate--iconSlam {
    background: #242426;
    border: #242426;
}
.list-item.dropdown-item:hover, .list-item.dropdown.dropdown--nav:hover {
    background: #242426;
}
.icon-x {
    display: none;
}
.card.card--b.card--shadow.card--f.card--grit.well.well--b.well--l {
    background: url("https://www.transparenttextures.com/patterns/asfalt-light.png");
    background-color: #131313;
    opacity: 0.9;
    box-shadow: inset 0 20px 300px -20px rgba(0,0,0,1);
}
path:nth-child(7), path.logo-slash {
    fill: #345cd7 !important;
}
.card.card--e.card--sq.card--f.card--shadow-l, .card.card--e.card--sq.card--shadow-l, .gridTable-row.gridTable-row--body {
    background: rgb(0, 0, 0, 0);
    border: 0px;
}
.card.card--e.card--sq.card--f.card--shadow-l {
    border-radius: 30px;
}
.btn.btn--f.btn--fw.btn--float.btn--bare.btn--tab.btn--tabB.btn--s.is-active, .btn.btn--f.btn--fw.btn--float.btn--bare.btn--tab.btn--tabB.btn--s {
    background: none;
}
.achievement {
    background: none;
}
.card.card--d.card--fade.card--sq.card--f {
    background: linear-gradient(rgb(0, 0, 0, 0), rgb(0, 0, 0, 0.8));
    border-radius: 30px;
}
.season.season--default, .bucket-content {
    background: none;
}
.gridTable-row.gridTable-row--fw.gridTable-row--even {
    background: none;
    border: 0px;
}
.input-select.season-select {
    background: rgb(0, 0, 0, 0.5);
}
.btn.btn--stroke-i.btn--xs.btn--float.is-active, .btn.btn--stroke-i.btn--xs.btn--float {
    background: none;
    color: white;
}
button.tab {
    background: none !important;
}
.comment, .comment.comment--moderator, .comment.comment--admin {
    background: none;
    border: 0px;
}
.btn.btn--a.btn--fw.btn--float.mbs:hover {
    background-color: #1c2973;
}
.news-hero {
    border-radius: 10px;
}
.card.card--e.card--sq.card--shadow-l.card--bc-brick.dhf.stat-box.stat-box--speed, .card.card--e.card--sq.card--shadow-l.card--bc-dodger.dhf.stat-box.stat-box--races, .card.card--e.card--sq.card--shadow-l.card--bc-emerald.dhf.stat-box.stat-box--money {
    border-radius: 75px;
}
path.rv-xy-plot__series.rv-xy-plot__series--line.chart-area {
    background: red !important;
}
.gridTable-row.gridTable-row--body.gridTable-row--even:hover, .gridTable-row.gridTable-row--body.gridTable-row--odd:hover {
    background: none;
}
div.modal-container {
    background: url("https://i.pinimg.com/originals/a1/9a/b7/a19ab7e71095bb14bd13c6c0fc4c5e55.jpg") !important;
}
.rv-xy-plot__grid-lines {
    color: red;
    background: red;
    background-color: red;
    fill: red;
}
#chart-gradient {
    color: red !important;
}


.chart--a .chart-gradient .chart-gradient1 {
  stop-color: #f00;
  stop-opacity: 0.6; 
}
.chart--a .chart-gradient .chart-gradient2 {
  stop-color: #486dd7;
  stop-opacity: 0.3; 
}
.chart--a .chart-gradient .chart-gradient3 {
  stop-color: #000;
  stop-opacity: 0.5;
}
.chart--a .chart-line {
  stroke: rgb(0, 0, 0, 0) !important;
  stroke-width: 2 !important; 
}
.chart--a .chart-line circle {
    fill: #000 !important;
    stroke-width: 0 !important;
    stroke: #fff !important; 
    display: none;
}

}
