css:
@-moz-document regexp("(http[s]?:\\/\\/)*www.forocoches.*") {
/* FOROCOCHES MD v2.56 */

/* Inicio Parámetros Tema Nocturno */

    :root {
        /* Colores */
        --bg01: #121212; /* Fondo principal */
        --tx01: #d1d1d1; /* Texto principal */
        --bg02: #252525; /* Bordes */
        --bg05: transparent; /* No tocar */
        --bg04: #232323; /* Fondo oscuro */
        --bg03: #1c1c1c; /* Fondo claro */
        --bg07: #404040; /* Barra editor */
        --bg08: #737373; /* Barra */
        --tx07: #e57650; /* enlaces principales */
        --tx08: #e5987e; /* enlaces principales */
        --tx04: #e57650; /* enlaces secundarios */
        --tx05: #e5987e; /* enlaces secundarios */
        --rs01: #6bbeee; /* Resalto usuario */
        --rs1h: #81cefb; /* Resalto usuario :hover */
        --rs02: #ff7d79; /* Resalto autor */
        --rs2h: #e5987e; /* Resalto autor :hover */
        --c200: #232323; /* Cabecera fondo */
        --c400: #ffffff; /* Notificación fondo */
        --c40t: #777777; /* Notificación texto */
        --c401: #e57650; /* Resalto */
        --co04: #ffffff; /* Cabecera texto :hover */
        --co03: #ffffff; /* No modificar */
        --co01: #2e2e2e; /* Blanco / negro */
        --co02: #e8e8e8; /* Negro / blanco */
        --rgb7: #e8e8e8;
        --rgb7b: #e8e8e8; /* Cabecera texto */
        --rgb5: #b0b0b0; /* Texto secundario, varios */
        --rgb2: #404040;
        --rgb2i: rgba(0, 0, 0, .2); /* Paginación y flechas */
        --rgb1: #404040;
        --tx10: inherit;

        /* Sombras */
        --bsh2: 0px 3px 1px -2px rgba(0, 0, 0, 0.08), 0px 2px 2px 0px rgba(0, 0, 0, 0.06), 0px 1px 5px 0px rgba(0, 0, 0, 0.06);
        --bsh4: 0px 2px 4px -1px rgba(0, 0, 0, 0.08), 0px 4px 5px 0px rgba(0, 0, 0, 0.06), 0px 1px 10px 0px rgba(0, 0, 0, 0.06);
        --bsh8: 0px 5px 5px -3px rgba(0, 0, 0, 0.2), 0px 8px 10px 1px rgba(0, 0, 0, 0.14), 0px 3px 14px 2px rgba(0, 0, 0, 0.12);

        /* Tamaño texto titulos */
        --sz01: 14px;

        /* Resaltado botones */
        --bns1: brightness(90%);
        --bns2: brightness(85%);

        /* Compatibilidad */
        --invert: invert(100%);
    }

    /* Botones foro */
    img[src*="/http://reply.gif"],
    img[src*="/http://threadclosed.gif"],
    img[src*="/http://newthread.gif"],
    img[src*="/http://multiquote_on.gif"],
    img[src*="/http://multiquote_off.gif"],
    img[src*="/http://quickreply.gif"],
    img[src*="/http://edit.gif"],
    img[src*="/http://quote.gif"] {
    background: url(http://imgur.com/P26bjeG.png);
    background-size: 114px 232px;
    }

    /* Botones privados */
    img[src*="/http://newprivate.png"],
    img[src*="/http://reply_small.gif"],
    img[src*="/http://forward.gif"] {
    background: url(http://imgur.com/74ujPY8.png);
    background-size: 132px 86px;
    }

    /* Flecha input */
    select {
        background: var(--co01) url(https://i.ibb.co/QNWg75d/desplegable.png) right no-repeat;
    }

    /* Compatibilidad */
    img[src*="/editor/"], 
    img[src*="/misc/"],
    img[src*="/buttons/"],
    img[src*="images/mention.png"] {
        filter: invert(100%);
    }
    img[src*="/statusicon/user"]:not([src*="http://user_offline.gif"]) {
        filter: initial;
    }

    /* Colores necesarios para tema oscuro */
    font[color="Black"] {color: var(--tx01);}
    font[color="#000000"] {color: var(--tx01);}
    font[color="#f1f1f1"] {color: var(--bg03);}
    font[color="White"] {color: var(--bg01);}
    font[color="Silver"] {color: #2c2c2c;}
    font[color="Red"] {color: #f75252;}
    font[color="DarkRed"] {color: #b33636;}
    font[color="Navy"] {color: #5c95e5;}
    font[color="Blue"] {color: #91baf2;}
    font[color="Green"] {color: #85e585;}
    font[color="DarkGreen"] {color: #5cbf5c;}
    font[color="Yellow"] {color: #f2f26d;}

    img[id^="vB_Editor"][style="background-color: Black;"] {background: var(--tx01)!important;}
    img[id^="vB_Editor"][style="background-color:black"] {background: var(--tx01) !important;}
    img[id^="vB_Editor"][style="background-color: White;"] {background: var(--bg01) !important;}
    img[id^="vB_Editor"][style="background-color: Silver;"] {background: #2c2c2c !important;}
    img[id^="vB_Editor"][style="background-color: Red;"] {background: #f75252 !important;}
    img[id^="vB_Editor"][style="background-color: DarkRed;"] {background: #b33636 !important;}
    img[id^="vB_Editor"][style="background-color: Navy;"] {background: #5c95e5 !important;}
    img[id^="vB_Editor"][style="background-color: Blue;"] {background: #91baf2 !important;}
    img[id^="vB_Editor"][style="background-color: Green;"] {background: #85e585 !important;}
    img[id^="vB_Editor"][style="background-color: DarkGreen;"] {background: #5cbf5c !important;}
    img[id^="vB_Editor"][style="background-color: Yellow;"] {background: #f2f26d !important;}

    /* Compatibilidad */
    font[color="#f1f1f1"] {color: var(--bg03);}
    .thead { background: var(--bg03); color: var(--tx01) } /* compatibilidad colores desplegables */
    .tcat { background: var(--bg05) !important; } /* cabeceras transparentes */
    table.page tbody tr td.tfoot:nth-child(3) { opacity: 1 !important; } /* opacidad botón Ir Arriba */

/* Fin Parámetros Tema Nocturno */
}

@-moz-document regexp("(http[s]?:\\/\\/)*www.forocoches.*") {
.alt1-user,
.alt1-author {
    background: var(--bg03);
    color: var(--tx01);
    border: 0 !important;
    font-size: 16px;
    line-height: 1.375;
}


/* Hilos inicio */
.texto,
.texto1 {
    font-size: var(--sz01)
}

/* Posts */
.alt1 div[id^=post_message],
.alt1 div[id^=post_message] li {
    font-size: 16px;
    line-height: 1.375;
}

/* Citas */
#HOTWordsTxt td.alt2,
td.alt2[style="border:1px inset; -moz-border-radius: 8px; border-radius: 8px; margin: 8px; padding: 8px; word-wrap: break-word; display:block; max-height:410px; overflow:auto;"] {
    font-size: 16px;
    line-height: 1.375;
}
div[id^="post_message"] div table { 
    background-color: var(--bg04);
    border-radius: 5px;
}

/* Code */
div#posts pre.alt2,
code {
    line-height: 1.125;
}

/* Editor de mensajes */
.wysiwyg,
textarea {
    font-size: 16px !important;
    line-height: 1.4 !important;
    font-family: Roboto !important;
}

/* Tamaño de fuente */
.smallfont {
    font-size: 13px;
}
font[size="1"] {
    font-size: 12px;
}
font[size="2"] {
    font-size: 15px;
}
font[size="3"] {
    font-size: 17px;
}
font[size="4"] {
    font-size: 20px;
}
font[size="5"] {
    font-size: 24px;
}
font[size="6"] {
    font-size: 29px;
}
font[size="7"] {
    font-size: 37px;
}



/* QUITAR ELEMENTOS */
td.contenido > form[name="busca"],
td.cmini > form[name="busca"] {
    display: none;
}

/* Icono multipágina */
img[src*="/misc/multipage.png"] {
    display: none;
}

/* Tablas vacías bajo cabecera */
table.cajasnews td[bgcolor="#f5f5f5"][align=left][width="65%"],
table.cajasnews td[bgcolor="#f5f5f5"][align=left][width="65%"] + td[bgcolor="#f5f5f5"][align=right],
div[id="1st"],
TABLE#AutoNumber4[width="100%"][cellspacing="0"][bordercolor="#111111"][border="0"][style="border-collapse: collapse"],
table#AutoNumber6 {
    display: none;
}

/* Quitar iconos inicio */
img[src*="/bullet_pic2.gif"],
img[src*="/bullet_minisquare.gif"] {
    display: none;
}

/* Tabla botón refrescar, no modificar */
#inlinemodform table[style="margin-bottom:3px"] + br + table#table3.tborder[width="100%"][cellspacing="1"][cellpadding="5"][border="0"][align=center],
table[style="margin-bottom:3px"] + br + table#table3.tborder[width="100%"][cellspacing="1"][cellpadding="5"][border="0"][align=center] + br {
    display: none;
}

/* Barra lateral InfoCoches */
td#collapseobj_sidebar[style="padding-right:3px; "][bgcolor="#f9f9f9"][width="170"],
td[style="background-color:#f1f1f1;"][align=center][valign=top][width="15"] {
    display: none !important;
}
td[style="padding-left:3px;"] {
    padding: 0 !important;
}

/* Estando desconectado */
table.tborder[bgcolor="#555576"],
td[width="170"][bgcolor="#f9f9f9"],
td[width="170"][bgcolor="#f9f9f9"] + td[width="3"] {
    display: none;
}

/* Iconos fecha/hora post */
img[src*="/post_old.gif"],
img[src*="/post_new.gif"] {
    display: none;
}

/* Iconos barra de herramientas */
img[src*="/sendtofriend.gif"],
img[src*="/subscribe.gif"] {
    display: none;
}

/* Resaltar autor barra de herramientas */
td[style*="border-left: 3px solid rgb(85, 145, 204); border-right: 3px solid rgb(85, 145, 204);"],
td[style*="border-left: 3px solid rgb(202, 52, 21); border-right: 3px solid rgb(202, 52, 21);"],
td[style="border-left: 3px solid #ca3415; border-right: 3px solid #ca3415;"],
td[style="border-left: 3px solid #5591cc; border-right: 3px solid #5591cc;"] {
    border: 0 !important;
}

/* Titulo de hilo */
.cmega {
    display: none;
}

/* Tabla vacia titulo de hilo */
table[id^=fcthread] td {
    padding: 0 !important;
    font: 0 none !important;
}

/* Fix botón borrar hilo */
table[id^=fcthread] form {
    position: relative;
}
table[id^=fcthread] input.button {
    position: absolute;
    right: -4px;
    top: -51px;
}

/* Tabla vacia titulo de hilo */
table[bgcolor="#d5e5ee"] {
    margin-bottom: 0 !important;
    background: 0 0;
}

/* Tabla amarilla pie de página */
table.cajasprin > tbody > tr > td[bgcolor="#ffffe0"][align=center] {
    display: none;
}

/* Ranking bloqueo, table3 cuando hay publicidad */
table[style="margin-top:-2px"] + br + table#table3 + br + br + table.tborder[cellpadding="6"] + br + table.tborder,
table[style="margin-top:-2px"] + br + table#table3 + br + br + table.tborder[cellpadding="6"] + br,
table[style="margin-top:-2px"] + br + table.tborder[cellpadding="6"] + br + table.tborder,
table[style="margin-top:-2px"] + br + table.tborder[cellpadding="6"] + br {
    display: none;
}

/* Tablas de publicidad vacias hilos */
TABLE.cajasprin[width="100%"][cellspacing="0"][cellpadding="2"][bordercolor="#111111"][border="0"][style="border-collapse: collapse"] > tbody > tr > td {
    padding: 0;
}
TABLE.cajasprin[width="100%"][cellspacing="0"][cellpadding="2"][bordercolor="#111111"][border="0"][style="border-collapse: collapse"] {
    border: 0;
}

/* Tablas de publicidad inicio */
table#AutoNumber30 {
    border: transparent;
}

div[id^='tg-adhesion']{
    display: none;
}

div[id^='tg-adhesion-bar-']{
    display: none;
}

/* Fondo tabla publicidad desconectado */
td[bgcolor="#ffffe0"] {
    background: 0 0;
}

/* Tabla publicidad pie de foro desconectado */
div[id*=div-336x280] {
    height: auto !important;
}

/* Publicidad primer post desconectado */
span[style="border: solid #FAFAFA; border-left: 10px solid #DADADA; PADDING-LEFT: 5px; padding:5px; margin: 1px; float: right; width: 301px; height: 251px;"] {
    border: none !important;
    height: auto !important;
}

/* Aviso hilos restringidos desconectado */
table#table3.tborder td.alt1[style="background-color:#FFF"] {
    background-color: var(--bg04) !important;
}

/* Listado de foros aviso desconectado */
div.panel[style="margin-top:5px; background-color:#ffffff;"] {
    background-color: var(--bg03) !important;
}
div#div-300x250,
div#div-300x600 {
    height: auto !important;
}

/* Publicidad persistente */
div[id^=div-970x90],
table[height="90"][width="970"],
table#AutoNumber30,
table.cajasprin[bgcolor="#f0f5ff"],
.opd_adsticky-scroll:not(.opd_adsticky-close) {
    display: none;
}

/* Tablas publicidad hilos */
body > div > center > table#AutoNumber1 td:not(:last-child),
td[bgcolor="#f0f5ff"] {
    display: none;
}



/* PRINCIPAL */
/* Logo forocoches */
body > div > center > table#AutoNumber1 td:last-child img {
    position: relative;
    width: 166px;
    height: 45px;
    top: 15px;
    opacity: 1;
    display: none;
}
body > div > center > table#AutoNumber1 td:last-child {
    position: absolute;
    z-index: 1;
    display: none;
    top: -5px;
    left: 48px;
    width: 166px;
    height: 75px;
    background: url(https://st.forocoches.com/image/top_c1_hd.png) 0 0/166px 75px;
}

/* Aviso cookies */
#infocookie {
    display: none;
}

/* Fondo principal */
body {
    background: var(--bg01);
    color: var(--tx01);
    margin: 0;
}

/* Textos principales */
.texto,
.texto1,
font[color="#000000"] {
    color: var(--tx01);
}

/* Textos secundarios */
.time,
.smallfont-gray {
    color: var(--rgb5);
}

/* Enlaces principales */
a:link,
a:visited,
.texto A {
    color: var(--tx07);
}
.contenido A:hover,
.contenido_menu A:hover,
.adcontenido A:hover,
.texto A:hover,
.texto1 A:hover,
.miniyo A:hover,
.top2 A:hover,
A:hover,
a:active {
    color: var(--tx08);
    text-decoration: none;
}


/* Barra principal botones */
table.cajasprin[style="border-collapse: collapse"]:nth-of-type(1) {
    position: relative;
    top: 0;
    width: 100%;
    border: 0;
    border-bottom: 0;
}
table#AutoNumber9 {
    position: absolute;
    top: 16px;
    left: 25px;
    display: block;
    width: auto;
    z-index: 1;
}
table#AutoNumber9 > tbody > tr {
    display: block;
    border-spacing: 25px 0;
    border-collapse: separate;
}
table#AutoNumber9 > tbody > tr > td {
    border: 0;
    height: 32px;
}

/* Texto */
table#AutoNumber9 font {
    font-size: 13.16px;
    /* fix chrome valign */
    font-family: Roboto;
    font-weight: 500;
    -webkit-user-select: none;
    -moz-user-select: none;
}

/* Botón INICIO */
table#AutoNumber9 > tbody > tr > td:nth-child(1) {
    position: relative;
    background: var(--tx07);
    border-radius: 2px;
    padding: 0;
    box-shadow: var(--bsh2);
}

/* Botón INICIO hover */
table#AutoNumber9 > tbody > tr > td[bgcolor="#ffffff"] {
    background: var(--rgb5);
    border-radius: 2px;
    padding: 0;
    box-shadow: var(--bsh2);
}

/* Botón FORO */
table#AutoNumber9 > tbody > tr > td[bgcolor="#FFFFFB"],
table#AutoNumber9 > tbody > tr > td[bgcolor="#FFFFFF"],
table#AutoNumber9 > tbody > tr > td[bgcolor="#ECECEC"] {
    background: var(--tx07);
    border-radius: 2px;
    padding: 0;
    box-shadow: var(--bsh2);
}

/* Botón FORO hover */
table#AutoNumber9 > tbody > tr > td[bgcolor="#FFFFFB"]:hover,
table#AutoNumber9 > tbody > tr > td[bgcolor="#FFFFFF"]:hover {
    background: var(--tx08);
}

/* Personalización Botones INICIO y FORO */
table#AutoNumber9 > tbody > tr > td > a {
    padding: 0 8px;
    line-height: 32px;
    display: block;
    color: var(--co03);
}


/* Pie de página foro */
td.cmini {
    border: none !important;
    padding: 8px 4px;
}

/* Borde y fondo tabla pie de página */
table[style="border-collapse: collapse"][bordercolordark="#000000"][bordercolorlight="#000000"][bordercolor="#111111"][bgcolor="#CCCCCC"] {
    border: 1px solid transparent;
    background: 0 0;
}

/* Pie de página foro quitar color superpuesto */
td.cmini[bgcolor="#FFFFFF"] {
    background: 0 0;
}
table[width="100%"][cellpadding="2"].page {
    border-top: 1px solid var(--bg02);
    border-left: 1px solid var(--bg02);
    border-right: 1px solid var(--bg02);
}

/* Pie de página inicio */
td.contenido[style="border-right:1px solid #CCCCCC; border-top:1px solid #CCCCCC; border-bottom:1px solid #CCCCCC;"],
td.contenido[style="border-left:1px solid #CCCCCC; border-top:1px solid #CCCCCC; border-bottom:1px solid #CCCCCC;"],
td.contenido[style="border-right:1px solid #CCCCCC; border-left:1px solid #CCCCCC; border-top:1px solid #CCCCCC; border-bottom:1px solid #CCCCCC;"] {
    background: 0 0 !important;
    padding: 8px 5px 9px;
    border-top: 1px solid transparent !important;
}
td.contenido[style="border-left:1px solid #CCCCCC; border-top:1px solid #CCCCCC; border-bottom:1px solid #CCCCCC;"] a {
    font-weight: 400;
}

/* Pantalla resultados búsqueda con buscador de google */
.panelsurround table#AutoNumber1 td[bordercolor="#C0C0C0"][bgcolor="#FBFBFB"] {
    background: var(--bg03);
    border: 1px solid !important;
}

/* Bordes */
table.tborder,
div.tborder[style="padding:1px; border-top-width:0px"] {
    border-collapse: collapse;
    padding: 0 !important;
}



/* INICIO */
/* Header y Buscador */
table.cajastip > tbody > tr > td.texto {
    background: 0 0 !important;
}
table.cajastip {
    border-style: none;
    border-spacing: 1px;
    background: var(--c200);
    background-size: cover;
    padding: 50px;
    margin-bottom: 60px;
    position: relative;
    box-shadow: var(--bsh4);
}
table.cajastip td.texto[width="10%"] {
    position: absolute;
    right: 42px;
    top: 9px;
    width: auto;
}

/* Bordes Header Inicio */
table.cajasprin td.contenido div > center > table[cellpadding="2"][bordercolor="#111111"] > tbody > tr > td {
    padding: 0;
}

/* Ocultar usuarios en linea en Inicio */
td.texto[width="90%"][bgcolor="#ffffff"] {
    display: none;
}

/* Tabla principal */
td[bgcolor="#f5f5f5"] {
    background: var(--bg01);
}

/* Hilos inicio */
td[width="84%"] {
    position: relative;
    left: 0;
    width: 100%;
    padding: 0 48px;
}
td[width="84%"] > .cajasnews {
    box-shadow: var(--bsh2);
}
table.cajascat,
table.cajasprin {
    border: 0;
}

/* Tabla final página */
td.contenido table#AutoNumber7 {
    display: none;
}

/* Barra de herramientas inicio */
table.contenido {
    border: none !important;
}

/* Fondo principal inicio */
td.contenido {
    border: none !important;
    color: var(--tx01);
    background: inherit;
}
.cajascat > tbody > tr > td {
    padding: 0;
}
td[width="312"] {
    position: absolute;
    top: 0;
    left: 0;
    width: calc(100% - 4px);
}
table#AutoNumber1.contenido[bgcolor="#CCCCCC"] {
    background: 0 0;
}
table.cajascat td.contenido tr > td:nth-child(1) {
    display: none;
}

/* Temas iniciados y temas participados inicio */
table.cajascat > tbody > tr:nth-child(1) > td:nth-child(2),
table.cajascat > tbody > tr:nth-child(1) > td:nth-child(3) {
    position: absolute;
    z-index: 4;
}
table.cajascat {
    border-left: 48px solid transparent;
    background: 0 0;
}
table.cajascat td.contenido tr {
    display: inline-block;
    height: 32px;
}
table.cajascat td.contenido tr > td {
    background: 0 0 !important;
    display: block;
    padding: 8px 16px;
    color: transparent;
    font-size: 0;
}
table.cajascat td.contenido tr > td a {
    color: var(--rgb7b);
    font-weight: 400;
}
table.cajascat td.contenido tr > td a:hover {
    color: var(--co04);
}

/* Mover columna hora a la derecha */
td[width="84%"] .texto > table > tbody > tr > td:nth-child(2) {
    position: absolute;
    right: 250px;
    vertical-align: middle;
    margin-top: 10px;
    height: auto;
    border: 0;
}
td[width="84%"] .texto > table > tbody > tr:nth-child(1) > td:nth-child(2) {
    margin-top: 10px;
}

/* Ocultar nombre usuario pagina inicio */
table.cajascat > tbody > tr:nth-child(1) > td:nth-child(1) {
    position: absolute;
    background: 0 0;
    border: 0;
    width: auto;
    font-size: 13px;
    font-weight: 400;
    right: 48px;
    top: 67px;
    color: var(--c200);
    /* texto del mismo color que el fondo */
}

/* Avatar Inicio */
#AutoNumber1 td.contenido a[href^="/foro/member.php"] {
    position: absolute;
    width: 96px;
    height: 18px;
    top: 67px;
    right: 40px;
    z-index: 8;
}
img[style="max-height:90px; margin-left: 6px; border-radius: 5%; -moz-border-radius: 5%; -webkit-border-radius: 5%"],
img[src*="/image/forocoches_avatar.gif"] {
    visibility: visible;
    position: absolute;
    z-index: 8;
    display: block;
    height: 75px;
    width: 75px;
    border-radius: 112px !important;
    box-shadow: var(--bsh4);
}

/* Títulos página Información - acceso barra inferior */
table.cajasnews tbody tr td.texto table.cajascat tbody tr td.cat { 
    background: 0 0;
    position: inherit;
    border: 0;
    width: auto;
    font-size: 16px;
