/* DISEÑO DE POSTS */
/* BASE */
/* Quitar espacio vacío bajo primer post de cada hilo */
#posts table[id^=post]:not([style="table-layout: fixed; width: 100%;"]) tr:not(:nth-child(3)),
div[id^=edit][style="padding:0px 0px 5px 0px"] > br {
    display: none;
}
/* Quita contenedor vacío final de posts */
#posts #lastpost {
    display: none;
}
/* Interior tabla post */
.page div[id^="edit"] {
    padding: 0 !important;
    /* style incrustado */
}
/* Base para distribuir ficha de usuario y post */
#posts [id^="post"] > tbody,
#post > tbody {
    display: block;
}
/* Necesario para posicionar elementos */
#posts table[id^="post"],
#post {
    position: relative;
}
/* Nuevo formato posts */
#posts > div,
#post > tbody {
    padding: 48px 32px 48px 0;
    margin-bottom: 24px;
    box-shadow: var(--bsh2);
    border-radius: 5px;
    background: var(--bg03);
}
/* Quita todos los fondos */
#posts > div .page,
#posts > div .tborder,
#posts > div .alt1,
#posts > div .alt2,
#posts > div .thead {
    background: transparent;
}
/* Quita bordes tablas */
#posts .tborder,
#posts .alt2,
#posts .alt1,
#posts .thead {
    border: 0 !important;
}



/* FICHA USUARIO */
/* Base */
table[id^="post"] > tbody > tr:nth-child(2) > td:nth-child(1),
#post > tbody > tr:nth-child(2) > td:nth-child(1) {
    min-width: 200px;
    max-width: 200px;
    padding: 0 16px;
    position: relative;
    z-index: 2;
}

/* Borde ficha de usuario */
#posts tbody > tr:nth-child(2) > td:nth-child(1) {
    border-right: 1px solid var(--bg02) !important;
}

/* Hora post */
table[id^="post"] > tbody > tr > td.thead:nth-child(1) {
    display: block;
    position: relative;
    top: -30px;
    font-size: 13px;
    width: 200px;
    padding: 0px 16px;
    text-align: center;
    z-index: 2;
}

/* Numero post */
table[id^="post"] > tbody > tr > td.thead:nth-child(2) {
    display: block;
    position: absolute;
    right: -32px;
    top: -48px;
    color: transparent;
    font-size: 0;
    padding: 16px;
    overflow: hidden;
    background: transparent;
}
table[id^="post"] > tbody > tr > td.thead:nth-child(2) a {
    display: inline-block;
}
table[id^="post"] > tbody > tr > td.thead:nth-child(2) > a {
    color: var(--rgb5);
    font-size: 13px;
}
table[id^="post"] > tbody > tr > td.thead:nth-child(2) > a > strong {
    font-weight: normal;
}

/* Nick usuario */
a.bigusername {
    overflow: hidden;
    font-size: 18px;
    color: var(--rgb7);
}
a.bigusername:hover {
    color: var(--tx01);
}

/* Avatar redondo y posición */
.smallfont > br + a[href^="member.php?u="] {
    position: absolute;
    top: 0;
    margin: 0 40px;
    display: block;
    height: 120px;
    width: 120px;
    border-radius: 50%;
    overflow: hidden;
}
.smallfont > br + a[href^="member.php?u="] > img {
    object-fit: cover;
