/* Editor de mensajes wysiwyg */
.wysiwyg {
    background: var(--co01) !important;
    color: var(--tx01) !important;
    margin: 8px !important;
    padding: 0 !important;
}

/* Enlaces en editor de mensajes */
.wysiwyg a:link,
.wysiwyg a:visited {
    color: var(--tx04) !important;
}

/* Editor de mensajes rapido */
div[style="max-width:880px; width:auto !important; width:880px"] {
    max-width: 100% !important/* 1440px */
}

/* Editor de mensajes completo */
#vB_Editor_001_controls table:nth-child(2) tr td {
    display: inline-block;
}
div#vB_Editor_001_controls.controlbar + table {
    table-layout: fixed;
    max-width: 100% !important/* 1440px */
}
iframe#vB_Editor_001_iframe {
    width: 100% !important;
}

/* Iconos mensaje */
fieldset.fieldset[style="margin-top: 5px"] {
    position: relative;
    top: -5px;
    margin: auto;
    background: var(--bg04);
    border-radius: 2px;
    padding: 8px;
}
fieldset.fieldset[style="margin-top: 5px"] table[width="95%"] {
    width: 100%;
}
fieldset.fieldset[style="margin-top: 5px"] tr {
    display: table-cell;
    width: 0;
}
fieldset.fieldset[style="margin-top: 5px"] tr td:nth-child(odd) {
    padding-right: 24px;
}
fieldset.fieldset[style="margin-top: 5px"] legend,
fieldset.fieldset[style="margin-top: 5px"] div[style="margin-bottom:3px"],
fieldset.fieldset[style="margin-top: 5px"] tr:nth-child(3) td:nth-child(1) {
    display: none;
}

/* Opciones editor mensajes */
tbody#collapseobj_newpost_options fieldset legend {
    margin-top: 12px;
}
tbody#collapseobj_newpost_options fieldset td {
    font-size: 13px;
}

/* Fix recuadro de opciones dentro de editor */
.vBulletin_editor fieldset {
    border: 0 !important;
}
.vBulletin_editor fieldset > legend {
    display: none;
}

/* Botones bajo editor de mensajes, enviar y vista previa */
td.panelsurround div[style="margin-top:5px"] {
    margin-top: 12px !important;
}

/* Ajustes editor mensajes */
table[style="min-width: 480px; max-width: 100%; width: auto !important; width: 480px;"] div div.smallfont {
    opacity: 0;
}
table[style="min-width: 480px; max-width: 100%; width: auto !important; width: 480px;"] table.fieldset input {
    margin: 4px 0;
}

/* Opciones, formularios, botones */
textarea,
input,
button,
select {
    outline: 0;
    position: relative;
    z-index: 8;
    /* chrome */
}
textarea {
    color: var(--tx01);
    background-color: transparent;
    border: 1px solid var(--rgb1) !important;
    transition: .12s ease all;
}
textarea:focus {
    border: 1px solid var(--c401) !important;
}

/* Input, !important necesario */
.bginput,
input[name="q"] {
    display: inline-block;
    vertical-align: middle;
    background-color: transparent;
    color: var(--co02) !important;
    border: 0 !important;
    height: 24px !important;
    padding: 0 8px !important;
    font-family: Roboto !important;
    font-size: 14px !important;
    margin: 4px 8px 4px 0;
    padding-left: 0 !important;
    border-bottom: 1px solid var(--rgb7) !important;
    box-shadow: none;
    box-sizing: content-box;
    transition: .12s ease all;
}
.bginput:focus,
input[name="q"]:focus {
    border-bottom: 1px solid var(--c401) !important;
    box-shadow: 0 1px 0 0 var(--c401);
}

/* BUSCADOR Desplegable */
.cfield {
    border: 0;
    color: transparent;
    height: 32px;
    width: 0;
    padding: 0 16px;
    background: url(https://i.ibb.co/SxdqnTv/search-white.png) center right 4px/24px no-repeat;
    cursor: pointer;
    z-index: 16;
    transition: .12s;
    transition-delay: 0s;
    margin-right: 25px;
}
.cfield:focus {
    color: #000;
    border: 0;
    border-radius: 2px;
    height: 32px;
    padding: 0 32px 0 8px;
    font-family: Roboto;
    font-size: 14px;
    width: 260px;
    box-shadow: var(--bsh4);
    background: var(--co03) url(https://i.ibb.co/C5Wwywx/search-black.png) center right 4px/24px no-repeat;
    background-size: 24px;
    cursor: text;
    z-index: 1;
    transition-delay: 0s;
}
.cbutton {
    z-index: 1;
}

/* Buscador Desplegable FIX Chrome */
form[name=busca] + td {
    position: relative;
}
form[name=busca] + td .cfield {
    position: absolute;
    top: 4px;
    right: 40px;
}
label,
input {
    line-height: 16px;
    vertical-align: middle;
}
input[type=radio],
input[type=checkbox] {
    margin: 4px 4px 4px 0;
    vertical-align: middle;
    height: 16px;
}

/* Botón Búsqueda Avanzada */
.cfield + input + input + .cbutton {
    background-color: transparent !important;
    position: relative;
    font-size: 0 !important;
    box-shadow: initial;
    border-radius: 0 2px 2px 0 !important;
    left: -40px;
    margin-right: -80px;
}
.cfield + input + input + .cbutton + .cbutton {
    min-width: 30px !important;
    height: 30px !important;
    border-radius: 24px !important;
    background-color: var(--bg07);
    box-shadow: var(--bsh8);
}



/* DESPLEGABLES */
select {
    -webkit-appearance: none;
    -moz-appearance: none;
    vertical-align: middle;
    color: var(--co02) !important;
    border: 0 !important;
    border-radius: 2px !important;
    min-height: 24px !important;
    padding: 0 24px 0 4px !important;
    font-family: Roboto !important;
    font-size: 14px !important;
    margin: 4px 8px 4px 0;
    box-shadow: var(--bsh2);
    background-size: 18px;
    cursor: pointer;
}
select[name="forumchoice[]"] {
    background-image: none;
    cursor: default;
}
fieldset legend {
    color: var(--tx01);
    font-size: 13px;
    padding-left: 3px;
    font-weight: 500;
    text-transform: uppercase;
}
.fieldset td,
.fieldset div {
    font-size: 13px;
    line-height: 1.4;
}
fieldset {
    border: 0;
}
fieldset div[style="padding:3px"],
fieldset div {
    font-size: 13px;
}
fieldset[style="margin:0px"] form#cse-search-box {
    position: relative;
    left: -4px;
}
form#searchform div[align=left] div.thead[style="padding:5px"] {
    background-color: var(--bg02);
    height: 24px;
    line-height: 24px;
    font-size: 15px;
    color: var(--rgb5);
    font-weight: 500;
    padding: 4px 16px !important;
    display: block;
    margin: 10px 0px 15px 0px;
}
select option {
    vertical-align: middle;
    padding: 2px 4px;
    font-family: Roboto !important;
    font-size: 14px !important;
}
.fjdpth0 {
    background: 0 0;
    color: var(--tx01);
}



/* BOTONES */
.button,
input[name=sa],
.cbutton {
    display: inline-block;
    box-sizing: border-box;
    vertical-align: middle;
    background-color: var(--co01);
    color: var(--co02) !important;
    border: 0 !important;
    border-radius: 2px !important;
    height: 24px !important;
    min-width: 24px !important;
    padding: 0 8px !important;
    font-family: Roboto !important;
    font-weight: 500 !important;
    font-size: 13px !important;
    text-transform: uppercase;
    margin: 4px;
    filter: brightness(100%);
    box-shadow: var(--bsh2);
    cursor: pointer;
}
.cbutton {
    height: 32px !important;
    min-width: 32px !important;
    font-size: 15px !important;
    color: var(--rgb7) !important;
}
.button:hover,
input[name=sa]:hover,
.cbutton:hover {
    filter: var(--bns1);
    background-color: var(--co01);
    color: var(--co02) !important;
}
.button:active,
input[name=sa]:active,
.cbutton:active {
    filter: var(--bns2);
    box-shadow: var(--bsh4);
}
.button:disabled {
    box-shadow: var(--bsh2);
    background-color: rgba(0, 0, 0, .12);
    color: rgba(0, 0, 0, .26) !important;
}
input[src="clear.gif"] {
    display: none;
}



/* MENSAJES PRIVADOS */
/* barra mensajes */
table[style="border:2px groove"][width="100%"][cellspacing="1"] {
    border: 1px solid var(--bg02) !important;
    border-spacing: 0;
    border-collapse: collapse;
}
table[style="border:2px groove"] > tbody > tr > td[style="background-color:red; font-size:10px"] {
    background-color: #e57373 !important;
}
table[style="border:2px groove"] > tbody > tr > td[style="background-color:orange; font-size:10px"] {
    background-color: #fff176 !important;
}
table[style="border:2px groove"] > tbody > tr > td[style="background-color:green; font-size:10px"] {
    background-color: #81c784 !important;
}
img[src*="/calendar_popup.png"] {
    display: none;
}

/* Bordes */
form#pmform tbody[id^=collapseobj] > tr > td {
    border-top: 1px solid var(--bg02);
}

/* Lista mensajes privados */
form#pmform td.alt1:nth-child(1) {
    padding: 0 12px;
    height: 56px;
}
form#pmform td.alt1,
form#pmform tbody[id^=collapseobj] td.alt2,
form#pmform td.alt1Active,
form#pmform table.tborder {
    background: var(--bg03);
}
form#pmform .thead div a {
    font-size: 13px;
    font-weight: 500;
    line-height: 24px;
}
form#pmform .alt2 strong,
form#pmform .alt2 label {
    font-size: 13px;
    font-weight: 400;
    line-height: 24px;
}
form#pmform td.alt1 a {
    font-size: var(--sz01);
    font-weight: 500;
}
form#pmform td.alt1 span,
form#pmform td.alt1 .time {
    font-size: 12px;
    color: var(--rgb7);
}

/* Fix respecto nueva barra herramientas */
form#pmform td.alt2 {
    visibility: visible;
}

/* Calendario */
.floatcontainer table.tborder.vB_DatePicker.page {
    background: var(--bg03) !important;
}

/* Varios MP */
#pm_historylink {
    margin-top: 4px;
}

/* Panel de control barra lateral */
div.page > div[style="padding:0px 0px 0px 0px"] table > tbody > tr[valign=top] > td:nth-child(1) table.tborder td.thead {
    font-size: 13px;
    font-weight: 500;
    padding: 8px;
}
div.page > div[style="padding:0px 0px 0px 0px"] table > tbody > tr[valign=top] > td:nth-child(1) table.tborder td.alt2 a {
    font-size: 13px;
    padding: 0 4px;
    color: var(--rgb7);
}
div.page > div[style="padding:0px 0px 0px 0px"] table > tbody > tr[valign=top] > td:nth-child(1) table.tborder td.alt2 a:hover {
    color: var(--co02);
}
div.page > div[style="padding:0px 0px 0px 0px"] table > tbody > tr[valign=top] > td:nth-child(1) table.tborder td.alt1 a:not([href*="member.php"]):not([href*="forumdisplay.php"]):not([href*="search.php"]) {
    font-size: 13px;
    padding: 0 4px;
    color: var(--tx04);
}
div.page > div[style="padding:0px 0px 0px 0px"] table > tbody > tr[valign=top] > td:nth-child(1) table.tborder td.alt2 {
    background: var(--bg03);
    padding: 8px 5px;
}

/* Separacion contenedores en Panel de Control */
/* Todas las reglas del selector son necesarias */
div.page > div[style="padding:0px 0px 0px 0px"] table > tbody > tr[valign=top] > td:nth-child(2):not([align=right]):not(.smallfont):not([width="50%"]):not(.alt1) {
    padding-right: 18px;
}

/* Enviar invitaciones */
div.page > div[style="padding:0px 0px 0px 0px"] table > tbody > tr[valign=top] > td:nth-child(3) table.tborder td.alt1 span.smallfont a {
    line-height: 24px;
    padding: 0 4px;
    color: var(--rgb7);
    font-size: 13px;
}

/* Mensaje de error al enviar mensaje 30s */
#qr_error_tbody .smallfont a {
    font-size: 13px;
    font-weight: 500;
    text-transform: uppercase;
}
#qr_error_tbody {
    display: table;
    margin-left: 16px;
    width: calc(100% - 32px);
    background: var(--rgb5);
    border-radius: 2px;
}
#qr_error_tbody a {
    color: var(--co01);
}
#qr_error_tbody td {
    color: var(--co01);
    background: 0 0;
    font-size: 13px;
    font-weight: 500;
    padding: 8px;
}
/* Tablas anchas opciones post, encuestas */
div[style="width:750px"],
div[style="width:880px"] {
    width: auto !important;
    padding: 0px 15px;
}
/* Tabla ancha mp */
form[action*="postings.php?do=dodeletethread"] td.thead,
div[style="width:750px"] > fieldset > legend {
    display: none;
}

/* Sombras paneles varios */
form[action^="editpost.php?do=deletepost"] .tborder,
tr[valign=top] > td:not(.smallfont):not([align=right]) .tborder,
form[name=postvarform] > .tborder,
#collapseobj_threadreview,
#inlinemodform table[id^=post][style="margin:5px 0px 5px 0px"] {
    box-shadow: var(--bsh8);
}

/* Quitar sombra donde no es necesaria */
form[name=vbform] .tborder:first-of-type {
    box-shadow: initial !important;
}




/* WIP */
/* Resultados búsqueda */
#inlinemodform [id^="post"] {
    position: relative
}

/* Nuevos botones posts */
td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] {
    font-size: 0;
}
td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] > a > img {
    display: none;
}
td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] > a:after,
a[href^="private.php?do=newpm&pmid="]:after,
a[href^="private.php?do=newpm&forward=1&pmid="]:after {
    display: inline-block;
    padding: 0 16px;
    margin: 0 4px;
    border-radius: 2px;
    font-size: 13px;
    font-weight: 500;
    color: var(--tx01);
    height: 32px;
    line-height: 32px;
    transition: .12s;
}
td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] > a:hover:after {
    background: var(--rgb1);
}
td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] > a:focus:after {
    background: var(--rgb2);
}

/* Editar */
td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] > a:nth-last-child(4):after {
    content: "EDITAR";
}

/* Citar */
td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] > a:nth-last-child(3):after {
    content: "CITAR";
}

/* Multi-cita */
#posts td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] > a:nth-last-child(2):after {
    content: "+";
    width: 32px;
    padding: 0px;
    color: transparent;
    margin-right: 1px;
}
#posts td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] > a:nth-last-child(2) > img {
    display: inline-block;
    position: absolute;
    top: 0;
    right: 0;
    width: 0;
    padding-right: 32px;
    height: 32px;
    z-index: 1;
}

/* Multi-cita desactivada */
td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] > a:nth-last-child(2) > img[src$="multiquote_off.gif"] {
    background: url(https://i.imgur.com/NzVeCve.png) no-repeat center;
    background-size: 24px;
}

/* Multi-cita activada */
td[style="border: 1px solid #D1D1D1; border-left: 0px; border-top: 0px"] > a:nth-last-child(2) > img[src$="multiquote_on.gif"] {
    background: url(https://i.imgur.com/m8s1n1q.png) no-repeat center;
    background-size: 24px;
}


/* MP Responder */
a[href^="private.php?do=newpm&pmid="]:after {
    content: "RESPONDER";
}

/* MP Reenviar */
a[href^="private.php?do=newpm&forward=1&pmid="]:after {
    content: "REENVIAR";
}

/* Nuevo tema y Nuevo Privado*/
a[href^="newthread.php"] > img,
a[href="private.php?do=newpm"] > img {
    display: none;
}
a[href^="newthread.php"]:after,
td.smallfont a[href="private.php?do=newpm"]:after {
    content: "NUEVO TEMA";
    display: inline-block;
    background: var(--c401);
    padding: 0 16px;
    border-radius: 2px;
    font-size: 13px;
    font-weight: 500;
    color: var(--rgb7b);
    height: 36px;
    line-height: 36px;
    transition: .12s;
    box-shadow: var(--bsh2);
}
td.smallfont a[href="private.php?do=newpm"]:after {
    content: "NUEVO PRIVADO";
}
a[href^="newthread.php"]:hover:after,
a[href="private.php?do=newpm"]:hover:after {
    background: var(--tx08) !important;
    box-shadow: var(--bsh4) !important;
    color: var(--co04) !important;
}
a[href^="newthread.php"]:focus:after,
a[href="private.php?do=newpm"]:focus:after {
    box-shadow: var(--bsh8) !important;
}

/* Ocultar botón nuevo tema en herramientas */
#forumtools_menu > form:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) > tr:nth-child(2) > td:nth-child(1) > a:nth-child(1)::after {
    display: none;
}

/* Responder */
a[href^="newreply.php?do=newreply&noquote="] > img {
    display: none;
}
a[href^="newreply.php?do=newreply&noquote="]:after {
    content: "RESPONDER";
    display: inline-block;
    background: var(--c401);
    padding: 0 16px;
    border-radius: 2px;
    font-size: 13px;
    font-weight: 500;
    color: var(--rgb7b);
    height: 36px;
    line-height: 36px;
    transition: .12s;
    box-shadow: var(--bsh2);
}
a[href^="newreply.php?do=newreply&noquote="]:hover:after {
    background: var(--tx08);
    box-shadow: var(--bsh4);
    color: var(--co04);
}
a[href^="newreply.php?do=newreply&noquote="]:focus:after {
    box-shadow: var(--bsh8);
}

/* Tema cerrado */
a[href^="newreply.php?do=newreply&noquote="] {
    position: relative;
}
img[src="//st.forocoches.com/foro/images/buttons/threadclosed.gif"] {
    display: block !important;
    top: -10px;
    left: -2px;
    position: absolute;
    width: 120px;
    height: 38px;
    padding-right: 102px;
    border-radius: 2px;
}
}

@-moz-document regexp("(http[s]?:\\/\\/)*www.forocoches.com/foro/"), regexp("(http[s]?:\\/\\/)*www.forocoches.com/foro/index.php"), regexp("(http[s]?:\\/\\/)*www.forocoches.com/foro/index.php\\?.*"), regexp("https://www.forocoches.com/foro/#top") {
/* FORO only */

    /* Ocultar barra navegación en indice foros */
    .page td[style="padding-left:3px;"] {
        line-height: 0px;
    }
    .page td[style="padding-left:3px;"] .tborder:nth-of-type(1) {
        background: var(--bg01);
    }
    .page td[style="padding-left:3px;"] .tborder:nth-of-type(1) .alt1 {
        display: none;
    }
    .page td[style="padding-left:3px;"] .tborder:nth-of-type(1) .alt2 {
        height: 0px;
        line-height: 0px;
        border-bottom: 22px solid;
        border-color: var(--bg01) !important;
    }

    /* Estadisticas forocoches */
    .page td > table.tborder:last-of-type {
        margin-top: 16px;
    }
    .page td > table.tborder:last-of-type .thead {
        background: var(--bg02);
        color: var(--rgb7);
        font-size: 13px;
        font-weight: 500;
        line-height: 24px;
        padding: 4px 16px;
    }
    .page td > table.tborder:last-of-type .smallfont {
        font-size: 13px;
    }

    /* Color cabeceras principales */
    .tcat {
        background: var(--bg02) !important;
    }

    /* Ocultar Panel de Control clásico en FORO */
    td.vbmenu_control a[href*="usercp.php"]:after {
        content: "";
        background: transparent;
    }
}

@-moz-document regexp("(http[s]?:\\/\\/)*www.forocoches.com/foro/search.php"), regexp("(http[s]?:\\/\\/)*www.forocoches.com/foro/search.php\\?.*") {
/* Pantalla BUSQUEDA */

    /* Corrección tabla búsqueda */
    table#threadslist tr:first-of-type {
        border-top: initial;
    }
    #inlinemodform .tborder:first-of-type .tcat {
        display: none;
    }
    table.tborder[id^="post"] {
        border-radius: 0;
    }

    /* Fix ancho resultados búsquedas */
    #threadslist tr > .thead:nth-child(2) {
        width: 100%;
    }

    /* Quita borde primer titulo */
    #threadslist tr:nth-child(3) td[id^="td_threadtitle"] {
        box-shadow: initial;
    }
    #threadslist tr:nth-child(n+2):nth-child(-n+3) {
        border-top: 0;
    }

    /* Color fila principal resultado búsqueda */
    #threadslist td:nth-child(2),
    #threadslist td:nth-child(3),
    #threadslist td:nth-child(4),
    #threadslist td:nth-child(5) {
        color: var(--tx01);
    }

    /* Color cabecera búsqueda */
    .tcat {
        background: inherit;
        color: var(--rgb5);
    }

    /* Color tablas */
    .tborder {
        background: inherit;
        color: var(--tx01);
    }
}

@-moz-document regexp("(http[s]?:\\/\\/)*www.forocoches.*") {
/* Inicio ICONOS */
    
    /* Ocultar iconos grandes secciones Foro */
    img[src*="/images/statusicon/forum_new.gif"],
    img[src*="/images/statusicon/forum_old.gif"] {
