@-moz-document regexp("(http[s]?:\\/\\/)*www.forocoches.*") {
/* Inicio NOTIFICACIONES */

    table.cajascat td.contenido tr > td a,
    table.cajascat td.contenido tr > td strong {
        font-size: 13px;
    }
    table.cajascat td.contenido tr > td.contenido[bgcolor="#FFFFCC"] strong {
        position: absolute;
        left: 0;
        top: 0;
        background: var(--c400);
        border-radius: 50%;
        color: var(--c40t);
        display: inline-flex;
        justify-content: center;
        align-items: center;
        min-width: 22px;
        height: 22px;
        font-size: 11px;
    }
    table.cajascat td.contenido tr > td a:not([href^="/foro/member.php?u="]) {
        position: relative;
        z-index: 4;
    }

    /* Panel de control */
    table.cajascat td.contenido tr:nth-child(1) > td > a:not([href^="/foro/member.php"]) {
        position: relative;
        top: 55px;
        left: -19px;
    }

    /* Temas iniciados */
    table.cajascat > tbody > tr:nth-child(1) > td:nth-child(2) a {
        position: absolute;
        width: 102px;
        height: 24px;
        top: 60px;
        left: 123px;
        opacity: 0;
        z-index: 6;
    }
    table.cajascat > tbody > tr:nth-child(1) > td:nth-child(2):after {
        position: absolute;
        cursor: pointer;
        content: 'Temas Iniciados';
        width: 102px;
        line-height: 24px;
        top: 60px;
        left: 123px;
        font-size: 13px;
        font-weight: 400;
        color: var(--rgb7b);
    }
    table.cajascat > tbody > tr:nth-child(1) > td:nth-child(2):hover:after {
        color: var(--co04);
    }

    /* Temas participados */
    table.cajascat > tbody > tr:nth-child(1) > td:nth-child(3) a {
        position: absolute;
        width: 116px;
        height: 24px;
        top: 60px;
        left: 257px;
        opacity: 0;
        z-index: 6;
    }
    table.cajascat > tbody > tr:nth-child(1) > td:nth-child(3):after {
        position: absolute;
        cursor: pointer;
        content: 'Temas Participados';
        width: 116px;
        line-height: 24px;
        top: 60px;
        left: 257px;
        font-size: 13px;
        font-weight: 400;
        color: var(--rgb7b);
    }
    table.cajascat > tbody > tr:nth-child(1) > td:nth-child(3):hover:after {
        color: var(--co04);
    }

    /* MP */
    table.cajascat td.contenido tr:nth-child(2) > td a {
        position: absolute;
        left: 459px;
        top: 67px;
    }
    table.cajascat td.contenido tr:nth-child(2) > td.contenido[bgcolor="#FFFFCC"] strong {
        position: absolute;
        left: 572px;
        top: 54px;
    }

    /* Menciones */
    table.cajascat td.contenido tr:nth-child(3) > td a {
        position: absolute;
        left: 604px;
        top: 67px;
    }
    table.cajascat td.contenido tr:nth-child(3) > td.contenido[bgcolor="#FFFFCC"] strong {
        position: absolute;
        left: 670px;
        top: 54px;
    }

    /* Citas */
    table.cajascat td.contenido tr:nth-child(4) > td a {
        position: absolute;
        left: 702px;
        top: 67px;
    }
    table.cajascat td.contenido tr:nth-child(4) > td.contenido[bgcolor="#FFFFCC"] strong {
        position: absolute;
        left: 735px;
        top: 54px;
    }

    /* Desconectar */
    table.cajascat td.contenido tr:nth-child(5) > td a {
        position: absolute;
        left: 767px;
        top: 67px;
    }

    /* Otras notificaciones */
    #notices .tborder {
        background: 0 0;
    }
    #notices .alt1 {
        background: var(--rgb2);
        border-radius: 2px;
        position: relative;
        padding: 16px;
        margin-bottom: 24px;
    }
    style[type="text/css"] + #notices .alt1 {
        width: calc(100% - 128px);
        margin-left: 48px;
        margin-bottom: 11px;
    }

    /* Hilos con notificaciones */
    [id^=td_threadtitle][style="background-color: #ffd1d1;"],
    [id^=td_threadtitle][style="background-color: #fffde4;"] {
        background-color: var(--bg03) !important;
    }

/* Fin NOTIFICACIONES */
}

