css:
@-moz-document domain("crafttweaker.readthedocs.io"), domain("docs.blamejared.com") {
body {
    background-color: #14181e;
    color: #ffffff;
}
h1 {
    color: rgba(195, 210, 255, 0.92)
}
nav.wm-toc-pane {
    background-color: #101215;
    border-right: 1px solid #e7efff29
}

.navbar {
    background-color: #162831;
}

a.wm-article-link.wm-toc-text {
    color: #bfe1ffcc;
}
.wm-toctree a,
.wm-toctree a:visited,
.wm-toctree a:hover,
.wm-toctree a:focus {
    color: #88a9b8;
}
.wm-toc-li:hover {
    background-color: rgba(198, 237, 255, 0.15);
}

h2,
h3,
h4,
h5,
h6 {
    color: #cfeaff69;
}

.table-bordered {
    border: 1px solid rgba(255, 255, 255, 0.15);
    background-color: rgba(0, 0, 0, 0.25);
}

.table-striped > tbody > tr:nth-of-type(odd) {
    background-color: rgba(0, 0, 0, 0.15);
}

.table-bordered > tbody > tr > td,
.table-bordered > tbody > tr > th,
.table-bordered > tfoot > tr > td,
.table-bordered > tfoot > tr > th,
.table-bordered > thead > tr > td,
.table-bordered > thead > tr > th {
    border: 1px solid rgba(255, 255, 255, 0.15);
}

.table-hover > tbody > tr:hover {
    background-color: rgba(255, 255, 255, 0.03);
}

.form-control {
    background-color: rgba(0, 0, 0, 0.36);
    border: 1px solid rgba(255, 255, 255, 0.20);
}

.btn-default {
    border: 1px solid #3b454a;
    background: #0a0a11;
    color: #dedede;
}

.btn-default:hover {
    background: #0a0a00;
    border-color: #66afe9;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
    color: #dedede;
}

.btn-default.active.focus,
.btn-default.active:focus,
.btn-default.active:hover,
.btn-default:active.focus,
.btn-default:active:focus,
.btn-default:active:hover,
.open > .dropdown-toggle.btn-default.focus,
.open > .dropdown-toggle.btn-default:focus,
.open > .dropdown-toggle.btn-default:hover {
    border-color: #66afe9;
    background-color: #0a0a00;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075), 0 0 8px rgba(102, 175, 233, .6);
    color: #dedede;
}

.btn-default.active,
.btn-default:active,
.open > .dropdown-toggle.btn-default {
    background-color: #0a0a00;
    color: #dedede;
    border: 1px solid #3b454a;
}

.btn-default.focus,
.btn-default:focus {
    background-color: #0a0a00;
    color: #dedede;
    border: 1px solid #3b454a;
}


.form-control {
    color: #9cd3f1
}

.search-text {
    color: #c6c6c6;
}

.search-text > b {
    color: #6cb4db;
}

.search-title {
    font-size: 1.8rem;
}

.wm-search-page > li {
    border-bottom: 1px solid rgba(255, 255, 255, 0.07);
}

}

@-moz-document domain("crafttweaker.readthedocs.io"), domain("docs.blamejared.com") {
/***** Dark Codes Blocks *****/
pre {
    background-color: rgba(0, 0, 0, 0.25);
    border: solid 1px rgba(255, 255, 255, 0.15);
}

a {
    color: #2980B9;
}

code {
    background-color: #1c1c1c;
    border-color: #1c1c1c;
    color: #F05732;
}

.rst-content img {
    filter: invert(80%);
}

/* Logo */
.keras-logo {
    background-color: #333;
}

/** Syntax Highlighting **/
.hljs {
    color: #ccc;
    background: #0f1216;
}

/* keywords */
.hljs-keyword,
.css .rule .hljs-keyword,
.hljs-winutils,
.nginx .hljs-title,
.hljs-subst,
.hljs-request,
.hljs-status {
    color: #007020;
}

/* strings */
.hljs-string,
.hljs-tag .hljs-value,
.hljs-phpdoc,
.hljs-dartdoc,
.tex .hljs-formula {
    color: #4070a0;
}

/* numeric literal */
.hljs-number,
.hljs-hexcolor,
.ruby .hljs-constant {
    color: #208050;
}

/* Function names */
.hljs-title,
.hljs-id,
.scss .hljs-preprocessor {
    color: #2762f5;
}

/* Comment */
.hljs-comment,
.diff .hljs-header,
.hljs-javadoc {
    color: #408090;
}

.hljs-tag,
.hljs-tag .hljs-title,
.hljs-rule .hljs-property,
.django .hljs-tag .hljs-keyword {
    color: #8f8ff5;
}
}
