}

@-moz-document domain("www.spigotmc.org") {
/* Scrollbar */
 ::-webkit-scrollbar {
	width: 13px;
	height: 13px;
}
::-webkit-scrollbar-track {
	background-color: #121212;
}
::-webkit-scrollbar-thumb {
	background: #242424;
	border-radius: 3px;
}
}

@-moz-document domain("www.spigotmc.org") {
/* User bar */
 #userBar .navTabs {
	background: #171717;
	border-bottom: none
}
}

@-moz-document domain("www.spigotmc.org") {
/* Logo Block */
 #logoBlock .pageWidth {
	justify-content: space-evenly !important;
}
}

@-moz-document domain("www.spigotmc.org") {
/* Main Content */
 .mainContent .sectionMain {
	background-color: #151515;
	border-color: #151515;
	padding: 0;
	border: none;
}
}

@-moz-document domain("www.spigotmc.org") {
/* Primary components */
 .button {
	background-color: #191919;
	border-color: #191919;
	box-shadow: 0px 0px 8px 4px rgba(0, 0, 0, .15);
	color: #cfcfcf;
	transition: background ease-in-out 150ms;
}
.button:hover {
	background-color: #212121 !important;
	border-color: #212121;
	box-shadow: 0px 0px 8px 4px rgba(0, 0, 0, .2);
	color: #cfcfcf;
}
.textCtrl {
	background-color: #191919;
	border-color: #191919;
	box-shadow: 0px 0px 8px 4px rgba(0, 0, 0, .15);
	padding: 8px 10px;
	color: #cfcfcf;
}
.textCtrl:focus, .textCtrl.Focus {
	border-color: transparent;
}
}

@-moz-document domain("www.spigotmc.org") {
.nodeList .categoryStrip {
	background: #0a0a0a;
	box-shadow: none;
	text-shadow: none;
}
.nodeList .categoryStrip .nodeTitle a {
	color: #cfcfcf;
}
.nodeList .categoryStrip .nodeDescription {
	color: #adadad;
}
.forum_list .nodeList .nodeList, .forum_view .nodeList, .category_view .nodeList, .watch_forums .nodeList {
	background: #212121;
}
.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
	background: #212121;
}
.nodeList .node.level_2:nth-child(even) .nodeInfo, .nodeList .node.level_2:nth-child(even) .nodeInfo .nodeLastPost {
	background: #292929;
}
.node .unread .nodeText .nodeTitle {
	color: #cfcfcf;
}
.node .nodeLastPost {
	background-color: #363636;
	padding: 8px 18px;
}
.staffHeading {
	color: #cfcfcf;
}
.messageUserInfo {
	background-color: #0f0f0f;
	border-color: #0f0f0f;
}
.messageUserBlock h3.userText {
	background-color: #0f0f0f;
	border-color: #0f0f0f;
}
.messageUserBlock .arrow span {
	border-left-color: #0f0f0f;
}
.messageUserBlock .arrow {
	border-left-color: #0f0f0f;
}
}

@-moz-document domain("www.spigotmc.org") {
.breadBoxTop .topCtrl {
	margin: 10px 0 0 8px;
}
.resourceListItem {
	background: #333333;
}
.resourceListItem .resourceImage, .resourceListItem .resourceStats {
