}
.hasJs .bbCodeSpoilerText {
	background-color: #212121;
	color: #cfcfcf;
	border-color: #212121;
}
.innerContent .rateBlock {
	background-color: #0a0a0a;
	border-color: #0a0a0a;
	text-transform: uppercase;
	display: flex;
	justify-content: space-evenly;
	align-items: center;
}
.resourceUpdate .likesSummary {
	background-color: #121212;
	border-color: #121212;
}
.innerContent .rateBlock .Hint {
	display: none;
}
.bbCodeBlock .type {
	background-color: #0a0a0a;
	border-color: #0a0a0a;
	color: #cfcfcf;
}
.bbCodeBlock {
	border-color: #0a0a0a;
}
.bbCodeBlock .code {
	background-image: -webkit-repeating-linear-gradient(top, rgba(0, 0, 0, .015) 0px, rgba(0, 0, 0, .015) 26px, rgba(0, 0, 0, .08) 26px, rgba(0, 0, 0, .08) 52px);
	background-image: repeating-linear-gradient(top, rgba(0, 0, 0, .015) 0px, rgba(0, 0, 0, .015) 26px, rgba(0, 0, 0, .08) 26px, rgba(0, 0, 0, .08) 52px);
}
.bbCodeBlock pre, .bbCodeBlock .code {
	background-color: #333;
}
.sidebar .callToAction {
	margin: 5px 0;
}
a.callToAction span {
	box-shadow: none;
	background: rgb(58, 101, 129);
}
}

