css:
@-moz-document regexp("http(s?):\\/\\/torrentgalaxy\\.(to|org|pw).*") {
ul.navbar-nav > li > a:hover, ul.navbar-nav > li > a:active
{
    background-color: #1c1c1c;
}

body {
    background-color: #262626;
}

.row {
    margin: 0;
}

a {
    color: #3996e6;
}
a:hover, a:active {
    color: #3d81bc;
}

#arcade {
    background-color: black;
    box-shadow: 0 0 20px 10px #383838 inset;
}
.thumbnail {
    background-color: #505050;
}
.thumbnail .caption {
    color: white;
}
.alert-info {
    background-color: #212121 !important;
}

#panelmain {
    color: white;
    background-color: #383838;
}

#panelmain legend {
    color: #a7a3a3;
}

.tgxtablerow:hover, .tgxtablerow:active, .tgxtablerow:nth-of-type(2n+1):hover, .tgxtablerow:nth-of-type(2n+1):active
{
    background-color: #bbb;
}

.tprow, .newtablerow, .tprow:hover {
    background-color: #4d4d4d;
}
.tprow:nth-of-type(2n+1), .newtablerow:nth-of-type(2n+1)
{
    background-color: #595959;
}
.ntranimate:hover, .ntranimate:active,
.ntranimate:nth-of-type(2n+1):hover, .ntranimate:nth-of-type(2n+1):active{
    background-color: #5b5b46;
}

.breadcrumb, .form-control, .btn-default,
.pagination > li > a, .pagination > li > span,
.table-striped > tbody > tr:nth-of-type(2n+1),
.likeboxtr,
.panel,
.chattersonline
{
    background-color: #242424;
    color: #a7a3a3;
}

.chatbox {
    background-color: #181818;
}
#chatcoldd:nth-of-type(2n+1) {
    background-color: rgba(20,20,20,0.75);
}

.chatinputdiv button {
    background-color: #181818 !important;
}

#bbinput {
    background-color: #242424 !important;
    color: #a7a3a3 !important;
}

.chattersonline > .namebadge {
    border: none;
    box-shadow: none;
    background: none;
}

#intblock {
    border-color: black;
}

#intblockheader
{
    border-color: black;
    background-color: #535337;
}

#innerhead {
    background-color: #363636;
    color: #898989;
}

.tgxtablerow {
    background-color: #878787;
}
.tgxtablerow:nth-of-type(2n+1) {
    background-color: #717171;
}
.tgxtablerow a {
    color: #080864;
}


#smallnav[href="/account.php"] {
    background-color: transparent !important;
    color: #a7a3a3 !important;
}

#ddnews:nth-of-type(2n+1) {
    background-color: #181818;
}

textarea#ghosted.mail,
textarea.mail,
#ddnews
{
    background-color: #242424;
    color: white;
}

#headnew {
    background: radial-gradient(ellipse at center,#272a16 0%,#121212 71%);
}

img.profile {
    border-color: #242424;
}

.well {
    background-color: #242424;
    color: white;
}

.btn-default:hover, .btn-default:active,
.pagination > li > a:hover, .pagination > li > span:hover,
.pagination > li > a:active, .pagination > li > span:active
{
    background-color: #303030;
    color: #a7a3a3;
}

.pagination > .disabled > a, .pagination > .disabled > a:focus, .pagination > .disabled > a:hover, .pagination > .disabled > span, .pagination > .disabled > span:focus, .pagination > .disabled > span:hover {
    background-color: #151515;
}

.pagination > .active > a, .pagination > .active > a:focus, .pagination > .active > a:hover, .pagination > .active > span, .pagination > .active > span:focus, .pagination > .active > span:hover {
    background-color: #002748;
    border-color: #002748;
}

th.forumhead {
    background-color: #0e0e0e;
}
tr.subforumhead {
    background-color: #181818 !important;
}

.clickable-row:hover, .clickable-row:active {
    background-color: rgba(255,255,255,.1) !important;
}
.subforumhead:hover, .subforumhead:active {
    background-color: #181818 !important;
}

#inthpheader {
    background-color: #171717;
}

.select select {
    background-color: #424242;
    color: white;
}
.select select:hover, .select select:active, .select select:focus {
    color: white;
    background-color: #4f4f4f !important;
}

.btn-success {
    background-color: #386f38;
    border-color: #386f38;
}
.btn-success:hover, .btn-success:active, .btn-success:focus {
    background-color: #387938;
    border-color: #387938;
}

#filterbox, #filterbox2 {
    background-color: #424242;
}

form[name="upload"] .input-group > input {
    background-color: #242424;
    color: #a7a3a3;
}

.namebadge {
    background: none;
    background-color: #1d1d1d;
    border-color: #000;
}
.namebadge:hover, .namebadge:active, .namebadge:focus
{
    background: none;
    background-color: #222;
    border-color: #000;
}

.panel-primary > .panel-heading {
    border-bottom: none;
}
}
