css:
@-moz-document url-prefix("https://bbs.hupu.com/bxj") {
body {
	padding: 0px;
	margin: 0px;
	font-family: "Helvetica Neue", "Luxi Sans", "DejaVu Sans", Tahoma, "Hiragino Sans GB", "Microsoft Yahei", sans-serif;
	background: #e2e2e2
}
.hp-threeNav,
.showPlate,
.breadCrumbs,
.pl_nav,
.bbsPlate {
	display: none
}
body .bbs-content #container .container_padd {
	background: #fff
}
.bbsHotPit {
	width: 1170px ! important;
}
.bbsHotPit .show-list .show-tit .t1 {
	width: 700px;
	margin-left: 60px;
}
.bbsHotPit .show-list .for-list li .titlelink {
	width: 710px ! important;
	margin-left: 50px;
}
.bbsHotPit .show-list .for-list li .titlelink {
	font-size: 17px;
	margin-top: 8px;
	margin-bottom: 8px;
	padding-top: 8px;
	padding-bottom: 8px;
}
.bbsHotPit .show-list .for-list li {
	border-bottom: 1px solid #f9f7f7;
}
.downpage {
	margin-right: auto ! important;
	margin-left: auto ! important;
}
body .bbs-content {
	padding: 0;
}
body .bbs-content #container .container_padd {
	border: 0;
}

#container a:active,
#container a:link,
#container a:visited {
	color: #666e74 !important;
}

#container a:hover {
	color: #2b89d0!important;
	-webkit-transition: color .25s ease-in-out, color .25s ease-in-out;
	-moz-transition: color .25s ease-in-out, color .25s ease-in-out;
	transition: color .25s ease-in-out, color .25s ease-in-out;
	text-decoration: none
}
}
