css:
@-moz-document url("https://www.theduckwebcomics.com/"), url-prefix("https://www.theduckwebcomics.com/search/"), url-prefix("https://www.theduckwebcomics.com/news/"), url-prefix("https://www.theduckwebcomics.com/forum/"), url-prefix("https://www.theduckwebcomics.com/quackcast/"), url-prefix("https://www.theduckwebcomics.com/user/"), url-prefix("https://www.theduckwebcomics.com/quack/inbox/"), url-prefix("https://www.theduckwebcomics.com/requests/"), url-prefix("https://www.theduckwebcomics.com/tutorial/") {
/* Minimal *Drunk* Duck by mishihime */

body {
	background-image: linear-gradient(#005826, #1a1d20) !important;
}

maindiv {
	background-image: linear-gradient(rgba(255,0,0,0), #fcf3c4) !important;
}

#header-top , .sideModule {
	background-color: #f0f0f0 !important;
}

#header-bottom {
	background-color: #fcf3c4      !important;
	padding-top: 8px;
	padding-bottom: 6px;
	border-radius: 5px !important;
}

#topmenu.blue {
	margin: 4px !important;
}

.mainDiv {
	background-color: #f0f0f0 !important;
	border-radius: 25px !important;
}

input.headersearch {
	background-color: #fafafa !important;
}

#maincontent {
	background-color: #f0f0f0  !important;
}

#maincontent.homecontent {
	border-radius: 25px !important;
}

img.feature_label {
	display: none !important;
}

.blue , .blue a , .blue label , a.blue:visited , .blue a:visited {
	color: #19974f !important;
}

a.blue:hover , .blue a:hover {
    color: #2c2f33 !important;
    
}
.newsBanner.blue {
	background-color: #fafafa !important;
	display: none !important;
}

.sideBody {
	background-color: #fcf3c4   !important;
}

.sideTop {
	background-color: #f0f0f0 !important;
}

.userComics {
	background: #fff !important;
}

#footer {
	background-color: #fcf3c4 !important;
}

.title div {
    background-color: #fafafa !important;
}

.title {
	background-color: #fafafa !important;
}

.yellow-box , .yellow-back {
	background: #fff !important;
}

#commentsection {
	background: #ddd !important;
}

#favorite_list {
	background: #fff;
}

.post-body blockquote {
    background: #fcf3c4;
}
}
