css:
@-moz-document domain("sufficientvelocity.com") {
/* Tags - remove backgrounds, make them white*/
.tagItem {
    color: #fff;
    background: none;
    border: 1px transparent;
}

/* Overall Post Area background edits - make the posts seamless */
.block--messages .block-container {
    background:  #000000a8;
/*    background:rgba(10, 10, 10, 0.65); */
    border: none;
}





/* Table border colors */
.sv-table-container .sv-table td {
    border-color: grey
}

/* Quotes & Spoilers background colors*/
.bbCodeBlock.bbCodeBlock--quote {
    margin: 1em 5px 1em 5px;
    border: 1px solid rgb(4, 38, 74);
    border-radius: 5px;
    overflow: auto;
    background: rgba(4, 38, 74, .3)
}

.bbCodeBlock-title {
    font-size: 9pt;
    color: rgb(220, 237, 255);
    background: rgba(3, 55, 74, .7) url(https://forums.sufficientvelocity.com/data/svg/18/0/1562936520/gradient_heading_muted.svg) repeat-x 0 0;
    padding: 3px 8px;
    border-color: rgb(4, 38, 74);
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-top-left-radius: 4px;
    border-top-right-radius: 4px;
}

.bbCodeBlock {
    margin: .75em 0;
    background: rgba(4, 38, 74, .3);
    border: 1px solid rgb(4, 38, 74);
    border-radius: 5px;
    overflow: auto;
    border-bottom-width: 1px;
    border-bottom-style: solid;
}

/* User profile background */
.memberTooltip-header {
    background: rgba(4, 38, 74, .3)
}

/* Overall inputs */

.input {
    background: rgba(1,27,50,0.4);
    border: none;
}

.input:focus, .input.is-focused {
    outline: 0;
    background: rgba(0,0,0,0.8);
    border: none;
}




/* Profile menu transparency */
.formRow>dt {
    background: transparent
}

/* Profile page transparency */
.memberHeader-main {
    background: rgba(1,27,50,0.4)
}

.memberHeader-content {
     background: rgba(0,0,0,0.6);
}

.block-tabHeader {
    background: rgba(1,27,50,0.4)  
}

/* Image preview */
.lg-backdrop {
    background: rgba(0,0,0,0.85) 
}

/* Playing with the editor & borders */

.block-container  {
    border-style: none;
}

form.block.js-quickReply .message.message--quickReply.block-topRadiusContent.block-bottomRadiusContent {
    padding:10px;
}

form.block.js-quickReply .message.message--quickReply.block-topRadiusContent.block-bottomRadiusContent .message-cell.message-cell--main {
    padding:0px;
}

form.block.js-quickReply .message.message--quickReply.block-topRadiusContent.block-bottomRadiusContent .message-cell.message-cell--user, .message-cell.message-cell--action {
    background: transparent;
    border-right: none;
    padding:0px;
    padding-right:10px;
}

.message.message--quickReply.block-topRadiusContent.block-bottomRadiusContent .message-avatar {
    position: relative;
    padding-top: 10px;
    padding-right: 6px;
    padding-bottom: 6px;
    padding-left: 6px;
    box-shadow: rgba(10, 10, 10, 0.5) 2px 3px 6px 2px;
    background: rgba(0, 0, 0, 0.7)
}

/* QuickReply Avatar Border
.message.message--quickReply.block-topRadiusContent.block-bottomRadiusContent .message-avatar {
    border-width: 1px;
    border-style: solid;
    border-top-color: #202020;
    border-right-color: #282828;
    border-bottom-color: #353535;
    border-left-color: #282828;
    border-radius: 6px;
}
*/

/*
.block-container .message-inner {
    padding-top: 10px;
}
*/

.message.message--quickReply.block-topRadiusContent.block-bottomRadiusContent .message-user {

}

.message.message--quickReply.block-topRadiusContent.block-bottomRadiusContent .formRow.threadmark--label {
    border-width: 1px;
    border: 1px solid rgb(7,59,110);
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
    width: auto
}

.message.message--quickReply.block-topRadiusContent.block-bottomRadiusContent .formRow.threadmark--category {
    border-width: 1px;
    border: 1px solid rgb(7,59,110);
    border-bottom-left-radius: 6px;
    border-bottom-right-radius: 6px;
    border-bottom: none;
    border-top: none;
    width: auto
}

form.block.js-quickReply .message.message--quickReply.block-topRadiusContent.block-bottomRadiusContent .fr-box {
}

/*
article.message.sticky-container {
    padding-bottom: 15px;
    border-bottom-style: groove;
    border-bottom-color: #1b2938c9;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px
}
*/

article.message.sticky-container .message-cell--main {
        background: #1b293878
}



/* Playing with threadmarks */
.block--messages .message-cell--threadmark-header .threadmarkLabel {
    border: none;
    background: transparent;
    color: #fff;
    padding: 3px;
}

.block--messages .message-cell--threadmark-header {
    background: rgba(0, 0, 0, 0.7);
    color: #fff;
    padding-top: 5px;
    padding-bottom: 5px;
    margin-bottom: 5px;
    margin-left: -10px;
    margin-right: -10px;
    border: none;
/*    border-radius: 0px !important;*/
}

.block--messages .message-cell--threadmark-footer {
    background: rgba(0, 0, 0, 0.7);
    border: none;
    padding-top: 5px;
    padding-bottom: 5px;
    margin-top: 5px;
    margin-left: -10px;
    margin-right: -10px;
    border-top-left-radius:0px !important;
/*    border-radius: 0px !important;*/
}

.block--messages .message-cell--threadmark-header label {
    padding-left: 10px;
}



/* Menu colors - subject to change */
.message-responseRow {
    background: rgba(0,0,0,0.4);
    border: none;
}

.editorPlaceholder {
    margin: 10px;
}

.editorPlaceholder .editorPlaceholder-placeholder .input {
}

.menu-content {
    background: rgba(0, 9, 15, .95);
    border: 1px solid rgb(7, 59, 110);
    border-top-width: 5px;
    border-top-color: rgb(7, 59, 110);
    border-radius: 0px;
    border-bottom-right-radius: 6px;
    border-bottom-left-radius: 6px;
    overflow: hidden;
    box-shadow: 0px 5px 5px rgba(0, 9, 15, 0.5);
}

.js-alertsMenuBody .alert.is-unread {
    background: rgba(1,27,50,0.95);
}

.js-alertsMenuBody .alert.is-recently-read {
      background: rgba(12,26,41,0.5)
}

.js-alertsMenuBody .alert.is-read {
    background: rgba(0, 9, 15, .95)
}

.menu-row.menu-row--clickable:hover {
    background: #1B2938;
    border-left: 3px solid rgba(48,75,101,0.5);
}

.menu-row.menu-row {
    border-left: 3px solid transparent;
}

.menu-row.menu-row--alt {
    background: none;
}

.menu-header {
    background: linear-gradient(0deg, rgba(0, 9, 15, .95), rgba(1,27,50));
}

.menu-footer {
    background: linear-gradient(0deg, rgba(0,10,20), rgba(0, 9, 15, .95));;
}

.menu-tabHeader {
    background: linear-gradient(0deg, rgba(0, 9, 15, .95), rgba(1,27,50));
}




/* Alerts page colors */

.alert-list  .alert.is-unread {
    background: rgba(1,27,50,0.95);
}

.alert-list  .alert.is-recently-read {
      background: rgba(12,26,41,0.5)
}

.alert-list  .alert.is-read {
    background: rgba(0, 9, 15, .95)
}



/* Various adjustments */
.autoCompleteList {
    list-style: none;
    margin: 0;
    padding: 0;
    cursor: default;
    font-size: 12px;
    color: #e6e6e6;
    background: rgb(0,20,40);
    border: 1px solid #304b65;
}

/* Change Search menu */
select.js-quickSearch-constraint.input, input:focus [style] {
    width: 95px !important;
}

/* Change Threadmark tags */
.select2 .select2-selection ul>li.select2-selection__choice {
    font-size: 12px;
    color: #fdfeff;
    background: #304b65;
}

.select2-results__options {
    background: rgb(0,20,40);
    margin: 0;
    padding: 0;
    color: #e6e6e6;
    border-top: 3px solid #304b65 !important;
    border: 1px solid #304b65;
}

/*
.overlay {
    background: none;
}
*/

.overlay-content {
    background: rgba(1,27,50,0.4);
    border-top-color: #202020;
    border-right-color: #282828;
    border-bottom-color: #353535;
    border-left-color: #282828;
}

/* Fix Calendar */
.pika-table abbr {
    text-decoration:none;
}

/* Search menu offset - redundant */
.formRow.formRow--input>dt {
    padding-top: 22px;
}

/* Fix Calendar */
.fr-popup .fr-input-line input+label, .fr-popup .fr-input-line textarea+label {
    display:none;
}

/* Change footer color */
.block-footer {
    background: linear-gradient(0deg, rgba(1,27,50,0.4), rgba(2,54,100,0.4));
    border-top: 1px solid #202020;
}

.block-footer .input {
    border: 1px solid #304b65;
}

/* Hide tags on the last thread in subforum view */
.node-body .label, .label-append {
    display:none;
}


.message-cell.message-cell--threadmark-footer {
}

.message-cell.message-cell--threadmark-footer .message-attribution-main.message-attribution-main--list2 {
}

.message-cell.message-cell--threadmark-footer a.bookmarkLink {
    display:none;
}

.message-cell.message-cell--threadmark-footer .message-newIndicator {
    display:none;
}

/* Threadmark Style in the OP */
.threadmarkListingHeader-main {
    background: #1b293878;
    border-top-left-radius: 6px;
    border-top-right-radius: 6px;
}
}

@-moz-document domain("sufficientvelocity.com") {
/* User Avatar Area edits - change the borders and move the online indicator */
.block--messages .message-cell:first-child {
    background: transparent;
    border-right: none;
    padding:0px;
    padding-right:10px;
}

section.message-user {
    padding-top: 10px;
    padding-right: 6px;
    padding-bottom: 6px;
    padding-left: 6px;
    box-shadow: rgba(15, 15, 15, 0.5) 2px 3px 6px 2px;
    position: relative;
    background: rgba(0, 0, 0, 0.7);
    border-radius: 6px;}

/*
@media (min-width: 650px) {
section.message-user {
    padding-top: 24px;
}
}
*/

section.message-user .message-userArrow {
    right:-10px
}

/* Keep borders around avatars 
section.message-user {
    border-width: 1px;
    border-style: solid;
    border-top-color: #202020;
    border-right-color: #282828;
    border-bottom-color: #353535;
    border-left-color: #282828;
}
*/

.message-avatar-wrapper {
    border-radius: 4px;
    border: 1.2px solid rgb(40, 40, 40);
    border-image: initial;
    padding: 2px;
}

/* Reply Box Background */
.message-cell.message-cell--user {
    background: transparent;
}

/* Location text changes */
section.message-user .pairs.pairs--justified>dt {
    font-size: 8pt;
}

section.message-user .pairs.pairs--justified>dd {
    font-size: 8pt;
    text-align: right;
    text-justify: auto;
    float:none;
}


@media (min-width: 650px) {
/* Move Online Indicator to the left-bottom corner*/
.message-avatar-wrapper .message-avatar-online {
    top: 4.22em;
    left: 4.54em;
    font-size: 20px;
    border: transparent;
    background: transparent;
}

/* Turn Online Indicator into an icon */
.message-avatar-wrapper .message-avatar-online:before {
    font-family: "FontAwesome";
    content: "\f2bd";
    border: none;
    background: none;
    color: #7fb900;
}
}

@media (max-width: 650px) {
/* Move Online Indicator to the left-bottom corner*/
.message-avatar-wrapper .message-avatar-online {
    top: 3.62em;
    left: 3.84em;
    border: transparent;
    background: transparent;
}

/* Turn Online Indicator into an icon */
.message-avatar-wrapper .message-avatar-online:before {
    font-family: "FontAwesome";
    content: "\f2bd";
    border: none;
    background: none;
    color: #7fb900;
}
}
}

@-moz-document domain("sufficientvelocity.com") {
/* Post Area edits - modify borders, make the background transparent */
.block--messages .message,
.block--messages .block-row {
    background: transparent;
    border-style: none;
    padding: 10px;
}

.block--messages .message-cell:last-child {
    border-radius: 5px;
}

/* Make post background darker and remove arrows 
.block--messages .message-cell--main {
    background: rgba(10, 10, 10, 0.65); 
}

.message-userArrow {
    display:none;
}
*/

/*Keep borders around posts */
.block--messages .message-cell--main {
    border-width: 1px;
    border-style: solid;
    border-top-color: #202020;
    border-right-color: #282828;
    border-bottom-color: #353535;
    border-left-color: #282828;
}

.stickyAvatars section.message-user .message-userArrow {
    right: 0px
}

/* Top of the post - remove borders */
.message-attribution.message-attribution--split {
    border-bottom-style: none
}

/* Signature - remove bottom border */
.message-signature {
    color: rgb(127, 127, 127);
    border-bottom: none;
}

/* Ratings - make background transparent*/
.sv-rating__count {
    background: none;
    border: none
}

.sv-rating {
    background: none;
    border: none;
}

.sv-rating-bar--under-controls.sv-rating-bar--menu-bar {
    margin-top: 0px;
}

/* Playing with the sticky posts */
article.message.sticky-container .message-cell--user {
        background: transparent
}

article.message.sticky-container section.message-user {
        background: #1b293878;
        border-radius: 6px;
}

article.message.sticky-container {
    border-bottom: none;
    padding-bottom: 10px;
}

/* Important Notifications (Voting) */
.blockMessage.blockMessage--important {
    background: linear-gradient(0deg, rgba(1,27,50,0.4), rgba(2,54,100,0.4));
    border: none;
    border-left: 3px solid #304b65;
}
/* Thread banners */
.thread-reply-banner {
        background: #1b293878;
        border-radius: 6px;
}
}

@-moz-document domain("sufficientvelocity.com") {
/* Text Editor colors */
.fr-box textarea.input {
    background: rgba(1,27,50,0.4);
    border: 1px solid #304b65;
    border-top: none
}

.fr-box textarea.input:focus, .input.is-focused {
    outline: 0;
    background: rgba(0,0,0,0.8);
    border-top: none
}

.fr-element, .fr-element:focus {
    background: rgba(0,0,0,0.8)
}

.block-container {
    background:  #000000a8;
}

/*
.fr-box {  
    border: 1px solid rgb(7,59,110);
    border-radius: 4px;
    position: relative;
}
*/

.fr-toolbar {
    border: 1px solid #304b65;
/*    border-bottom: 0px;*/
    background-color: #001223;
}

.fr-box.fr-basic .fr-wrapper {
    background: transparent;
    border: 1px solid #304b65;
    border-radius: 4px;
}

.fr-toolbar .fr-command.fr-btn.fr-active, .fr-popup .fr-command.fr-btn.fr-active {
    color: #fff;  
}

/*    Dropdown menus border & background  */
.fr-command.fr-btn.fr-active+.fr-dropdown-menu
{
/*  background: rgb(0,20,40); */
    border: 1px solid #304b65;
}

/*    Color menus border & background  */
.fr-popup.fr-active {
/*  background: rgb(0,20,40); */
    border: 1px solid #304b65;
    border-top: 3px solid #304b65;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}

/*    Button separator */
.fr-separator {
    background: #304b65;
}

.fr-popup .fr-color-hex-layer .fr-action-buttons button.fr-command {
    background-color: #304b65
}
}

@-moz-document url-prefix("https://forums.sufficientvelocity.com/conversations") {
/* Move Sidebar in Conversations */
.p-body-main {
    display: table-row;    
}

.p-body-sideNavCol, .p-body-contentCol, .p-body-sidebarCol {
    display: none;
}

.conversationProgress-container {display:none}

@media (min-width: 650px) {
.p-body-sidebar>*
    {
    margin: 0 5px 20px;
    min-width: 250px;
    max-width:max-content;
}

.p-body-sidebar {
    display: flex;
    flex-wrap: nowrap;
    align-items: stretch;
    margin: 20px -5px -20px;
    width: auto;
}

.p-body-sidebar:after {
    display: none;
}
}

@media (min-width: 900px) {
.p-body-sidebar .block-container {
    height: 100%;
}
}

@media (max-width: 900px) {
.p-body-sidebar>* {
    flex: auto;
}
}

.p-body-sidebar>*:last-child {
    margin-bottom: 20px;
}

.p-body-sidebar li.block-row {
    display: inline-block;
    width: 150px;
}

.p-body-sidebar .block-row {
    background:transparent;
}
}

@-moz-document url-prefix("https://forums.sufficientvelocity.com/watched/threads") {
/* Change offset at Watched Threads */
@media (min-width: 900px) {
    
.block-footer.block-footer--split .block-footer-opposite, .block-footer.block-footer--split .block-footer-controls {
    margin:0px
    }
    
.block-footer.block-footer--split {
    justify-content: flex-end;
}
    
.block-footer.block-footer--split .block-footer-select {
    padding-right:6.28em;
    font-size: 0.75rem;
}

span.iconic-label {
   font-size: 0.7rem;
   padding-left:0.96em;
}
}

@media (min-width: 650px) and (max-width:900px) {

.block-footer.block-footer--split .block-footer-opposite, .block-footer.block-footer--split .block-footer-controls {
    margin:0px
    }
    
.block-footer.block-footer--split {
    justify-content: flex-end;
}

.block-footer.block-footer--split .block-footer-select {
    padding-right:0.5em;
    font-size: 0.75rem;
}
    
span.iconic-label {
   font-size: 0.7rem;
   padding-left:0.96em;
}
}

@media (max-width: 650px) {
    
.block-footer.block-footer--split .block-footer-select:not(:last-child) {
    font-size: 0.8rem;
    margin-left: 0.6em;
}
    
span.iconic-label {
   font-size: 0.8rem;
   padding-left:1.2em;
}
}

.input option {
   background: rgba(0, 9, 15,1);
}

/* Change thread icons position on Watched Threads */
.structItem-minor .structItem-extraInfo>li {
    margin:0;
}
}
