css:
@-moz-document url-prefix("http://drulsrecord.ru"){
body {
    background: url("https://pp.userapi.com/c851024/v851024764/a869d/7bNbMyRQ70I.jpg") no-repeat fixed center center !important;
    -webkit-background-size: cover !important;
    -moz-background-size: cover !important;
    -o-background-size: cover !important;
    background-size: cover !important;
    color: #c9dfff;
}
.hero-unit-small {
    background-color: rgba(68, 83, 109, 0.4);
}
.page_head {
    color: #c9dfff;
}
.navbar-inner {
    background-color: rgba(68, 83, 109, 0.0);
}
.navbar .nav > li > a {
    color: #c9dfff;
}
.navbar .nav > .active > a,
.navbar .nav > .active > a:hover,
.navbar .nav > .active > a:focus,
.navbar .nav li.dropdown.open > .dropdown-toggle,
.navbar .nav li.dropdown.active > .dropdown-toggle,
.navbar .nav li.dropdown.open.active > .dropdown-toggle,
.navbar .btn-navbar:hover,
.navbar .btn-navbar:focus,
.navbar .btn-navbar:active,
.navbar .btn-navbar.active,
.navbar .btn-navbar.disabled,
.navbar .btn-navbar[disabled],
.dropup,
.slide-btn,
a.featured_port_projects_btn,
.purchase_main_content,
.purchase_now_btn,
.Search_btn,
.Search_btn,
.search-btn,
.read_more,
.sign_up,
.newsletter_btn,
a.aboutus_readmore,
a.blog_section_readmore,
a.blog_section2_readmore,
.more-link,
.comment_btn,
#comment_btn,
.button_red,
.shortcode_btn,
.portfolio_showcase .portfolio_showcase_icons a,
.portfolio_tabs li.active > a,
.pagination ul li a.active,
.pagination > ul > li > a:hover,
#panel-admin p a.onclick_default,
#wp-calendar caption,
.onclick_default,
.callout_now_btn,
.wpcf7-submit,
.post-password-form input[type="submit"] {
    background-color: rgba(68, 83, 109, 0.0);
}
.dropdown-menu {
    background-color: rgba(68, 83, 109, 0.8);
    box-shadow: rgba(68, 83, 109, 0.0);
}
.dropdown-menu > li > a {
    color: #c9dfff;
}
.dropdown-menu > li > a:hover,
.dropdown-menu > li > a:focus,
.dropdown-submenu:hover > a,
.dropdown-submenu:focus > a {
    background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0.15), rgba(255, 255, 255, 0.15));
    color: #fff;
    background-color: rgba(68, 83, 109, 0.0);
}
.sidebar_widget_title h2,
.sidebar_widget_title h2 a,
.page-template-contact .sidebar .widget .widget_title h2,
.head_title h3,
.team_head_title h3,
.latest_news_title h3,
.blog_single_post_head_title h3,
.typography_head_title h3,
.shortcode_head_title h3,
.service_head_title h3,
.porfolio_detail_title h3 {
    color: #c9dfff;
}
p.comment-notes {
    color: #c9dfff;
}
textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
    background-color: rgba(12, 26, 52, 0.63);
}
.comment_btn,
#comment_btn {
    color: #fff;
}
.navbar .nav > .active > a,
.navbar .nav > .active > a:hover,
.navbar .nav > .active > a:focus,
.navbar .nav li.dropdown.open > .dropdown-toggle,
.navbar .nav li.dropdown.active > .dropdown-toggle,
.navbar .nav li.dropdown.open.active > .dropdown-toggle,
.navbar .btn-navbar:hover,
.navbar .btn-navbar:focus,
.navbar .btn-navbar:active,
.navbar .btn-navbar.active,
.navbar .btn-navbar.disabled,
.navbar .btn-navbar[disabled],
.dropup,
.slide-btn,
a.featured_port_projects_btn,
.purchase_main_content,
.purchase_now_btn,
.Search_btn,
.Search_btn,
.search-btn,
.read_more,
.sign_up,
.newsletter_btn,
a.aboutus_readmore,
a.blog_section_readmore,
a.blog_section2_readmore,
.more-link,
.comment_btn,
#comment_btn,
.button_red,
.shortcode_btn,
.portfolio_showcase .portfolio_showcase_icons a,
.portfolio_tabs li.active > a,
.pagination ul li a.active,
.pagination > ul > li > a:hover,
#panel-admin p a.onclick_default,
#wp-calendar caption,
.onclick_default,
.callout_now_btn,
.wpcf7-submit,
.post-password-form input[type="submit"] {
    background-color: rgba(37, 54, 83, .7);
}
.blog_single_post {
    background-color: rgba(68, 83, 109, 0.4);
}
.blog_section2 h2 a,
.blog_single_post h2 a {
    color: #c9dfff;
}
.blog_section2_comment a {
    color: #c9dfff;
}
.blog_section2 p,
.blog_single_post p {
    color: #c9dfff;
}
a,
.logo-title,
.home_service_btn:hover,
.purchase_now_content .purchase_now_btn:hover,
.team_section h4 a,
.reply a,
.leave_comment_section label span,
.latest_news_comment a:hover,
.sidebar_widget .usefull_link a:hover,
.para_tooltip a,
.para_tooltip a:hover,
p.text_error,
.shortcode_coloumn label span,
a.continue_readmore,
.testimonial_author span,
.pagination ul > li > a,
.pagination ul > li > span,
.pager li > a,
.pager li > span,
.widget_archive ul li a i,
.widget_categories ul li a i,
.sidebar_widget ul li a i,
.sidebar_link a:hover,
.sidebar_link a.active,
.top-header-widget li > a:hover,
.top-header-widget li > a:focus {
    color: #fff;
}
.blog_section2 {
    background-color: rgba(68, 83, 109, 0.4);
}
.sidebar {
    background-color: rgba(68, 83, 109, 0.4);
}
form#searchform {
    margin: 10px 0px 20px 0px;
}
.sidebar_widget ul li a {
    color: #fff;
}
.pagination a,
.pagination .page-numbers {
    color: #fff;
    background-color: rgba(68, 83, 109, 0.4);
}
.pagination span.current,
.pagination a:hover,
.pagination a.active {
    background-color: rgba(24, 50, 98, 0.8);
}
.navbar .nav > li > a:hover {
    background-color: rgba(24, 50, 98, 0.8) !important;
}
.blog_single_post h2,
.blog_section2 h2,
.blog_section h2 {
    color: #c9dfff;
}
.blog_single_post h5,
.blog_section2 h5 {
    color: #c9dfff;
}
.blog_single_post h1,
.blog_section2 h1,
.blog_section h1 {
    color: #c9dfff;
}
}
