css:
@-moz-document url-prefix("https://www.brick-hill.com") {
/* Creatd by Heartbreak Kid/Leaf (Jordan#3883) */

    /* The Fundamental */


    body {
    background-image: url("https://i.imgur.com/YO1tE4n.png")!important; background-repeat: unset; color: white;
    }

    nav div.primary, nav .info {
    background-color: #620000;
    }

    nav div.primary ul li a:hover {
    border-color: white;
    }

    ::selection {
    background-color: #A80000; color: black;
    }

    nav div.secondary {
    background-color: #390000; color: #EB0000!important;
    }

    nav div.secondary ul li a:hover {
    border-color: #EB0000;
    }

    .alert.success {
    background-color: #181818; border: 1px solid black;
    }

    .button.red, button.red {
    color: #fff; background-color: #780000; border-color: #000000A1;
    }

    .button.red:hover, button.red:hover {
    color: #fff; background-color: #8D0000; border-color: #000000A1;
    }


    .card .content {
    background-color: #181818; border: 1px solid black;
    }

    .line, hr {
    border-bottom: 1px solid #151515; border-top: none;
    }

    .card .top:not(.absolute).blue, .card .top:not(.absolute).orange, .card .top:not(.absolute).red, .card .top:not(.absolute).green, .card .top:not(.absolute) {
    background-color: #630404; color: #fff; border: 1px solid black; border-bottom: 1px solid #ff1e1e;
    }

    .input, textarea, input.rigid, textarea.rigid {
    background-color: #141414;  border: 1px solid #141414; color: white;
    }

    .input-group .input-button {
    border: 1px solid transparent; color: #cbcbcb; background-color: #151515;
    }

    .input-button:hover {
    background-color: #141414;
    }

    .input-group {
    border: 1px solid transparent;
    }

    .input-group.fill .input, .input-group.fill input, .input-group.fill textarea {
    background-color: #161616; color: white;
    }

    .button.blue, button.blue {
    background-color: #1D1D1D; border-color: #0000;
    }

    .button.blue:hover, button.blue:hover {
    background-color: #202020;
    }

    a.very-bold.dark-gray-text.block {
    color: #a40000;
    }

    a.very-bold.dark-gray-text.block:hover {
    color: #C90000;
    }

    .gray-text {
    color: #d0d0d0;
    }

    .dark-gray-text {
    color: #a2a2a2;
    }

    footer {
    background-color: transparent;
    }

    .file-img-box, .upload-preview-box {
    border: 1px solid #0b0b0b; background-color: #141414;
    }

    .select, select {
    background-color: #151515; border-color: #151515; color: #DDD;
    }

    .mb1 {
    color: #fff;
    }

    .shop-categories .category.active, .shop-categories .category a:hover {
    color: #fff;
    }

    .button.green, button.green {
    background-color: #1d1d1d; border-color: #1d1d1d;
    }   

    .card {
     box-shadow: none;
    }

    .nav-notif, .notif {
    margin-top: 1.5px;
    }

    i.f-right.light-gray-text.far.fa-edit, i.far.fa-caret-square-down.dropdown-arrow {
    color: #7e0000;
    }

    .trade-serial {
    background-color: #0f0f0f; color: #ff0000;
    }

    .button, .button:hover {
    background-color: #1d1d1d; border-color: #1d1d1d;
    }

    button.no-click {
    background-color: #630404; color: #F00; border: 1px solid black;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/forum") {
/* Focused on forums design */

    .thread-row {
    border-bottom: 2px solid #171717;
    }

    .select, select {
    background-color: #151515; border-color: #151515;
    }

    .mb1 {
    color: #cd0000!important;
    }

    .label.dark, .board-info a, .stat .title {
    color: #fefefe;
    }

    .divide {
    background-color: transparent;
    }

    .forum-links a:hover {
    color: white;
    }

    .hover-card:hover {
    background-color: #1b1b1b;
    }

    .hover-card.viewed {
    background-color: #161616;
    }

    .forum-bar a {
    color: white;
    }

    .forum-bar .fa-angle-double-right {
    color: #b10000;
    }

    .thread-label.blue {
    background-color: #1e1e1e;
    color: #a10000;
    } 

    .thread {
    border-bottom: 1px solid #151515;
    }

    .darkest-gray-text {
    color: #9f1212;
    }

    .forum-tag {
    background-color: #151515;
    }

    .forum-tag:after {
    border-right-color: #151515;
    }
    .col-3-12.center-text.ellipsis a {
    color: #fff!important;
    }

    .mr4:hover, .report:hover {
    color: #fff;
    }

    .blockquote.blue, blockquote.blue {
    border-color: #641300;
    }

    blockquote.blue a {
    color: #f30000!important;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/play"), url-prefix("https://www.brick-hill.com/sets") {
/* focused on game pages design */

    .card .content, .card .footer {
    border: none;
    }

    .card .footer {
    background-color: #1b1b1b;
    }

    .card .name {
    color: #d8d8d8;
    }

    .card .creator {
    color: #b5b5b5;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/shop") {
/* Focused on the catalog/shop's deisgn */

    .card .name {
    color: #dedede;
    } 

    .card .thumbnail.dark {
    background-color: #181818;
    }

    .card .item-card-details, .card .item-content {
    background-color: #1b1b1b;
    }

    .card .item-card-details, .card .item-content {
    border-top: #181818;
    }

    .card .item-card-details .price, .card .item-content .price{
    color: #ff0000;
    }

    .card .creator {
    color: #c1c1c1;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/user"), url-prefix("https://www.brick-hill.com/awards") {
/* Focused on user page */

     .button.blue, button.blue, .button.blue:hover, button.blue:hover {
    border: 1px solid black;
    }

    .award-card img, .profile-card.award {
    border: 1px solid #0b0b0b!important; background-color: #141414!important;
    }

    .award-card img:hover, .profile-card.award:hover {
    border: 1px solid #0b0b0b!important; background-color: #0E0E0E!important;
    }

    .profile-card.crate:hover img {
    background-color: #141414; border: none!important;
    }

    .profile-card.crate img {
    background-color: #111;
    }

    img {
    border: none!important;
    }

    .profile-card:hover {
    background-color: transparent;
    }

    .button.tab-button.transparent:hover, button.tab-button.transparent:hover {
    color:red;
    }

    .crate-types li:hover {
    background-color: #191919; border-left: #171717;
    }

    .crate-types .active, .crate-types :hover{
    background-color: #171717; border-left: #171717;
    }

    .crate-types {
    border-right: 2px solid #171717;
    }

    .toggle-user-desc:not(.open):after {
    background-color: #151515f2; color: transparent;
    }

    .read-more-desc {
    background-color: transparent;  color: red; bottom: 0px;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/trades"), url-prefix("https://www.brick-hill.com/trade"), url-prefix("https://www.brick-hill.com/messages") {
/* Focuses on trades page */

    .tabs .tab-body {
    border: none; background-color: #101010;
    }

    .hover-card {
    background-color: #111111;
    }

    .tabs .tab.active {
    background-color: #171717; font-weight: 600; border-bottom: 1px solid transparent;
    }

    .tabs .tab { 
    color: #fdfdfd; border-radius: 0px; background-color: #151515; border: 1px solid; border-color: #151515;
    }

    .hover-card:hover {
    background-color: #131313;
    }

    .trade-item img {
    border: 1px solid #080808; background-color: #121212;
    }

    .button.green, button.green {
    background-color: #171717; border-color: #000000;
    }

    .trade-item .trade-name {
    color: #c5c5c5;
    }

    .trade-env {
    color: #b9b9b9;
    }

    input, textarea {
    background-color: #141414;  border: 1px solid #141414; color: white;
    }

    li.item-card-tile.small-tile.inline, .item-card-image img, .item-card-container, .item-card-image {
    background-color: #0f0f0f!important; border: #0f0f0f!important;
    }

    .pages .page.active {
    color: red;
    }

    .label.dark 
    {
    color: #fff6ff;
    }

    .darkest-gray-text 
    {
    color: #c40202;
    }

    .board-info a, .stat .title 
    {
    color: #ffffff;
    }

    .thread-info:hover 
    {
    background-color: #131313;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/currency"), url-prefix("https://www.brick-hill.com/shop") {
/* Focuses on currency/shop page */

    .tabs .tab-body {
    border: none; background-color: #101010;
    }

    .hover-card {
    background-color: #111111;
    }

    .tabs .tab.active {
    background-color: #171717; font-weight: 600; border-bottom: 1px solid transparent;
    }

    .tabs .tab { 
    color: #fdfdfd; border-radius: 0px; background-color: #151515; border: 1px solid; border-color: #151515;
    }

    .hover-card:hover {
    background-color: #131313;
    }

    input, textarea {
    background-color: #141414;  border: 1px solid #141414; color: white;
    }

    .select, select {
    padding: 3.5px;
    }

    .box.shaded {
    border: 1px solid #080808; background-color: #121212;
    }

    .ablack-text {
    color: red;
    }

    .card .item-creator {
    color: #e5e5e5;
    }

    .agray-text {
    color: #B50000;
    }

    .darkest-gray-text {
    color: #d3d3d3;
    }

    .box.item-img.owns, .box.item-img.special {
    border: 5px solid #0f0f0f;
    }

    .thumbnail.dark {
    border-color: #121212!important;
    }

    .sellers .seller:not(:last-child) {
    border-color: #151515;
    }

    .sellers .price .serial{
    color: #34f700;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/clans"), url-prefix("https://www.brick-hill.com/clan") {
/* Focused mainly on clans page. */

    .tabs .tab-body {
    border: none; background-color: #101010;
    }

    .hover-card {
    background-color: #111111;
    }

    .tabs .tab.active {
    background-color: #171717; font-weight: 600; border-bottom: 1px solid transparent;
    }

    .tabs .tab { 
    color: #fdfdfd; border-radius: 0px; background-color: #151515; border: 1px solid; border-color: #151515;
    }

    .profile-card:hover {
    background-color: transparent;
    }

    .hover-card.clan:hover {
    background-color: #151515;
    }

    .button.blue, button.blue {
    padding: 11px;
    }

    .black-text {
    color: #f00;
    }

    .dark-gray-text {
    color: #ededed;
    }

    .darkest-gray-text {
    color: #cacaca;
    }

    .fieldset.green, .fieldset.red {
    border-color: transparent;
    }

    .fieldset.green legend {
    color: #e69b00;
    }

    input.upload-input {
    background-color: transparent;
    }

    input, textarea {
    background-color: #141414;  border: 1px solid #141414; color: white;
    }

    i.fas.fa-ban {
    color: red;
    }
}

@-moz-document url-prefix("https://www.brick-hill.com/client") {
/* Client page */

    a.download h5 {
    color: red!important;
    margin: 20px;
    }

    .button.orange, button.orange {
    background-color: #1D1D1D; border-color: black;
    }
}
