css:
@-moz-document domain("gatesofsurvival.com") {
/*global background*/
body {
    background: url(https://www.syberwatch.xyz/wp-content/uploads/2019/07/wrap17.png) center repeat #000 !important;
}
/* main text color, size and bubble box-shadow */
a,
body,
form,
form font,
.slayer,
.boost_show,
.text_button,
.tips,
.quest,
.post-heading,
.news,
.label,
.alert-pet_box,
.csstable,
.csstable td,
.nav-pills li a,
.achievement,
.success,
.random_event,
.notice,
.warning,
.lvl_up,
.error,
.alert-box {
    color: #99aab5 !important;
    box-shadow: none !important;
    font-size: 12px;
}
/* Bubble Radiuses */
.alert-box,
.alert-alert_box,
.news {
    border-radius: 20px !important;
}
/* Bubble Background color */
.boost_show,
.quest,
.tips,
.achievement,
.success,
.news,
.warning,
.lvl_up,
.error,
.notice,
.pet,
.pet2,
.random_event {
    background-color: #181818 !important;
}
/*slayer bubble*/
.slayer {
    margin-top: 1.5em !important;
    background: url(https://www.gatesofsurvival.com/game/images/skills/slayer/slayer_logo.png) 10% 50% no-repeat #181818 !important;
    border: medium outset #D9534F !important;
    padding: 1.5em 3em;
}
.slayer img {
    padding: 1em 0;
}
.slayer br:nth-last-child(2) {
    display: none;
}
/* Boost Bubble */
.boost_show {
    border: medium outset #4285F4 !important;
    padding: 1.5em 3em;
    margin-top: 1.5em !important;
}
/* Daily Quest Bubble */
.quest {
    border: medium outset #FF8800 !important;
    padding: 1.5em 3em;
}
/* Game Tips Bubble */
.tips {
    border: medium outset #2BBBAD !important;
    border-radius: 3em/10em !important;
    padding: 1.5em 3em;
    margin-bottom: 1.5em;
    margin-top: 1em;
}
/*Achievements Bubble */
.achievement {
    border: medium outset #9933CC !important;
}
/* Success Bubble */
.success {
    border: medium outset #007E33 !important;
    padding-top: 1em;
    padding-bottom: 1em;
}
/* New Bubble */
.news {
    border: medium outset #3F729B !important;
    margin-top: 1.5em !important;
    padding: 1.5em 3em;
}
/* Warning Bubble */
.warning {
    border: medium outset #ffbb33 !important;
    margin-top: 1.5em !important;
    padding: 1.5em 3em;
    margin-bottom: 1.5em;
}
/* Level Up Bubble */
.lvl_up {
    border: medium inset #0d47a1 !important;
    border-radius: 0 50px;
    padding-top: 1em;
    padding-bottom: 1em;
}
/* Error Bubble */
.error {
    border: medium outset #CC0000 !important;
}
/* Notice Bubble */
.notice {
    margin-top: 1.5em;
    border: medium outset #aa66cc !important;
}
/*Primary Pet Bubble */
.pet {
    border: medium solid black !important;
    width: auto !important;
    margin-top: 1.5em !important;
    padding: 1.5em 3em;
}
/* Secondary Pet Bubble */
.pet2 {
    border: medium solid black !important;
    margin-top: 1.5em !important;
    padding: 1.5em 3em;
}
/* Random Event Bubble */
.random_event {
    border: medium outset #00695c !important;
}
/* Combat Settings */
#change_combat_settings br:nth-last-child(2) {
    display: none !important;
}
/*Fighting Grounds Boostrap Buttons */
#show10,
#show9 {
    background-color: #aa66cc;
}
#show10:hover,
#show9:hover {
    background-color: #9933CC;
}
#show_monsters {
    border-top: 0.1em solid;
    border-image: linear-gradient(to right, rgba(114, 137, 218, 0), rgba(114, 137, 218, 0.80), rgba(114, 137, 218, 0)) 1;
    padding-top: 1em;
}
/*Chatbox Pill Size */
#message_check > .nav-pills li a {
    width: 13.5em !important;
}
/*Iventory and Player Profile Pill Styles */
.nav-pills li {
    display: table !important;
    height: 3em;
    margin-bottom: 0.5em;
    margin-right: 0.5em;
}
.nav-pills li a {
    border: medium solid black !important;
    background-color: #181818;
    text-align: center;
    width: 13em !important;
    padding: 1 !important;
    display: table-cell !important;
    vertical-align: middle;
}
.nav-pills > li.active > a,
.nav-pills > li.active > a:focus,
.nav-pills > li.active > a:hover,
.nav-pills > li > a:hover,
.nav-pills > li > a:focus {
    color: #212121 !important;
}
.alert-box {
    font-size: 12px !important;
}
/* Table borders*/
.text_button,
.post-heading,
.backpack,
.csstable,
#search_table8 {
    border: medium solid #000 !important;
}
/* Remove Top Border from High scores */
#third-container20 > .csstable {
    border-top: none !important;
}
/* text area input boxes */
textarea,
.searchbox {
    border: medium solid black !important;
    background-color: #2b2b2b;
    color: #99aab5 !important;
    width: 100%;
    resize: vertical;
}
/* bubbles text color */
.tooltips,
.single-message span,
.alert-backpack_box {
    color: #212121;
}
/* all horizontal rules */
hr {
    border-top: 1px;
    background-image: linear-gradient(to right, rgba(114, 137, 218, 0.20), rgba(114, 137, 218, 0.80), rgba(114, 137, 218, 0.20));
}
/* additional text color */
.error,
.dropdown-content a:hover,
a:hover {
    color: #f7f8f9 !important;
}
/*Left Nav Menu Dropdown Menu */
.classy,
.classy:hover {
    box-shadow: none;
    line-height: 3em !important;
}
/* Hide br's on Left & Right Columns, Column Banners Fighting Grounds, main bg, old header logos, homepage navigation text, trade tent */
.classy br,
#show_monsters br:last-child,
#show_monsters br:nth-last-child(2),
.menu_l::before,
.menu_l::after,
.menu_r > center > br:first-of-type,
.bg-stretch,
.menu_r::before,
.menu_r::after,
img[src*="logo_halloween"],
img[src*="new_logo_halloween"],
.text-navigation,
.aside.right > br:first-of-type,
.aside.right > br:nth-of-type(2),
.menu_r > br:first-of-type,
.menu_r > br:last-of-type,
.btns,
#char_create br:first-child,
#banking hr:first-of-type,
#myFormW1 br:nth-of-type(n+10):nth-of-type(-n+14),
#myFormW1 br:nth-of-type(2),
#myFormW1 br:nth-of-type(6),
#myFormW1 br:last-of-type,
#myFormW1 br:nth-last-of-type(3),
#myFormW1 br:nth-last-of-type(4),
#myFormW1 br:nth-last-of-type(10) {
    display: none !important;
}
/*Left Column Sub Menu styling */
.dropdown-content {
 	border-left: medium solid #000;
	border-right: medium solid #000;
    padding: 1em !important;
    background: #181818 !important;
    left: 100% !important;
    -moz-column-fill: balance;
    column-fill: balance;
    column-gap: 0.5em;
    -webkit-column-gap: 0.5em;
    -moz-column-gap: 0.5em;
    min-height: auto !important;
}
.dropdown-content:before {
  position :absolute;
  left:0;
  right:0;
  top:0;
  content:"";
  height: 0.2em;
  background-image: linear-gradient(to right, rgba(114, 137, 218, 0.06), rgba(114, 137, 218, 0.80), rgba(114, 137, 218, 0.06));
}
.dropdown-content:after {
  position :absolute;
  left:0;
  right:0;
  bottom:0;
  content:"";
  height: 0.2em;
  background-image: linear-gradient(to right, rgba(114, 137, 218, 0.06), rgba(114, 137, 218, 0.80), rgba(114, 137, 218, 0.06));
}
.dropdown-content a {
    font-size: 12px !important;
    background-color: #202020;
    color: #99aab5;
    padding: 0.6em;
    margin-bottom: 0.5em !important;
    border: medium solid black;
    -moz-page-break-inside: avoid;
    -webkit-column-break-inside: avoid;
    break-inside: avoid;
    -o-column-break-inside: avoid;
    -ms-column-break-inside: avoid;
    page-break-inside: avoid;
}
/*Skills Sub Menu Styling */
#skills_nav.dropdown-content {
    column-count: 5;
    -webkit-column-count: 5;
    -moz-column-count: 5;
}
#skills_nav.dropdown-content a:hover:nth-child(-n+6) {
    background-color: #007E33;
}
#skills_nav.dropdown-content a:hover:nth-child(n+7):nth-child(-n+12) {
    background-color: #0d47a1;
}
#skills_nav.dropdown-content a:hover:nth-child(n+13):nth-child(-n+18) {
    background-color: #9933CC;
}
#skills_nav.dropdown-content a:hover:nth-child(n+19):nth-child(-n+24) {
    background-color: #00695c;
}
#skills_nav.dropdown-content a:hover:nth-child(n+25):nth-child(-n+29) {
    background-color: #0099CC;
}
/*Misc Sub Menu Styling */
#nav_misc_li ~ .dropdown-content {
    column-count: 3;
    -webkit-column-count: 3;
    -moz-column-count: 3;
    height: auto !important;
}
#nav_misc_li ~ .dropdown-content a:hover:nth-child(-n+4) {
    background-color: #007E33;
}
#nav_misc_li ~ .dropdown-content a:hover:nth-child(n+5):nth-child(-n+8) {
    background-color: #0d47a1;
}
#nav_misc_li ~ .dropdown-content a:hover:nth-child(n+9):nth-child(-n+11) {
    background-color: #9933CC;
}
/*Game Help Sub Menu Styling */
#nav_game_li ~ .dropdown-content {
    column-count: 2;
    -webkit-column-count: 2;
    -moz-column-count: 2;
    height: auto !important;
}
#nav_game_li ~ .dropdown-content a:hover:nth-child(-n+3) {
    background-color: #007E33;
}
#nav_game_li ~ .dropdown-content a:hover:nth-child(n+5):nth-child(-n+7) {
    background-color: #0d47a1;
}
/*High Scores Sub Menu Styling */
#highscores ~ .dropdown-content {
    column-count: 6 !important;
    -webkit-column-count: 6;
    -moz-column-count: 6;
    height: auto !important;
}
#highscores ~ .dropdown-content a:hover:nth-child(-n+6) {
    background-color: #007E33;
}
#highscores ~ .dropdown-content a:hover:nth-child(n+7):nth-child(-n+12) {
    background-color: #0d47a1;
}
#highscores ~ .dropdown-content a:hover:nth-child(n+13):nth-child(-n+18) {
    background-color: #9933CC;
}
#highscores ~ .dropdown-content a:hover:nth-child(n+19):nth-child(-n+24) {
    background-color: #00695c;
}
#highscores ~ .dropdown-content a:hover:nth-child(n+25):nth-child(-n+30) {
    background-color: #0099CC;
}
#highscores ~ .dropdown-content a:hover:nth-child(n+31):nth-child(-n+36) {
    background-color: #3F729B;
}
/*Oval headings above character profile stats */
.alert-backpack_box {
    padding: 3px 8px;
}
.backpack {
    box-shadow: none !important;
    background: #c0c0c0 !important;
}
/* table first row colors */
.csstable tr:first-child td,
.csstable tr:first-child:hover td {
    color: #212121 !important;
    padding: 0.5em;
    background: #c0c0c0 !important;
}
#search_table tr:not(:nth-child(1)):hover td,
#search_table8 tr:not(:nth-child(1)):hover td,
#search_table8 tr:not(:nth-child(1)):hover td input,
#search_table8 tr:not(:nth-child(1)):hover td form,
#search_table7 tr:not(:nth-child(1)):hover td {
    background-color: #4285F4;
    color: black !important;
}
/* CSStable font-size */
.csstable tr:nth-child(n+2) td {
    font-size: 12px !important;
}
/*table bg colors including suggestions board */
.csstable tr:nth-of-type(odd) {
    background-color: #181818;
}
.csstable tr:nth-of-type(even) {
    background-color: #202020;
}
/*pet bubbles*/
.alert-pet_box {
    margin: 5px;
    font-size: 12px !important;
}
/* table borders high scores */
.table-bordered,
.table-bordered td,
.table-bordered th {
    border: thin solid #000 !important;
}
/* main heading center column */
.post-heading {
    background: #181818 !important;
}
/*time css at top heading */
#time,
.post-heading span {
    font-weight: bold !important;
}
.by {
    background: none !important;
}
.menu_l ul {
    padding-left: 1.5em !important;
    padding-right: 1.5em !important;
    width: auto !important;
    line-height: 0 !important;
}
/* left Column */
.menu_l li {
    font-weight: 700;
    font-size: 12px;
    font-family: "Helvetica Neue", Helvetica, Arial, sans-serif;
    border-left: medium solid black;
    border-top: medium solid black;
    border-bottom: medium solid black;
    background: #202020 !important;
    color: #99aab5;
    margin-bottom: 0.2em !important;
    width: auto !important;
    box-shadow: none !important;
    padding: 0 !important;
}
#register2 li:hover,
#lore li:hover,
#game_forgot li:hover,
#game_verify li:hover,
#nav_skills_li:hover,
#nav_misc_li:hover,
#worldmap li:hover,
#inventory li:hover,
#equipment li:hover,
#nav_game_li:hover,
#gems li:hover,
#hourly_check li:hover,
#online li:hover,
#updates li:hover,
#clear_top3 li:hover,
.dropdown-content a:hover {
    color: #f8f9fa;
}
#register2 li,
#lore li,
#nav_skills_li,
#nav_misc_li,
#worldmap li,
#inventory li,
#equipment li,
#skills_nav.dropdown-content a:nth-child(-n+6),
#nav_misc_li ~ .dropdown-content a:nth-child(-n+4),
#nav_game_li ~ .dropdown-content a:nth-child(-n+3),
#highscores ~ .dropdown-content a:nth-child(-n+6) {
    border-right: thick inset #007E33;
}
#register2 li:hover,
#lore li:hover,
#nav_skills_li:hover,
#nav_misc_li:hover,
#worldmap li:hover,
#inventory li:hover,
#equipment li:hover {
    background-image: linear-gradient(to left, #007E33 0%, #202020 74%) !important;
}
#game_forgot li,
#game_verify li,
#nav_game_li,
#gems li,
#hourly_check li,
#online li,
#updates li,
#skills_nav.dropdown-content a:nth-child(n+7):nth-child(-n+12),
#nav_misc_li ~ .dropdown-content a:nth-child(n+5):nth-child(-n+8),
#nav_game_li ~ .dropdown-content a:nth-child(n+5):nth-child(-n+7),
#highscores ~ .dropdown-content a:nth-child(n+7):nth-child(-n+12) {
    border-right: thick inset #0d47a1;
}
#game_forgot li:hover,
#game_verify li:hover,
#nav_game_li:hover,
#gems li:hover,
#hourly_check li:hover,
#online li:hover,
#updates li:hover {
    background-image: linear-gradient(to left, #0d47a1 0%, #202020 74%) !important;
}
#clear_top3 li,
#messaging li,
#nav_highscores_li,
#logout li,
#skills_nav.dropdown-content a:nth-child(n+13):nth-child(-n+18),
#nav_misc_li ~ .dropdown-content a:nth-child(n+9):nth-child(-n+11),
#highscores ~ .dropdown-content a:nth-child(n+13):nth-child(-n+18) {
    border-right: thick inset #9933CC;
}
#clear_top3 li:hover,
#messaging li:hover,
#nav_highscores_li:hover,
#logout li:hover {
    background-image: linear-gradient(to left, #9933CC 0%, #202020 74%) !important;
}
#skills_nav.dropdown-content a:nth-child(n+19):nth-child(-n+24),
#highscores ~ .dropdown-content a:nth-child(n+19):nth-child(-n+24) {
    border-right: thick inset #00695c;
}
#skills_nav.dropdown-content a:nth-child(n+25):nth-child(-n+29),
#highscores ~ .dropdown-content a:nth-child(n+25):nth-child(-n+30) {
    border-right: thick inset #0099CC;
}
#highscores ~ .dropdown-content a:nth-child(n+31):nth-child(-n+36) {
    border-right: thick inset #3F729B;
}
/* left Column buttons & main background border */
.post .post-content {
    border: medium solid #000;
}
/*Right Column */
.menu_r {
    top: 0 !important;
    left: 0 !important;
    text-shadow: none !important;
}
/* Right Column text size & color */
#heal_health12,
.menu_r font,
#banking font[color="ffffff"],
#show_pets font[color="ffffff"] {
    color: #99aab5;
    font-size: 12px !important;
}
#banking font[style="font-size: 0.9em"] {
    line-height: 2em;
}
/* Right Column Text Alignment */
.menu_r h5,
.menu_r form {
    text-align: center !important;
}
#show_potions {
    width: auto !important;
}
#show_summons {
    width: auto !important;
    margin: 0 1em;
}
#show_pies {
    width: auto !important;
    padding-bottom: 1em;
}

/* Right Column Bank Stats */
#banking {
    width: auto !important;
    padding-top: 1em;
    border-top: 0.1em solid;
    border-image: linear-gradient(to right, rgba(114, 137, 218, 0), rgba(114, 137, 218, 0.80), rgba(114, 137, 218, 0)) 1;
}
/* Pet section Right Column */
#show_pets {
    width: auto !important;
}
#show_pets td:nth-child(1) {
    cursor: pointer;
}
#show_pets td:nth-child(2) {
    padding-bottom: 1em;
}
/* Right Column Character Info */
#char_create {
    font-size: 12px;
    width: auto !important;
    color: #99aab5;
}
#char_create i {
    font-weight: 700;
    font-style: normal;
}
#char_create img {
    display: block;
    padding-bottom: 1em;
}
#heal_health12 {
    padding-top: 1em !important;
}
/* global chat messages*/
.single-message {
    color: #212121 !important;
}
/* arrows bar on calendar on updates menu */
.controls {
    background-image: linear-gradient(to left, #632e2e 0%, #000000 74%) !important;
}
/* Calendar on updates menu */
#mini-clndr .clndr {
    border-bottom: 7px solid #632e2e;
}
/*boostrap panel css */
.panel-heading {
    background-color: #2b2b2b !important;
    border: medium solid black !important;
}
.panel-heading:hover {
    background-color: #c0c0c0 !important;
}
.panel-title a {
    color: #99aab5 !important;
}
.panel-title a:hover {
    text-decoration: none;
    color: #212121 !important;
}
.panel {
    background-color: transparent;
    margin-bottom: 0.5em !important;
}
.panel-body {
    background-color: #181818;
    margin-top: 1em;
    padding-top: 1em;
    padding-bottom: 0.5em;
}
/*action timer css */
.bar {
    background-color: #181818 !important;
}
#progressbar {
    background-color: #632e2e;
}
/* dropdown menus, game settings, combat settings css */
select,
select[id=listBox1],
input[type=number],
input[type=text],
input[id=pass],
input[name=username],
input[type=password],
input[name=email_address] {
    border: medium solid black !important;
    background-color: #2b2b2b !important;
    color: #99aab5 !important;
    box-shadow: none;
}
/* Username & Password form height */
input[id=pass],
input[id=user],
input[id=username],
input[id=email],
input[name=username],
input[type=password],
input[name=email_address] {
    margin-top: 0.5em;
    height: 2.4em;
}
input[id=username]:focus,
input[id=user]:focus,
input[id=email]:focus,
input[id=pass]:focus,
input[name=username]:focus,
input[type=password]:focus,
input[name=email_address]:focus {
    border: thin solid #4d90fe !important;
}
/* equipment and pet square buttons css */
button#show[style="height:175px; width:125px; vertical-align:top;"],
button#show2[style="height:175px; width:125px; vertical-align:top;"],
button#show3[style="height:175px; width:125px; vertical-align:top;"],
button#show4[style="height:175px; width:125px; vertical-align:top;"],
button#show6[style="height:175px; width:125px; vertical-align:top;"],
button#show8[style="height:175px; width:125px; vertical-align:top;"] {
    background-image: linear-gradient(315deg, #080808 0%, #2b2b2b 74%) !important;
    color: #ddd9a7;
    box-shadow: 2px 3px 3px 2px #23272a;
    border: 2px solid #000;
}
button#show:hover[style="height:175px; width:125px; vertical-align:top;"],
button#show2:hover[style="height:175px; width:125px; vertical-align:top;"],
button#show3:hover[style="height:175px; width:125px; vertical-align:top;"],
button#show4:hover[style="height:175px; width:125px; vertical-align:top;"],
button#show6:hover[style="height:175px; width:125px; vertical-align:top;"],
button#show8:hover[style="height:175px; width:125px; vertical-align:top;"] {
    background-image: linear-gradient(to bottom right, #632e2e 0%, #000000 74%);
    color: #ddd9a7;
    box-shadow: 2px 3px 3px 2px #23272a;
    border: 2px solid #000;
}
/* calendar css */
.days {
    background-color: #000 !important;
}
.day {
    color: #f7f8f9 !important;
    background-color: #414141;
    border: thin solid #000 !important;
}
/* calendar events css */
.event {
    color: #ddd9a7 !important;
    background-image: linear-gradient(to top left, #000000 0%, #383838 74%);
}
.event:hover {
    color: #f7f8f9 !important;
    background-image: linear-gradient(to bottom right, #632e2e 0%, #000000 74%);
}
.event a {
    color: #ddd9a7 !important;
}
.event a:hover {
    color: #f7f8f9 !important;
}
/*main skills progress bar */
.progress {
    border: medium solid #000;
    background-color: #181818;
    box-shadow: none;
}
.progress-bar {
    background-color: #632e2e;
    color: #f7f8f9 !important;
}
/* slayer bubble exp bar */
#red5 {
    border: medium solid #000 !important;
    background-color: #2b2b2b !important;
    box-shadow: none;
    height: 2.2em !important;
