css:
@-moz-document url-prefix('https://osu.ppy.sh/')
{
:root {--base-hue: 125 !important}
    .profile-info__name {color: #6fb957;}
    

    .profile-header__top
{
    color: #fff;
    background-color: #151e16 
}

    .profile-detail-bar
{
    color: #fff;
    background-color: #203322 
}
    .profile-detail
{
    color: #fff;
    background-color: #151e16 
}
    .profile-links
{
    color: #fff;
    background-color: #203322 
}
    .osu-page
{
    color: #fff;
    background-color: #151e16 
}
.js-switchable-mode-page--scrollspy-offset
{
        background-color: #151e16 ;
}
.page-extra 
{
    background-color: #121c11 
}
.page-extra--userpage
{
    background-color: #203322 
}
.page-extra-tabs
{
    background-color: #151e16 
}
.hidden-xs.page-extra-tabs.page-extra-tabs--profile-page.js-switchable-mode-page--scrollspy-offset
{
    background-color: #151e16
}
.play-detail__group.play-detail__group--top
{
    background-color: #0f2211
}
.nav2-header__transition-overlay
{
    background-color: #151e16
}
.play-detail__accuracy
{
    color:#8bec9c
}
.beatmap-playcount__count
{
    color:#8bec9c
}
.t-section .u-section--gradient-down
{
    background-image: linear-gradient(hsla(var(--base-hue),35%,15%,.7),hsl(var(--base-hue),30%,5%) 37.5%,hsl(var(--base-hue),40%,25%) 50%,hsl(var(--base-hue),30%,20%) 0,hsl(var(--base-hue),0%,0%))
}
}
